/** ==================== Better Zhihu | 我的知乎不可能这么英俊 ====================
 *  
 *  Author: miyuchina
 *
 *  Forked from https://userstyles.org/styles/121696/zhihu.
 *  Thank you Vanton Tang for the amazing color scheme!
 *  
 *  Also inspired by Frizen/Plains-Gate: https://github.com/Frizen/Plains-Gate
 *  and unogz/izhihu: https://github.com/unogz/izhihu
 *  Much thanks to them as well.
 *
 *  This CSS file is under Public Domain, which means you can do whatever you
 *  want with it, even for commercial benefits, and without notifying the author.
 *  With that said, I would much appreciate it if you can drop me a link to your
 *  work.
 *
 *  Enjoy.
**/

@-moz-document domain("zhihu.com") {

	/* Basic fonts and color scheme. */
	body.zhi {
		padding-top: 55px;
		color: #34495e;
		line-height: 2;
		font-size: 14px;
		font-family: BlinkMacSystemFont, Helvetica Neue, Helvetica, Arial, PingFang SC, Microsoft YaHei, serif;
	}
	body.zhi h2 {
		font-size: 18px;
		font-weight: 700;
	}
	body.zhi blockquote {
		line-height: 2;
		border-left-color: #DDD;
		padding-left: 2em;
	}

	.zm-item-vote {
		left: -68px;
		top: 10px;
	}
	.zu-top {
		background-image: none;
		border-bottom: none;
		background-color: #34495e;
		padding: 5px 0;
	}
	.zu-top-add-question {
		background-image: none !important;
		border-radius: 3px;
		border: 1px solid #4e6d8d;
		box-shadow: none !important;
		opacity: .9;
		-ms-filter: "alpha(Opacity=90)";
		line-height: 0px;
	}
	.zu-top-add-question:hover {
		opacity: 1;
		-ms-filter: "alpha(Opacity=100)";
	}
	.zu-top-search-input {
		border-radius: 3px;
		border: none;
		box-shadow: none;
	}
	.top-nav-dropdown li:last-child>a {
		border-radius: 0 0 3px 3px;
	}
	.top-nav-dropdown li:first-child>a {
		border-radius: 3px 3px 0 0;
	}
	*[class*="zg-btn"] {
		border-radius: 3px;
		background-image: none;
		box-shadow: none;
	}
	.zu-top-nav-li.current {
		background-image: none;
		background: #34495e;
		color: #16a085;
	}
	.zu-top-search-input:focus {
		background-color: #293a4a;
		color: #fff;
		border: none;
		box-shadow: none;
	}
	.zu-top-nav-link,
	.zu-top-nav-link:visited,
	.zu-top-nav-link:active {
		color: #fff;
		opacity: .8;
		-ms-filter: "alpha(Opacity=80)";
	}
	.zu-top-nav-link:hover,
	.current .zu-top-nav-link,
	.current .zu-top-nav-link:visited,
	.current .zu-top-nav-link:active {
		opacity: 1;
		-ms-filter: "alpha(Opacity=100)";
	}
	a {
		color: #ac3e40;
	}
	a:hover {
		text-decoration: underline;
	}

	/* Toned down red color in tags, "显示全部", "问题详情". */
	.zm-item-tag,
	a.toggle-expand.btn-toggle-question-detail span {
		color: #7C8A97;
	}

	/* Restore toggle-expand color. */
	a.toggle-expand span,
	a.toggle-expand span:hover {
		color: #259;
	}

	/* Dummy comment. */
	.zu-top-search-input {
		color: #536a81;
		background: #293a4a;
	}
	.zu-top-nav-userinfo.selected,
	html.no-touch .top-nav-profile:hover .zu-top-nav-userinfo {
		background: #34495E;
		border-radius: 3px;
	}
	.zu-top-nav-userinfo:hover {
		background: #536a81;
	}
	.top-nav-dropdown li a {
		border-top: 1px solid #2f4154;
	}
	.top-nav-profile a {
		background: #34495E;
	}
	.top-nav-profile a:hover {
		background: #293a4a!important;
	}
	.top-nav-profile .top-nav-dropdown {
		padding-top: 10px
	}
	html.no-touch .top-nav-dropdown a:hover {
		background: #34495e;
	}
	.zm-side-nav-link.active,
	html.no-touch .zm-side-nav-roundtable .follow-link:hover+.zm-side-nav-link,
	html.no-touch .zm-side-nav-link:hover {
		background: #ecf0f1;
		border-radius: 3px;
	}
	.zg-wrap {
		width: 1100px;
		margin: 0 auto;
		clear: both;
	}

	/* Apply color to votebar. */
	html.no-touch .zm-votebar .up:hover,
	html.no-touch .zm-votebar .down:hover,
	.zm-votebar .up.pressed,
	.zm-votebar .down.pressed {
		background: #2980b9;
		color: #fff;
	}

	/* Resolve feed list margin issues. */
	.feed-item-inner {
		margin-left: 25px;
		margin-top: 8px;
	}
	.feed-item .avatar,
	.feed-item .avatar a,
	.feed-item .avatar img {
		width: 45px;
		height: 45px;
	}
	.zm-votebar {
		margin-left: -67px;
	}
	.feed-item .avatar img {
		border-radius: 3px;
	}
	.feed-main,
	.zm-item-answer {
		margin-left: 70px;
	}

	/* Dummy comment. */
	.zh-backtotop {
		margin-left: 400px;
	}
	.origin_image {
		height: auto !important;
	}
	.zg-btn-blue {
		background: #34495e;
		border-radius: 3px;
	}
	.zg-btn-green,
	.zg-btn-follow {
		color: #fff !important;
		background: #34495e;
		border: #34495e;
		border-radius: 3px;
		opacity: .9;
		-ms-filter: "alpha(Opacity=90)";
	}
	.zg-btn-green:hover,
	.zg-btn-follow:hover {
		opacity: 1;
		-ms-filter: "alpha(Opacity=100)";
	}
	.zm-item-tag {
		border-radius: 3px;
	}
	.zm-item-tag:hover {
		background: #34495E !important;
	}
	.zu-top-search-button {
		background-image: none !important;
		border-radius: 3px;
		background: #4e6d8d;
		border: none !important;
		box-shadow: none !important;
	}

	/* Resolve autocomplete username display issue. */
	.zu-autocomplete-row-name {
		padding-top: 1px;
	}
	.modal-dialog-title {
		box-shadow: none;
		background-image: none;
		border: none;
		background: #34495e;
		padding: 1;
	}

	/* Resolve invitation name display issue. */
	.question-invite-panel .suggest-persons .person .zm-item-link-avatar,
	.question-invite-panel .suggest-persons .person .describe {
		padding-top: 1px;
	}

	.zm-votebar.version-text .up.pressed {
		background: #34495e;
	}
	.zm-votebar.version-text .up.pressed:hover {
		background: #4e6d8d !important;
	}

	/* Dummy comment. */
	.item-collapse.is-sticky {
		background-color: #ac3e40;
	}
	.zm-side-section #js-reg-with-sina-in-top {
		background: #e55345;
	}
	.goog-buttonset-default {
		background: #34495e;
		border: none;
	}
	.zg-btn-white:hover {
		background: #eee;
	}

	/* Resolve footer line overflow issue. */
	.zh-footer {
		font-size: 12px;
	}

	/* ================ 话题广场 Properties =============== */

	/* Apply colors to tag items. */
	.zm-topic-cat-main .zm-topic-cat-item a {
		color: #7C8A97;
	}
	html.no-touchevents .zm-topic-cat-main .zm-topic-cat-item:hover a,
	.zm-topic-cat-main .zm-topic-cat-item.current a {
		background: #34495E;
		border-color: #34495E;
		color: #fff;
	}

	/* Resolve item list <p> overflow issue. */
	.zm-topic-cat-sub .item {
		height: 118px;
	}
	.zm-topic-cat-sub p {
		height: 50px;
	}

	/* ================ 知乎圆桌 Properties =============== */

	/* Apply fonts and color to Roundtable. */
	.page-roundtable .content {
		color: #34495e;
		font-family: BlinkMacSystemFont, Helvetica Neue, Helvetica, Arial, PingFang SC, Microsoft YaHei, serif;
	}
	.page-roundtable .page-sidebar .items a.name {
		color: #ac3e40 !important;
		font-weight: bold;
	}

	/* Resolve Roundtable profile tab issues. */
	.roundtable-profile .content {
		height: 200px;
	}
	.roundtable-profile .description {
		height: 8em;
	}
	.roundtable-profile .actions {
		bottom: 0.2em;
	}
	.pure-button {
		padding-top: 2px;
		height: 26px;
	}

	/* Apply fonts to footer; resolve margin issues. */
	.global-footer {
		font-family: BlinkMacSystemFont, Helvetica Neue, Helvetica, Arial, PingFang SC, Microsoft YaHei, serif;
		font-size: 12px;
		text-align: center;
	}
	.global-footer ul {
		margin: 10px 10px 8px 32px;
	}
}
