#plzWait
{
	position: absolute;
	margin-top: 40%;
	left: 50%;
	width: 300px;
	margin-left: -150px;
	border-top: 2px solid white;
	border-left: 2px solid white;
	border-right: 2px solid gray;
	border-bottom: 2px solid gray;
	background-color: black;
	display: none;
	font-family: Verdana, Arial, sans-serif;
	text-align: center;
}

.study_year_width
{
    width:200px;
}

#attachPanel
{
	z-index:1005;
	border: 10px solid Gray;
	background: white;
	display: none;
	position: absolute;
	-moz-border-radius: 1em;
	-webkit-border-radius: 1em;
	border-radius: 1em;
	-moz-box-shadow: 10px 10px 20px black;
	-webkit-box-shadow: 10px 10px 20px black;
	box-shadow: 10px 10px 20px black;
}

#attachPanel clear
{
	vertical-align:bottom;
	height:100%;
}

#attachPanel iframe
{
	border:0;
	width: 600px;
	height: 400px;
	margin: 0;
	padding:.5em;
	z-index:100;
}

#imgCloseAttach {
    cursor: pointer;
    height: 20px;
    position: absolute;
    right: -15px;
    top: -15px;
    width: 20px;
}



input[type=button]
{
    margin:0px;
}

input[type=text], input[type=password]
{
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border:.1em solid silver;
    padding:.1em;
    -moz-box-shadow: inset 0 0 .2em silver;
    -webkit-box-shadow: inset 0 0 .2em silver;
    /*box-shadow: inner 0 0 .2em silver;*/
    box-shadow: inset 0 0 .2em silver;
    height: 1.3em;
    cursor: text !important;
}

a
{
	text-decoration: underline;
	cursor: pointer;
}
.page
{
    padding: 0.1em 0.2em !important; 
}
.none
{
    display:none;
}

.big_icon
{
    width:1em;
    height:1em;
}

.right
{
    text-align: right;
}

.bigtextareasize
{
    width:30em;
    height:10em;
}



