.news_channel
{
    background: #4E4E4E;
    color: white;
    padding: 10px;
    margin-bottom:10px;
    border-radius: 10px;
    -moz-border-radius: 10px;
    /*white-space:nowrap;*/
}

.news_channel ul {
    list-style: none;
    padding: 0 5px;
    margin: 5px 0;
}

.news_channel a
{
    color:White !important;
    text-decoration:none !important;
}
.news_channel a:hover
{
    text-decoration:underline !important;
}

.nc_header {
    text-align: center;
}

