css:
@-moz-document domain("byui.brightspace.com") {

/*compressing info*/
#d2l_navbar .d2l-navbar-title {
display: none
}

.vui-typography{

    font-family: "SegoeUI Light", tahoma, helvetica, sans-serif;
}

.vui-typography p{
    margin: 5px;
}
td.d_tl, td.d_tc, td.d_tr{
    padding: 0px;
}

/* Grading view sizing 
	if you would like to change the widths, the left column width is wherever it says 725, and the right panel is wherever it says 600. The feedback is on the bottom and you can mess with that too.

*/
#z_f > div:nth-child(1){
    width: 725px !important;
}
#z_f > div:nth-child(1) > div.dsl_p_m_f > div{
        width: 725px !important;
}
#z_g > div:nth-child(1) > div > div{
    width: 725px !important;
}
#z_f > div:nth-child(2){
    width: 600px !important;
}
#z_f > div:nth-child(2) > div.dsl_p_m_f > div{
    width: 600px !important;
}
#z_bg{
    width: 600px !important;
}

#feedback{
    width: 550px !important;
    height: 500px !important;
}
#feedback\24 html_iframecontainer{
    width: 548px !important;
    height: 425px !important;
}
}
