css:
@-moz-document domain(imgur.com) {

.post-container, #comments-container {
 position: relative;
 left: 140px;
}
.comment .arrow.btn.up {
    margin-bottom: 4px;
}
a.promoted-tag {
    font-size: .8em;
    color: #fff;
    background-color: /*[[main]]*/;
    border-radius: 4px;
    padding: 2px 6px;
}

/*Remove*/

.cta-content-container, .cta-background, .sentence-sorting, #outside-tagging-showhide, #top-comments.left-column, #right-content.right.post-unification, .social,.post-gridview .post-gridview-link,.icon-grid,active, .dropdown-footer.hamburger-menu.test-1868-hide, .right .panel.menu.selected .active, .right .panel.menu .button.selected .active {
 display: none;
}

/*Color*/
#topbar .link-menu {
    border-top: 3px solid /*[[other]]*/;
}
#topbar .account:hover {
    background-color: /*[[main]]*/;
    transition: all 0s 0s;
}
.arrow:hover{
 background: /*[[text]]*/; 
 color: /*[[main]]*/;
}

#fullbleed-bg {
    width: 100%;
    min-width: 1000px;
    height: 429px;
    position: absolute;
    top: 50px;
    background: none;
    max-width: 100%;
    overflow-x: hidden;
}
body {
    font-family: Arial,sans-serif;
    color: /*[[text]]*/;
    height: 100%;
    background: url(https://i.gyazo.com/89922925cbf1d79fccbe75d8b1a3bf6f.png);
    background-color: /*[[main]]*/;
    margin: 0;
    font-size: 14px;
}
#topbar {
    width: 100%;
    background-color: /*[[second]]*/;
    font-size: 15px;
    height: 50px;
    font-weight: 700;
    position: relative;
    z-index: 101;
    box-shadow: 0 0 11px 1px #111;
}
.post {
    background-color: #181817;
    border: 2px solid /*[[main]]*/;
    display: inline-block;
    margin: 4px 2px;
    position: relative;
}
.posts {
    position: relative;
    padding: 16px;
    background-color: /*[[second]]*/;
    box-shadow: 0 0 11px 1px #111;
    -webkit-box-shadow: 0 0 11px 1px #111;
    -moz-box-shadow: 0 0 11px 1px #111;
    margin-bottom: 50px;
}
#topbar .global-search-section {
    background-color: /*[[second]]*/;
    padding: 10px 10px 10px 40px;
     border-bottom: 1px solid /*[[[main]]*/; 
     border-right: 1px solid /*[[main]]*/; 
     border-left: 1px solid /*[[main]]*/; 
    line-height: 1.7em;
    position: relative;
}
#topbar .global-search-section .title {
    color: /*[[text]]*/;
    font-size: 10px;
    line-height: 1em;
    text-transform: uppercase;
    background-color: /*[[second]]*/;
    padding-bottom: 3px;
    cursor: default;
}
#topbar .search-focused, #topbar .search-focused:hover,#topbar .search-icon-container-icon.searchable:hover {
background-color: /*[[second]]*/
}

#topbar .search-icon-container-icon.searchable {
background-color: /*[[main]]*/
}

#topbar .account .user-dropdown-container, #topbar .signin-dropdown {
    position: absolute;
    width: 160px;
    right: 0;
    background-color: /*[[second]]*/;
    font-size: 14px;
    z-index: 1000;
    color: #121211;
    font-weight: 400;
    top: -300px;
    border-top: 3px solid /*[[other]]*/;
    box-shadow: 0 3px 5px rgba(0,0,0,.35);
    transition: all 0s .3s;
}
#topbar .upload-button-container .creation-dropdown.upload-blue .selection, #main-nav .upload-button-container:hover .creation-dropdown .selection {
    background: /*[[main]]*/;
    border-top: none;
    border-bottom:none;
}
#topbar .user-dropdown a.active, #topbar .link-menu a.active {
    background-color:  /*[[second]]*/;
    border-left: 5px solid /*[[other]]*/;
}
.dropdown-footer {
    font-size: 12px;
    background-color:  /*[[second]]*/;
    width: 144px;
    text-align: center;
    padding: 8px;
}
html {
    background-color: rgba(18, 18, 17, 0);
}
#copyright {
    color: /*[[other]]*/;
    padding-left: 8px;
}
.nav-wrapper,#thumbs-container {
    background-color: /*[[main]]*/;
}
.combobox .selection {
    color: /*[[text]]*/;
    font-size: .9em;
    background-color: /*[[main]]*/;
}
.textbox-ten {
    background-color: /*[[second]]*/;
}
hr {
 border: 1px solid /*[[second]]*/;   
}
#topbar .upload-button,#topbar .upload-button-container .creation-dropdown .selection {
 border: 1px solid /*[[text]]*/;   
}

/*No border*/

#topbar .upload-button.upload-blue, #main-nav .upload-button-container:hover .upload-button,.btn-action,textarea,.btn-grey, .btn:disabled, .btn-small:disabled, .btn-large:disabled, .btn:disabled:hover, .btn-small:disabled:hover, .btn-large:disabled:hover,.js-post-truncated,.btn-action:hover,.btn-grey:hover {
    border-top: none;
    border-bottom:none;
}


.post-image {
    text-align: center;
    background: /*[[second]]*/;
    position: relative;
}
.post-header {
    background: /*[[second]]*/;   
}
.post-container, #comments-container.comments-initialized,.post-image-description,.post-image-title,.js-post-truncated,.post-description,.panel,.panel.menu {
 background: /*[[second]]*/;   
}
.textbox,#topbar .upload-button.upload-blue, #main-nav .upload-button-container:hover .upload-button,.btn-action,textarea,.upvote-image, .downvote-image, .favorite-image, .upvote-image:hover, .downvote-image:hover, .favorite-image:hover,.arrow,.jcarousel,.carousel-button,.button-css {
 background: /*[[main]]*/;    
}
a:link, a:active, a:visited, a strong, a,.right .panel.menu .button.selected h2,.right .panel.menu .button h2,#topbar .global-search-footer .footer-content .keyword-desc {
    color: /*[[other]]*/;
    text-decoration: none;
}
.arrow {
 color: /*[[text]]*/;   
}
.right .panel.menu .active {
 border-right: none;   
}
.jcarousel {
 border: 2px solid /*[[text]]*/;   
}
.button-css {
 border: none;   
}
#topbar .link-menu {
 background-color: /*[[second]]*/;   
}
#topbar .user-dropdown a:link, #topbar .user-dropdown a:visited, #topbar #notification-dropdown a, #topbar .link-menu a, #topbar .other-accounts ul li a, #topbar .link-menu a.hiring {
 color: /*[[other]]*/;   
}
#topbar .global-search-footer {
    background-color: /*[[main]]*/;
    border-right: none;
    border-bottom: none;
    border-left: none;
    max-width: 100%;
    height: 54px;
}
#topbar .global-search-footer .footer-content .keyword-desc {
    width: 95px;
    text-align: left;
    color: /*[[text]]*/!important;
    padding-left: 10px!important;
}

#topbar .global-search-footer div .footer-label {
    width: 50px;
    text-transform: uppercase;
    font-size: 9px!important;
    color: /*[[text]]*/!important;
    line-height: 14px;
    padding: 4px 2px!important;
    border-right: 1px solid /*[[text]]*/;
    float: left;
}
.options-btn.opened .options {
 background: /*[[second]]*/;  
}
.options-btn .options .item {
 color: /*[[text]]*/;   
}
#topbar .global-search-autocomplete {
 border-top: 3px solid /*[[other]]*/;   
}
.navPrev:hover {
    border-color: /*[[text]]*/;
}
.navPrev {
    border: 1px solid /*[[main]]*/;
}
.btn-grey, .btn:disabled, .btn-small:disabled, .btn-large:disabled, .btn:disabled:hover, .btn-small:disabled:hover, .btn-large:disabled:hover {
    background: /*[[main]]*/;
}
.noConversations {
    background-color: /*[[main]]*/;
    padding: 10px;
}
#conversations-list .contacts {
    border: none;
}

    /*Hover*/
#comment-sort.combobox:hover .selection {
    background-color: rgba(0, 0, 0, 0);
}
.combobox:hover .selection, .combobox.opened, .like-combobox-but-not:hover .selection, .like-combobox-but-not.opened {
    background-color: rgba(165, 42, 42, 0);
    color:/*[[main]]*/;
}
.taxonomy-container #topic:hover {
 background: /*[[text]]*/;  
 color: /*[[main]]*/;
}
.btn-action:hover {
 color: /*[[main]]*/;
 background: /*[[text]]*/;
 }
#header-logo a.logo:hover,#topbar .menu-btn:hover, #topbar .menu-btn.opened,#topbar li a:hover,#topbar .search-container:hover {
 background-color: /*[[main]]*/;
}
a:hover {
 color: /*[[text]]*/;    
}
.btn-grey:hover {
 color: /*[[main]]*/;
 background: /*[[text]]*/; 
}
.taxonomy-container #tags-underbar:hover {
    background: /*[[text]]*/;
    color:/*[[main]]*/;
}
#topbar .dropdown-footer a:hover {
    background-color: transparent;
    color: /*[[text]]*/;
}
#topbar .dropdown-footer a, #topbar .dropdown-footer a.active {
    display: inline-block;
    color: /*[[other]]*/;
    padding: 0;
    margin-right: 8px;
}
.post .hover {
    background-color: /*[[second]]*/;
    border: 2px solid /*[[main]]*/;
}
.post .post-info {
background-color: /*[[main]]*/
}
/*#topbar .notification-conversations a:hover {
    background: rgba(12, 74, 12, 0.72);
}*/
#topbar .notification-events button:hover {
 background: /*[[main]]*/;   
}

/*text*/

.post-image-description,.post-title,body,.post-image-title,.btn, .btn-small, .btn-large,#captions .caption .author,.post .post-info,.post p,#topbar .search-focused .search {
color: /*[[text]]*/;
}
#topbar .upload-button .upload-btn-text,.upload-button .icon-upload,#topbar .upload-button .upload-btn-text,.upload-button .icon-upload,.action-bar .stats-link,.action-bar .post-menu,.taxonomy-container #topic,.taxonomy-container #tags-underbar {
color: /*[[text]]*/;
}
}
