css:
@-moz-document url-prefix("http://arstechnica.co") {
.index-row.features .post-tease:last-child h2, .post-tease .post-meta h2 a {
    font-size: 20px;
}

body, main {
    background-color :#EFEFEF;
}

.row, .posts-column {
	display:block !important;
    width:100%;
    max-width:none !important;
    min-width:0 !important;
}

section.xrail, .row.ad-row, .ad.ad_crown, .cns-ads-container {
 display:none !important;
}
.features .post-tease {
    width:990px !important;
    position:relative;
    min-width:500px;
    margin-bottom:30px;
}
.features article:before {
    content:'';
    position:absolute;
    margin-left:-20px;
    z-index:9;
	border-left:solid #fa501e 3px;
}

.features h2 a {
   font-size: 22px !important;
line-height:26px !important;
}

article.post-tease {
    width: 900px !important;
    max-width: 100% !important;
    min-width: 0 !important;
    -webkit-flex-basis: 99999;
    flex-basis:999999;
    margin-bottom:20px;
}

.post-tease figure {
    width: 70px !important;
    height: 60px !important;
    padding-bottom: 0px !important;
    margin-top:0px !important;
    margin-left:0px;
    z-index: 3;
}
.features .post-tease figure {
    width: 200px !important;
    height: 100px !important;
    padding-bottom: 0px !important;
    margin-top:0px !important;
    margin-left:0px;
    z-index: 3;
}


.post-tease .post-meta {
    padding-left: 80px !important;
    margin-top: -70px;
    background-image:none;
}

.features .post-tease .post-meta {
    padding-left: 210px !important;
    margin-top: -107px;
    background-image:none;
}

.post-tease:after {
    height: 3px;
    border-width: 3px;
}
.post-tease .comment-count {
    left: 94%;
    bottom: 3px;
    background-image:none !important;
    background-color:#fa501e !important;
    color:#FFF !important;
}

#sponsored-recommendations {
    display:none !important;
}
}
