css:
@-moz-document url-prefix("http://arstechnica.com/") {
.row.index-row.features {
    flex-flow: column nowrap;
    border-bottom: 1px solid #8A8A8A;
    margin-top: 0px;
    padding-top: 10px;
}

.index-row .posts-column {
    flex-flow: column wrap;
    flex: 1;
}

.index-row .post-tease {
    max-width: 1200px;
    width: inherit;
    display: inherit;
    padding-left: 5px;
}

.post-tease {
    padding-bottom: 0px;
}

.post-tease>a {
    width: 250px;
    min-width: 250px;
    float: left;
}

.post-tease .post-meta {
    margin-top: 0;
    margin-left: 0;
    max-width: 940px;
    min-width: 940px;
    padding-right: 0;
    background: #313131;
}

.post-tease .comment-count {
    left:inherit;
    right: 0;
    bottom: 0px;
    min-width: 10px;
}

.post-tease .comment-count.read,
.post-tease .comment-count.unread {
    background: #272727;
    height: 36px;
    border: 1px solid #fa501e;
}


.post-tease:after {
    border: 1px solid #000;
    border-top: none;
    border-right: none;
    border-left: none;
}

.index-row .post-tease:nth-of-type(3), 
.index-row .post-tease:nth-of-type(4), 
.index-row .post-tease:nth-of-type(5),
.index-row .post-tease:nth-of-type(6),
.index-row .post-tease:nth-of-type(7), 
.index-row .post-tease:nth-of-type(8),
.index-row .post-tease:nth-of-type(9),
.index-row .post-tease:nth-of-type(10),
.index-row .post-tease:nth-of-type(11),
.index-row .post-tease:nth-of-type(12), 
.index-row .post-tease:nth-of-type(13), 
.index-row .post-tease:nth-of-type(14), 
.index-row .post-tease:nth-of-type(15), 
.index-row .post-tease:nth-of-type(16) {
    width: inherit;
    
}

.index-row .posts-column {
    width: 1200px;
}

.index-row.features .post-tease:first-child, 
.index-row.features .post-tease:last-child {
    width: 100%;
}

.index-row.features .post-tease:last-child h2 {
    font-size: inherit;
}

body {
    background: #272727;
}

main {
    background: #232323;
    color: #FAFAFA
}

.post-tease .post-meta h2 a {
    color: #FFF;
    font-size: 20px;
}

header#header #hdr-logo {
    z-index: 1;
}

article {
    /* display: none; */
    padding-left: 10px;
}

article.single .post-title {
    color: #FFF;
}

article.single .post-lowerdek {
    color: #FFF;
}

.post-page .story-sidebar-part p {
   color: #cacaca;   
}

.post-page .pullbox {
   background: #2f2f2f;
}
.post-page .gallery.shortcode-gallery {
    background: #444;
}
.post-page .shortcode-img figure img {
    width: 100%;   
}

.post-content figure.image.right {
    background-color: #fff;
}

#story-recs.rec-wrap .post-meta,
#latest-recs.rec-wrap .post-meta {
    max-width: 310px;
    min-width: 238px;
}

#story-recs.rec-wrap .post-meta h2 a,
#latest-recs.rec-wrap .post-meta h2 a {
    font-size: 18px;
}

.post-content .intro-image figcaption,
.post-page .shortcode-img .shortcode-caption {
    background: #171717;
    color: #A7A7A7;
    border: none;
}

.post-page blockquote {
    background: #171717;
    border: 1px dashed #525252;
    color: #a7a7a7;
}

.post-page blockquote .p1 {
   color: #c1c1c1;
}

main aside.ad.ad_fullwidth,
#recommendations-footer #sponsored-recommendations {
    background-color: #171717;
    border: 1px dashed #4E4E4E;
    margin-top: 25px;
}

#recommendations-footer #sponsored-recommendations #outbrain-recs .outbrain-recommendation>a .ob-title {
   color: #fff;   
}
#sponsored-recommendations .heading-column {
  padding-left:5px;
  padding-right: 5px;
}

#outbrain-recs {
   width: 980px;   
}
aside.ad.ad_crown {
    max-height: 150px;
    overflow-y: scroll;
}

.cns-ads-stage.atp-cns-ctx-out-of-page {
    margin-top: -50px;
}

article.single .single-row .xrail .xrail-content {
    padding-left: 0px;   
}

aside#social-footer a {
    color: #fff;
}

.comments-row #comments-area #comments>li>header .author {
    color: white;
}

.comments-row #comments-area #comments>li>.body .quotetitle {
    padding-left: 0;
    border-left: 0;
}

.comments-row #comments-area #comments>li>.body .quotecontent {
    border: 1px solid #313131;
    border-left: 5px solid #404040;
    border-right: 0;
    color: #AAA;
    background-color: #2B2B2B;
    padding-bottom: 10px;
}

.comments-row #comments-area .pager .comment-pagination {
    float: none;
}

}
