css:
@-moz-document url-prefix("https://workflowy.com/"), url-prefix("http://workflowy.com/") {
body {
    background-color: #FFF;
    background-image: url("https://source.unsplash.com/category/nature/1920x1080/daily") !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
}
.noted>.notes>.content {
    font-size: 11px !important;
}
.notes .content {
    font-family: "Bookerly" !important;
}
.selected>.notes>.content {
    font-size: 13px !important;
}
.mainTreeRoot.selected {
    margin-top: 30px !important;
}
.selected {
    border-top: 1px solid !important;
}
#bottomLinks,
.proPitch {
    display: none !important;
}
#header,
#keyboardShortcutHelper,
#getMoreSpaceButtonTopLeft,
#getMoreSpaceButtonTopRight {
    -webkit-transition: opacity .2s !important;
    opacity: 0 !important;
}
#header:hover,
#keyboardShortcutHelper:hover,
#getMoreSpaceButtonTopLeft:hover,
#getMoreSpaceButtonTopRight:hover {
    -webkit-transition: opacity .2s !important;
    opacity: 1 !important;
}
#pageContainer {
    height: auto !important;
    padding-bottom: 110px !important;
}
#share_buttons {
    display: none !important;
}
#visible {
    background: transparent !important;
    margin-top: 0 !important;
    padding-top: 0 !important;
    width: 400px !important;
}
#workflowy *,
.editor textarea {
    font-family: "Bookerly" !important;
    font-size: 16px !important;
    font-weight: normal !important;
    text-shadow: rgba(255, 255, 255, .5) 1px 1px !important;
}
.bullet {
    text-indent: 4px !important;
    background-color: inherit !important;
    background-image: url(http://image.flaticon.com/icons/svg/126/126490.svg) !important;
}
.project.task>.name>.bullet,
.project.open>.name>.bullet {
    background-image: url(http://image.flaticon.com/icons/svg/118/118738.svg) !important;
}
.content .contentLink {
    color: #59597F !important;
}
.content .contentTagText,
.nameAnimated .contentTagText {
    text-decoration: none !important;
}
.pageStar {
    -webkit-mask-image: url(https://workflowy.com/media/i/star-icon-empty.svg) !important;
    -webkit-mask-size: cover !important;
    background-color: #FFC107 !important;
    background-image: none !important;
    background-size: 20px !important;
    height: 20px !important;
    position: absolute !important;
    right: 10px !important;
    top: 10px !important;
    width: 20px !important;
}
.pageStar.starred {
    -webkit-mask-image: url(https://workflowy.com/media/i/star-icon-grey.svg) !important;
    -webkit-mask-size: cover !important;
    background-color: #FFC107 !important;
    background-image: none !important;
    background-size: 20px !important;
}
.top,
.right,
.bottom,
.left {
    display: none !important;
}
#pageContainer {
    font-family: "Bookerly" !important;
}
div.footer {
    display: none !important;
}
div.mainTreeRoot {
    min-height: 200px !important;
    padding-bottom: 40px !important;
}
div.page {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2) !important;
    max-width: 100% !important;
    margin-top: 20px;
    margin-right: 15% !important;
    margin-left: 15% !important;
    padding: 20px 60px;
    transform: none !important;
    width: auto !important;
    background-color: rgba(255, 255, 255, 0.9);
}
.project.highlighted::before,
.project.moving::before,
.project.tapped::before,
.draggableNameClone::before {
    background-color: rgba(255, 255, 255, 0);
}
.project.moving::before {
    background-color: rgba(55, 55, 55, 0.2) !important;
}
.children {
    border-left: none !important;
}
span.contentMatch {
    background-color: inherit !important;
}
span.contentTag {
    background-color: #b1951d !important;
    box-shadow: 3px 3px 3px #b8b8b8 !important;
    color: #f8f8f8 !important;
    font-family: "Fira Code" !important;
    padding: 1px 5px 1px 5px !important;
    display: inline-block !important;
}
span.contentTag[title*="#d_"],
span.contentTag[title*="#t_"],
span.contentTag[title~="#daily"],
span.contentTag[title~="#weekly"],
span.contentTag[title~="#monthly"],
span.contentTag[title~="#annually"] {
    background-color: #a3a3a3 !important;
    color: #1f2022 !important;
}
span.contentTag[title~="#impt"] {
    background-color: #f2241f !important;
    color: #f8f8f8 !important;
}
span.contentTag[title~="#tday"],
span.contentTag[title~="#week"],
span.contentTag[title~="#soon"] {
    background-color: #67b11d !important;
    color: #f8f8f8 !important;
}
span.contentTag[title~="#small"] {
    background-color: #4f97d7 !important;
    color: #f8f8f8 !important;
}
span.contentTag[title~="#huge"] {
    background-color: #2d9574 !important;
    color: #f8f8f8 !important;
}
span.contentTag[title~="@pom"] {
    background-color: #b03060 !important;
    color: #b03060 !important;
}
span.contentTag[title~="@FIXME"],
span.contentTag[title~="@PR"],
span.contentTag[title~="@BACKLOG"],
span.contentTag[title~="@DONE"],
span.contentTag[title~="@DOING"],
span.contentTag[title~="@PAUSE"],
span.contentTag[title~="@WAITING"],
span.contentTag[title~="@NOTES"] {
    color: #f8f8f8 !important;
    background-color: #a31db1 !important;
    font-weight: bold;
}
span.contentTag[title~="@PersonalKanban"] {
    color: #1f2022 !important;
    background-color: #ffa500 !important;
}
span.contentTag[title~="@Work"] {
    color: #1f2022 !important;
    background-color: #2d9574 !important;
}
span.contentTag[title~="@Templates"] {
    color: #1f2022 !important;
    background-color: #b1951d !important;
}
span.contentUnderline {
    background-color: #ff0 !important;
    text-decoration: none !important;
}
span.contentItalic {
    font-family: "Fira Code" !important;
    text-decoration: inherit !important;
    font-style: inherit !important;
}
.parent>.name>.parentArrow:before {
    content: "";
    background-image: url(http://image.flaticon.com/icons/svg/118/118738.svg) !important;
}
}
