body *{
    box-sizing: border-box;
    font-family: "Open Sans";
    font-style: normal;
    text-decoration: none;
    font-weight: normal !important;
}

a{
	text-decoration: none;
}

a:hover{
	text-decoration: underline;
}

table, td, th{
	border: 1px solid gray !important;
}

td{
	height: auto !important;
}

img{
	height: auto !important;
    width: 100% !important;
}

table{
	border-collapse: collapse !important;
}

.h{
	color: #333333;
    font-size: 5em;
    font-family: "Roboto";
    font-weight: 100;
}

.ib, .ibs{
	background: none !important;
}

#footer{
	background-image: none !important;
    background-color: #368CC9;
    width: 100% !important;
    font-size: 1.3em;
    min-height: 85px;
}

#footer:after{
	content: "Redesign by Josef Kuchař - http://www.josefkuchar.cz";
    color: white;
    padding-bottom: 20px;
}

#menu, #lcol .bh, #main, .be, .lbox, body, #root, .ibe{
	background-image: none !important;
}

#rcol, #lcol .bt p:last-of-type,#lcol .bt p:nth-child(8), .nmenu img, a[href="javascript:history.go(-1)"], #header, #lcol .bh:nth-last-child(2), #maine{
	display: none !important;
}

#lcol .bt p:nth-child(7)::after{
    content: '';
	border-top: 1px solid gray;
    margin-top: 10px;
    margin-bottom: 10px;
    display: block;
}

#tmenu{
    width: 100%;
    padding-left: 10px;
    line-height: 50px;
    height: 400px;
    background-color: #292929;
    text-transform: capitalize;
    background: url(https://scontent-vie1-1.xx.fbcdn.net/v/t31.0-8/12891770_1039030419494254_4634441823609163635_o.jpg?oh=a5324d4642b5deb9f192c2a6907d3a12&oe=5943E0CE) 80% 20%;
}

#tmenu:before{
	content: "ZŠ Komenského Náchod";
    float: left;
    color: black;
    font-size: 3em;
    padding-right: 10px;
    font-family: "Roboto";
    font-weight: 100;
}

#tmenu ul{
    display: block;
    width: inherit;
	text-align: center;
    margin: auto !important;
    
}

#tmenu ul li{
    padding: 0 8px !important;
	border: none !important;
    display: block;
    text-align: center;
    margin: auto;
    font-size: 1.4em;
}

#tmenu a, #tmenu span {
	color: #333333 !important;
}

#tmenu ul ul li a {
	font-size: 0.8em !important;
}

#tmenu ul ul li{
	line-height: initial;
}

#tmenu ul ul{
	top: 40px;
}

#menu ul ul
{
	display:none
}

#menu ul li:hover > ul{
	position: absolute !important;
    left: 0 !important;
    display: block;
    width: inherit !important;
    height: 45px;
}

#menu ul ul li{
	width: 100px !important;
    left: -15px;
    background-color: white;
}

#menu ul ul a{
	width: 100px !important;
    background-color: #E6E6E6;
    color: black;
    text-align: center;
    padding: 0;
}

#menu ul li:hover > ul li a {
  float: left;
  font: bold 1.1em arial,verdana,tahoma,sans-serif;
  line-height: 45px;
  text-decoration: none;
  margin: 0;
}

#menu ul ul li{
	width: auto;
}

#menu > ul > li{
	background-color: #368CC9;
	color: black;
    height: auto !important;
    font-size: 1.3em;
	transition: background-color 0.2s ease;
}

#menu span{
	padding: 0 10px !important;
}

#menu{
	width: 100%;;
    background-color: #368CC9;
    height: 40px;
    line-height: 40px;
}

#menu ul ul{
	line-height: 20px;
    top: 40px;
}

#menu ul ul li{
	background-color: #07ACED;
}

#menu ul ul li a:hover{
	background-color: #07A0ED !important;
}

#menu ul{
	text-align: left;
    display: inline-block;
}
#menu li{
    border: none !important;
}

#header{
	margin: 0 !important;
}

#lcol .bh{
	background-color: #4A4A4A;
    width: 100% !important;
    line-height: 30px;
    height: 30px !important;
    font-size: 1.2em;
}

#lcol .bh:hover{
   background-color: #404040; 
}

#lcol .bh div{
	padding-top: 0 !important;
}
#lcol .lbox{
	margin: 0;
    width: 100%;
}
#root{
	background-color: #eee;
}
#cont, #lcol{
	background-color: white;
}
#main{
	padding-top: 20px;
    max-width: 1200px;
    width: inherit;
}
#mcol, #mcol .ib, #mcol .ibs, #mcol .ibe, #mcol .ibt{
	width: 100%;
}

#mcol .ibt{
	padding: 20px;
}

#cont{
	max-width: 900px;
    width: inherit;
}

#header span:hover{
	opacity: 0.7;
}

#header span{
	transition: opacity 0.2s ease;
}

br{
	display: none;
}

/* FOR TAMPER MONKEY */
hr{
	border: 0;
    height: 1px !important;
	background-color: #DBDBDB;
}
}

@-moz-document url("http://www.komenskehozsnachod.cz/") {
#cont .clt img{
	width: 40% !important;
}
}
