css:
@-moz-document domain("d20pfsrd.com") {
#sites-chrome-everything,
.sites-embed-title {
    color: #ededed;
    background-color: #202020;
}
a {
    text-decoration: none;
}
/*a,*/

a:link,
#sites-chrome-everything a:link,
#sites-chrome-sidebar-left .sites-embed a:link,
#sites-chrome-sidebar-right .sites-embed a:link {
    color: #5FC8E8 !important;
}
a:visited,
#sites-chrome-everything a:visited,
#sites-chrome-everything a:visited,
#sites-chrome-sidebar-left .sites-sidebar-nav a:visited,
#sites-chrome-sidebar-right .sites-sidebar-nav a:visited,
#sites-chrome-everything .goog-tree-item-label a:visited {
    color: #AE89CA !important;
}
.sites-embed-content-sidebar-textbox {
    background-color: inherit;
}
#sites-canvas,
.sites-sidebar-nav,
#sites-page-title-header,
#sites-page-title,
#title-crumbs,
.sites-canvas-main,
#sites-canvas-bottom-panel,
ul[jotid="navList"],
table[bordercolor] tr {
    background-color: #3a3a3a;
}
.sites-embed,
.sites-embed-title,
#sites-canvas,
.sites-sidebar-nav li.topLevel {
    border: none;
}
.sites-sidebar-nav .current-bg {
    background-color: #555 !important;
    border-bottom: #999 dashed 1px;
}
#sites-canvas h1,
#sites-canvas h2,
#sites-canvas h3,
#sites-canvas h4,
#sites-canvas h5,
#sites-canvas h6,
.goog-toc {
    color: white;
    background-color: #3a3a3a !important;
}
#sites-canvas-wrapper table th {
    font-size: inherit !important;
}
div[style*="box-shadow"],
table[bordercolor],
table[bordercolor] caption {
    -webkit-box-shadow: 0 0 16px 0 #000 !important;
    -moz-box-shadow: 0 0 16px 0 #000 !important;
    box-shadow: 0 0 16px 0 #000 !important;
}
div[style*="box-shadow"] div,
div[style*="rgb(255,255,255)"],
div[style*="rgb(243,243,243)"],
div[style*="rgb(204,204,204)"],
#sites-canvas .sites-embed-content-textbox h4 {
    background-color: #474747 !important;
}
div[style*="rgb(204,204,204)"] {
    color: white !important;
}
#sites-canvas-main-content>table,
table[bordercolor] td[style*="rgb(255,255,255)"],
div[style*="float"] {
    background-color: #3a3a3a !important;
}
div[style*="box-shadow"] div:first-child,
div[style*="rgb(234,153,153)"] {
    background-color: #644 !important;
}
table[bordercolor] caption {
    background-color: #565060;
    border: #888 solid 1px;
    border-bottom: none;
    padding-left: 5px !important;
}
table[bordercolor] tr[style*="rgb(243,243,243)"],
table[bordercolor] td[style*="rgb(243,243,243)"] {
    background-color: #555 !important;
}
table[bordercolor] tr[style*="rgb(207,226,243)"]:first-child,
table[bordercolor] td[style*="rgb(207,226,243)"],
table[bordercolor] th {
    background-color: #202020 !important;
    border-color: #888;
    border-bottom-color: inherit;
    font-size: 9px !important;
}
table[width="100%"],
#sites-page-title-header {
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
    /*border: none;*/
}
table[width="100%"] td,
span[style*="rgb(255,229,153)"] {
    background-color: inherit !important;
}
div[style*="float"] table[width="100%"] {
    -webkit-box-shadow: 0 0 16px 0 #000 !important;
    -moz-box-shadow: 0 0 16px 0 #000 !important;
    box-shadow: 0 0 16px 0 #000 !important;
}
div[style*="rgb(207,226,243)"]>table,
table[style*="rgb(207,226,243)"],
table[style*="clear:both;width:100%;padding:2px"] tr {
    background-color: #456 !important;
}
div[style*="border-top-left-radius"],
div[style*="rgb(217,210,233)"] {
    background-color: #446 !important;
}
div[style*="rgb(182,215,168)"] {
    background-color: #464 !important;
}
div[style*="rgb(252,229,205)"],
table[style*="clear:both;width:100%;padding:2px"] tr td:first-child {
    background-color: #5a5040 !important;
}
#sites-chrome-everything #sites-canvas .sites-embed-title {
    background-color: #644 !important;
    color: white !important;
}
.sites-embed-content-textbox {
    background-color: #474747;
    color: #ededed;
}
h4[style*="16px"][style*="border-top"],
p[style*="border-top"] {
    font-size: 16px !important;
}
h1,
h2 {
    border-bottom: none !important;
    -webkit-box-shadow: 0 8px 10px -7px #5B8A42;
    -moz-box-shadow: 0 8px 10px -7px #5B8A42;
    box-shadow: 0 8px 10px -7px #5B8A42;
}
h3,
h4,
p[style*="border-top"],
div[style*="border-top"] {
    -webkit-box-shadow: 0 8px 10px -7px #6483B4;
    -moz-box-shadow: 0 8px 10px -7px #6483B4;
    box-shadow: 0 8px 10px -7px #6483B4;
}
h1,
h2,
h3,
h4,
p[style*="border-top"],
div[style*="border-top"] {
    border: none !important;
}
td span[style*="rgb(255,255,255)"] {
    background-color: inherit !important;
}
}
