css:
@-moz-document domain('eldarya.fr'), url-prefix('http://www.eldarya.fr/'), regexp("http://(fa1|fa2|www)\\.eldarya\\.(com|fr|pl|de|dk|es|hu|it|nl|ro|ru|se|uk)\\/.*") {#contentPane > div {
text-align: center !important;}

#contentPane > div {
text-align: center !important;}


.tooltip-content {
    position: fixed;
    font-family: Nilland, serif;
    color: white;
    background-color: #2D557E;
    border: 1px solid #0E1541;
    padding: 10px;
    border-radius: 6px;
}
.tooltip-content {
    position: fixed;
    font-family: Nilland, serif;
    color: white;
    background-color: #2D557E;
    border: 1px solid #2D557E;
    padding: 10px;
    border-radius: 6px;
    text-shadow: 0px 0px 1px #fff6d3 !important;
}
.flavr-button.gold,
.button.button.button-gold {
    background-color: #F9CE4D;
    border-color: #F9CE4D;
}
.flavr-button.gold,
.button.button.button-gold:hover,
.flavr-button.gold,
.button.button.button-gold.selected {
    background-color: white !important;
    color: #F9CE4D !important;
}
.flavr-button.purple,
.button.button.button-purple:hover,
.flavr-button.purple,
.button.button.button-purple.selected {
    background-color: white !important;
    color: #0E1541 !important;
}
#header-login-form input {
    color: #0E1541;
    font-family: Banda, sans-serif;
    margin: 0 3px;
}
body {
    background: #4da8fd url("http://rife.tech/wp-content/uploads/2016/03/Fantasy-Wallpaper-Desktop-Background-.jpg") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    position: absolute;
    top: 0;
    background-color: #0E1541;
    height: 30px;
    width: 100%;
}
#header-menu a {
    font-family: Nilland, sans-serif;
    color: #6FC0FF;
}
#footer {
    font-family: Nilland, sans-serif;
    background-color: #0E1541;
}
#footer #footer-links a {
    color: #6FC0FF;
}
#footer #footer-in {
    color: #6FC0FF;
}
#header {
    height: 167px;
    position: relative;
    background-color: rgba(255, 255, 255, 0.5);
}
#player-headerPortrait {
    border: solid 4px #2D557E;
    box-shadow: 0 0 5px 0 #2196F3;
    background-color: #4b7d65;
}
#pet-headerPortrait {
    top: 35px;
    height: 73px;
    width: 73px;
    border-radius: 50%;
    border: solid 4px #2D557E;
    box-shadow: 0 0 5px 0 #2196F3;
    background: #2D557E;
    position: absolute;
    left: -10px !important
}
#player-name {
    font-family: Nilland, sans-serif;
    color: #fff;
    font-weight: 600;
}
#player-info-level {
    font-family: Candara;
    color: #0E1541;
    background-color: #F9CE4D;
    box-shadow: 0 0 5px 0 #2196F3;
}
#player-info-dots .player-info-dot {
    background-color: #0E1541;
    box-shadow: 0 0 5px 0 rgba(25, 25, 25, 0.25);
}
#player-info-dots .player-info-dot.lit {
    background-color: #4b7d65;
    box-shadow: 0 0 5px 0 rgba(0, 228, 255, 0.62);
}
#player-infos .player-currencies {
    background-color: #4b7d65;
    border: 1px solid;
    border-color: #4b7d65;
}
.tile-title {
    height: 37px;
    color: #fff;
    line-height: 37px;
    font-size: 17px;
    font-family: Amatic, serif;
    font-weight: 400;
    text-align: center;
}
img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("http://i.imgur.com/ZcfnWQz.png") top no-repeat;
    padding-left: 431px;
    padding-top: 165px;
    position: absolute;
    top: -0px;
    left: -130px;
}
.index-carousel {
    font-family: Nilland;
    border: 1px solid #00E4FF;
    background-color: #2D557E;
    overflow: hidden;
}
.index-carousel-title {
    font-family: Amatic, serif;
    color: white;
}
.index-carousel-subtitle {
    border-top: 1px solid #F9CE4D;
    font-family: "Nilland", serif;
    color: #F9CE4D;
}
.index-carousel-description {
    text-shadow: 0px 0px 2px white, 0px 0px 2px white !important;
    font-family: "Nilland", serif;
    text-align: justify;
    height: 80px;
}
.index-main-container .index-carousel-description {
    color: white;
}
.flavr-button.purple,
.button.button.button-purple {
    background-color: #4da8fd;
    border: 1px solid;
    border-color: #4da8fd;
}
#ranking-list > div {
    background: #2D557E no-repeat 20px;
    background-size: 75px;
    position: relative;
    border-top: 1px solid #4da8fd;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#ranking-list .guard-4 {
    background: #4b7d65 url('http://www.eldarya.fr/static/img/design/guard/guard-4-big.png') no-repeat 5px;
    background-size: 90px;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#index-ranking {
    background-color: #0C163E;
    border: 1px solid #4b7d65;
}
#news-list .news {
    background: #2D557E no-repeat 20px;
    position: relative;
    border-top: 1px solid #4b7d65;
    height: 50px;
    box-shadow: inset 0px 0px 2px rgba(0, 0, 0, 1), 0px 0px 5px #00fff6 !important;
}
#index-news .text a {
    color: #FFFFFF;
}
#index-news {
    background-color: #0C163E;
    border-radius: 10px;
    border: 1px solid #4b7d65;
}
.tile-title {
    color: #00BCD4;
}
#index-news .date {
    float: left;
    background-color: #0C163E;
    width: 55px;
    height: 25px;
    color: #FFFFFF;
    border-radius: 5px;
    font-size: 14px;
    text-align: center;
}
#index-player {
    border: solid 1px #00E4FF;
    background-color: #fff;
}
#main-menu {
  margin-top: -25px;
  position: absolute;
    z-index: 9;
    display: inline-block;
    width: 69px;
    height: 69px;
    border-radius: 5%;
    margin: -160px 30px;
    background-position: top;

}

#main-menu .menu-label {
    display: none;
    opacity: 0 ;
    position: fixed;
    white-space: nowrap;
    top: 5px;
    right: 5px;
    bottom: -10px;
    left: -10px;
    text-align: center;
    z-index: 1000;
    pointer-events: none;
    

      }
  


#main-menu .main-menu-episode {
    background-image: url("http://i.imgur.com/qdyJkLS.png");
}
#main-menu .main-menu-minigames {
    background-image: url("http://i.imgur.com/PDNLXdd.png");
}
#main-menu .main-menu-mall {
    background-image: url("http://i.imgur.com/SV5WPnq.png");
}
#main-menu .main-menu-marketplace {
    background-image: url("http://i.imgur.com/WCvL1RL.png");
}
#main-menu .main-menu-ranking {
    background-image: url("http://i.imgur.com/zsYyoTt.png");
}
#main-menu .main-menu-library {
    background-image: url("http://i.imgur.com/klif943.png");
}
#main-menu .main-menu-messaging {
    background-image: url("http://i.imgur.com/AxUl2Sz.png");
}
#main-menu .main-menu-bank {
    background-image: url("http://i.imgur.com/ltjTbXp.png");
}

#main-menu .main-menu-messaging .menu-label span {
    background-color: #383173;
}
#main-menu .main-menu-bank .menu-label span {
    background-color: #3C3373;
}
#library-npc-crush-heart {
    position: absolute;
    left: -14px;
    top: -10px;
    width: 30px;
    height: 23px;
    background: url("http://img11.hostingpics.net/pics/452048affectionfull.png") 0% 0% / 100%;
}
img[src="/static/img/design/m.png"] {
    width: 0 !important;
    height: 0 !important;
    background: url("http://img15.hostingpics.net/pics/21275116711012m.png") !important;
    padding-left: 14px !important;
    padding-top: 15px !important;
}
img[src="/static/img/design/f.png"] {
    width: 0 !important;
    height: 0 !important;
    background: url("http://img15.hostingpics.net/pics/52677834316483f.png") !important;
    padding-left: 9px !important;
    padding-top: 15px !important;
}
#pet-info-affection-level div {
    height: 25px;
    background: url('http://img15.hostingpics.net/pics/370787affectionfull.png');
}
#pet-info-affection-level {
    display: inline-block;
    width: 150px;
    height: 23px;
    background: url('http://img15.hostingpics.net/pics/806978affectionempty.png');
}
#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    background: url('http://img15.hostingpics.net/pics/292218278157luckfull.png') no-repeat left;
    margin-left: 6px;
}
#pet-info-evolve div.on {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}
#pet-info-food,
#pet-info-levels {
    background: rgba(116, 149, 186, 0.7) !important;
}
#pet-info-energy-level,
#event-bonus-progressbar-fill {
    background-color: #F9CE4D;
}
#messageBarLevel {
    background-color: #F9CE4D;
}
#episode-list > li .episode-conditions li.on {
    color: white;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important;
}
}
