@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
     div#globalContainer{

    margin: 0 !important;

    padding-right: 0 !important;

    width: 84.9% !important;

  }

  div#contentCol{

    border-right: 0 !important;

    margin-right: 172 px !important;

    padding-right: 11px !important;

  }

    div._3ss, div#pagelet_canvas_nav_content, div#eventsNav, div#developerNav, div#interestsNav, .rightCol, #rightCol, div#rightCol, ._4-u3._k2r {
       display: none !important;
    }
    
 
    ._2iwq{
        display:none;
    }
  div[id^="substream"]{
      width:1300px;
      width:50%;
  }
    div[id^="substream"]:nth-child(even){
      float:left;
  }
    div[id^="substream"]:nth-child(odd){
      float:right;
  }
  div[id^="u_ps_"]:nth-last-child(-n+2) {
      float:left;
  }
     div[id^="u_ps_"]:nth-child(odd){
      float:right;
  }

   div[id^="feed_stream"]{ 
        width:900px;
   }
}
