css:
@-moz-document regexp("https:\\/\\/[A-z]+\\.wikipedia\\.org.*")
{

/*
 * Wikipedia
 */

/* Hide ... */
							/* Header */
div#p-personal,						/* Accounts bar */

							/* Sidebar */
div#mw-panel div:not(:first-child):not(:last-child),	/* Everything but languages */
h3#p-lang-label,					/* Language label */
span.uls-settings-trigger,				/* Language settings */
li#interwiki-completelist,
a.wbc-editpage,						/* Edit links */

							/* Body */
div#footer						/* Footer */
{
	display: none;
}

/* Hide separator sidebar */
div#mw-panel div.portal
{
	background-image: none;
}

/* Offset content buttons */
div#left-navigation, div#right-navigation
{
	margin-top: 8px;
}

/* Offset content */
div#mw-page-base
{
	height: 3em;
}

