css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("t411.al") {

  body {
    background-color: #303030;
    padding:0 !important; /*chat*/
    font-family: Ubuntu,Arial,Helvetica,sans-serif;
  }

  body,h3 {
    color: rgba(255, 255, 255, 0.8) !important;
  }

  h2 {
    background-image: none !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    border:0 !important;
    margin-top:20px !important;
    margin-bottom:20px !important;
  }

  h2 span{
    font-size: 90% !important;
    color:rgba(255, 255, 255, 0.8) !important;
  }

  h2 span a{
    font-size: 60% !important;
  }

  .block > h2 span {
    border: none;
    border-bottom: 2px solid rgba(255, 255, 255, 0.8) !important;
    background: none !important;
    border-radius: 0 !important;
    padding-bottom: 5px;
  }

  div p a, a span,b a, a {
    font-weight:normal;
    text-decoration: underline;
    color: #F5DEB3 !important;
  }

  div p a:hover,a:hover span {
    text-decoration: none;
  }

  a.btn, a.btn span {
    text-decoration:none;
    color:inherit !important;
  }

  /* Forms */

  textarea, textarea:focus,input[type="text"],input[type="password"]:not(#password),select{
    border:1px solid #999 !important;
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
    padding-left:2px !important;
    font-family: "ubuntu mono",monospace,serif;
    font-size:90% !important;
  }

  select option {
    padding-left:2px !important;
  }

  /* Header */

  div.headerPlace.active div.loginWrapper div.userBox img.avatar-big {
    border:none;
  }

  .headerPlace {
    margin-bottom: 0;
  }

  .headerPlace a{
    color: #303030 !important;
  }

  ul#lavamenu a,dl.active{
    color: rgba(255, 255, 255, 0.8) !important;
  }

  .headWrapper {
    background: none;
    background-color: #303030;
    box-shadow:none;
  }

  div.headerPlace div.headWrapper div.head div.toolbar div.loginBar span a{
    color:#36414b !important;
    text-decoration:none;
  }

  /* Menu */

  #lavamenu li.backLava,#lavamenu li.noLava {
    background:none;
    border-color:rgba(255, 255, 255, 0.8) !important;
  }

  #lavamenu li.noLava:last-child {
    background:none;
    border:none;
  }

  #lavamenu li ul {
    background:none;
    background-color:#303030;
    border-color:rgba(255, 255, 255, 0.8) !important;
    z-index: 42000;
  }

  /* Footer */

  div.wrapper div.footer{
    background-image: none !important;
  }

  div.wrapper div.footer p{
    padding-top:50px;
  }

  div.wrapper div.footer a{
    color:whitesmoke !important;
  }

  /* Détails torrent */

  div.block.torrentDetails div.accordion * {
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
  }

  div.block.torrentDetails div.accordion pre {
    border:none;
    color: olivedrab !important;
  }

  div.block.torrentDetails div.accordion td {
    padding:2px;
  }

  div.details table * {
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
  }

  div.details table li a {
    text-decoration: none;
  }

  div.navigation a,
  div span.data.right a{
    color:whitesmoke !important;
  }

  span[style="color: #0000FF"],
  span[style="color: blue"],
  span[style="color: darkblue"],
  span[style="color: indigo"]{
    color: whitesmoke !important;
  }

  article blockquote {
    background-color: inherit !important;
  }

  div.block.torrentDetails div.block.votes a,
  div.block.torrentDetails div.block.votes a span{
    font-size:90%;
    font-weight: normal;
  }

  /* Votes */

  div.block.votes a.call span {
    color:inherit !important;
  }

  /* Comments */

  table.comment tr,table.comment td{
    background-color:#303030;
  }

  table.comment{
    border:none !important;
  }

  table.comment tbody tr th a{
    text-transform: uppercase;
    color:rgb(85,85,85) !important;
  }

  table.comment tbody tr th{
    font-size:90%;
    font-weight:normal;
  }

  table.comment tbody tr td p {
    text-align:left !important;
  }

  table.comment tbody tr th.userinfo img.avatar {
    border:none;
  }

  table.comment tbody tr th.userinfo span{
    color:whitesmoke !important;
  }

  table.comment tbody tr th.userinfo a.cls-1 {
    color: bisque !important;
  }

  table.comment tbody tr th.userinfo a.cls-2 {
    color: coral !important;
  }

  table.comment tbody tr th.userinfo a.cls-3 {
    color: crimson !important;
  }

  div table.comment tbody tr td,
  div table.comment tbody tr th{
    border-top:1px solid rgba(255, 255, 255, 0.8) !important;
    position:unset !important;
  }

  div table.comment tbody tr td:nth-child(1),
  div table.comment tbody tr td:nth-child(2),
  div table.comment tbody tr th:nth-child(1){
    border:none !important;
  }

  
  /* Homepage, FAQ */

  div.accordion.newsWrapper *:not(a){
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
  }

  div.accordion.newsWrapper div.announce.open p span.date,
  div.content article.block p span.date{
    color:whitesmoke !important;
  }

  div.accordion.newsWrapper a span {
    color:inherit !important;
  }

  /* Chat */

  div#wrapper div#bar,div#wrapper div#bar *,
  div#wrapper div#messages,div#wrapper div#messages *,
  div#wrapper div#form,div#wrapper div#form *{
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
    border:none !important;
    background-image: none !important;
    margin:0 !important;
  }

  form#chat input#text-input {
    font-size: 130% !important;
  }

  div.block.textleft.forumPosts ul li a.topic {
    color:#F5DEB3 !important;
  }

  /* Results */

  table.results tr td {
    background-color: inherit;
  }

  table.results a {
    color : inherit !important;
    text-decoration: none;
  }

  table.results a span {
    text-decoration: none;
  }

  table.results tr th a {
    font-weight:bold;
  }

  table.results {
    border:1px solid rgba(255, 255, 255, 0.8);
  }

  table.results tr th,table.results tr th a {
    background-color: #808080 !important;
    color: #100000 !important;
  }

  table.results tbody tr td.up, table.results strong.up, table.results a.up, table.results span.up, 
  table.comment tbody tr th.userinfo span.up,
  table tbody tr td.up strong{
    color:lightgreen !important;
  }

  table.results tbody tr td.down, table.results strong.down, table.results span.down,
  table.comment tbody tr th.userinfo span.down,
  table tbody tr td.down strong
  {
    color:#FF6633  !important;
  }

  table.results span.pending {
    color:orange  !important;
  }

  table.results tfoot tr td.textcenter div.pagebar {
    font-size: 80% !important;
  }

  table.results tbody tr:hover td{
    background-color: #202020   !important;
  }

  /* NFO in results, api */

  div#info.overlay.black div.inner pre,
  html body pre{
    background-color: rgb(66,66,66) !important;
    color: olivedrab !important;
    border: none;
  }

  /* Mail pop-up */

  div#jBoxID1.jBox-wrapper.jBox-Confirm.jBox-Default div.jBox-container, 
  div#jBoxID1.jBox-wrapper.jBox-Confirm.jBox-Default div.jBox-container div.jBox-Confirm-footer {
    background-color: #303030;
    color:whitesmoke !important;
  }

  /* Thanks pop-up */

  div#info.overlay.black div.inner {
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
    text-align:justify;
    padding: 10px;
    padding-bottom: 0;
    height: 2000px;
  }

  /* Mail content */

  div.sub.mail img.alignleft.avatar,
  div.sub.mail a.alignleft.avatar
  {
    border:0;
  }

  div.sub.mail p.notice span.alignright,
  table.compose tbody tr td label {
    color:whitesmoke !important;
  }

  table.mailbox.results tbody tr td {
    background-color: inherit;
  }

  /* Profile search */

  div.itemWrapperHalf.profile div.block img.avatar.left
  {
    border:0;
  }

  /* Profile */

  div.itemWrapperHalf.profile div.block div.private {
    background-color: #303030;
    color:whitesmoke !important;
    border:0;
  }

  div.editForm table tbody tr th img#edit-avatar.avatar.alignright {
    border:0;
  }

  /* RSS infos */

  div.rssinfo, div.rssinfo *,div#feedBody, div#feedBody * {
    background-color: #303030 !important;
    color:whitesmoke !important;    
  }

  /* Bonus */

  html body div.wrapper div.contentWrapper div.content div.block div.textcenter table.bonus tbody tr th {
    background-color: #303030 !important;
    color:whitesmoke !important;    
  }

  /* Forum content */

  div.Comment div.Message pre {
    border:none;
    color: olivedrab !important;
    background-color: rgb(66,66,66) !important;
  }

  div.Comment div.Message a,
  div.Tabs.HeadingTabs.DiscussionTabs ul li a.TabLink{
    font-weight:normal;
    text-decoration: underline;
    color: #F5DEB3 !important;
  }

  div.Comment div.Message a:hover,
  div.Tabs.HeadingTabs.DiscussionTabs ul li a.TabLink:hover{
    text-decoration:none; 
  }

  li.role-power-seeder div.Comment div.Meta span.Author a {
    color:coral !important;
  }

  li.role-uploader div.Comment div.Meta span.Author a,
  li.role-user div.Comment div.Meta span.Author a{
    color: bisque !important;
  }

  div.Tabs.HeadingTabs.DiscussionTabs {
    background-color: rgb(66,66,66) !important;
  }

  div.signupForm p.notice {
    color:whitesmoke;
  }

  div.Comment div.Meta span.DateCreated,
  div.Comment div.Meta div.CommentInfo span{
    color:whitesmoke;
  }

  div#Content div.Foot a.TabLink.SignInPopup {
    background-color: rgb(66,66,66);
    border:none;
  }

  /* Forum search */

  html body#dashboard_search_index.Dashboard.Search div#Frame div#Body div#Content ul.DataList.SearchResults li.Item div.ItemContent div.Meta span {
    color:whitesmoke;
  }

  html body#dashboard_search_index.Dashboard.Search div#Frame div#Body div#Content ul.DataList.SearchResults li.Item div.ItemContent div.Meta span a.Category{
    color:whitesmoke !important;
    background-color: rgb(66,66,66) !important;
  }

  html body#dashboard_search_index.Dashboard.Search div#Frame div#Body div#Content div.Tabs.SearchTabs form div{
    display:none !important;
  }

  /* Forum categories */

  li.Item.Announcement div.ItemContent.Discussion a.Title,
  li.Item div.ItemContent.Discussion a.Title,
  div.Meta span.LastCommentBy a,
  div.Meta span.LastDiscussionTitle a,
  div.ItemContent.Category a.Title{
    font-weight:normal;
    text-decoration: underline;
    color: #F5DEB3 !important; 
  }

  li.Item.Announcement div.ItemContent.Discussion a.Title:hover,
  li.Item div.ItemContent.Discussion a.Title:hover,
  div.Meta span.LastCommentBy a:hover,
  div.Meta span.LastDiscussionTitle a:hover,
  div.ItemContent.Category a.Title:hover{
    text-decoration: none;
  }

  div.ItemContent.Discussion div.Meta span a.Category,
  html body#vanilla_categories_all.Vanilla.Categories.all div#Frame div#Body div#Content div.Tabs.Headings.CategoryHeadings{
    background-color: rgb(66,66,66) !important;
    color:whitesmoke !important;
    border:none;
  }

  div.Pager.NumberedPager a {
    font-size:80% !important;
    color:inherit !important;
  }

  div.Pager.NumberedPager a.Highlight {
    text-decoration: underline;
  }

  div#Content div.Tabs.DiscussionsTabs * {
    background-color: rgb(66,66,66) !important;
  }

  div#Content div.Tabs.DiscussionsTabs a {
    text-decoration: underline;
    color: #F5DEB3 !important; 
  }

  div#Content div.Tabs.DiscussionsTabs a:hover {
    text-decoration: none;
  }

  body.Vanilla div#Frame div#Head {
    background-image:none;
    background-color: rgb(66,66,66) !important;
    border:none;
  }

  ul#Menu a:hover {
    background:rgb(66,66,66) none !important;
  }

  ul#Menu li ul,ul#Menu li ul li{
    background:rgb(66,66,66) none !important;
  }

  ul#Menu li ul

  div.Tabs.Headings.CategoryHeadings,
  html body#dashboard_search_index.Dashboard.Search div#Frame div#Head{
    background:rgb(66,66,66) none !important;
    border:none;
  }

  div.Meta span.LastCommentDate {
    color:whitesmoke !important;
  }


  /* Wiki */

  div#dokuwiki__site *:not(a):not([class^=note]):not(pre):not(code):not(samp):not(kdb) {
    background:rgb(66,66,66) none;
    color:whitesmoke;
  }

  div#dokuwiki__site [class^=note],
  div#dokuwiki__site [class^=note] a{
    color:rgb(66,66,66) !important;
  }

  div#dokuwiki__site [class^=note] *:not(a):not([class^=note]){
    background:transparent none !important;
    color:inherit !important;
  }

  div#dokuwiki__site a {
    /*background-color:rgb(66,66,66);*/
    text-decoration: underline;
    color: #F5DEB3 !important;
  }

  div#dokuwiki__site a:hover {
    text-decoration: none;
  }

  div#dokuwiki__site pre,
  div#dokuwiki__site code,
  div#dokuwiki__site samp,
  div#dokuwiki__site kbd,
  div#dokuwiki__site [class^=note] pre,
  div#dokuwiki__site [class^=note] code,
  div#dokuwiki__site [class^=note] samp,
  div#dokuwiki__site [class^=note] kdb{
    background:rgb(66,66,66) none !important;
    box-shadow:none;
    color: olivedrab !important;
  }

  html.js.desktop, html{
    height:100%
  }

  html.js.desktop body {
    min-height: 100%;
  }

  /* Search */

  form#searchform.plainform div div.terms-wrapper div.terms p.alignleft.notice {
    color:whitesmoke;
  }

  /* Messages */

  html body div.headerPlace div#messages {
    width:400px;
    text-align:center !important;
    top: 0 !important;
    color:rgb(66,66,66);
    padding:10px;
    border:none !important;
  }

  html body div.headerPlace div#messages p{
    margin:0 !important;
    width:unset;
  }
  
  
  /* Ads */

  html body div.wrapper div.contentWrapper div.content div.align-center div a#duck2,
  html body div.wrapper div.contentWrapper div.content div.align-center div img,
  html body div.wrapper div.contentWrapper div.content div.align-center div,
  html body div.wrapper div.contentWrapper div.content div.block div.block table.results tbody tr td a img,
  html body div.wrapper div.contentWrapper div.content div.block p a[href="#"],
  html body div.wrapper div.contentWrapper div.content div.block p.dbnw26543,
  html body div.wrapper div.contentWrapper div.content div.block p.dbnw26543 + div,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.align-center div a#duck,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.block.description article div.align-center p,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.block.description article h2.align-center,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.block.description article div.align-center a img,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.drgt14368,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.drgt14368 + div,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.share,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.drgt14367,
  html body div.wrapper div.contentWrapper div.content div.block.torrentDetails div.drgt14367 + div,
  html body div.wrapper div.contentWrapper div.content div.drgt14368,
  html body div.wrapper div.contentWrapper div.content div.drgt14368 + div

  {
    display:none !important;
  }
}
