css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forbes.com") {
.article-page {
    padding-left: 0%;
}
.main-article .article-rail {
    padding-right: 0px;
    margin-right: 0%;
}
.article-page .page-main .article-container.comments-expanded, .article-page .page-main .article-container.default-expanded, .article-page .page-main .article-container.nav-expanded, .article-page .page-main .article-container.trending-expanded {
  margin-left: 0px;
  padding-right: 0px;
} 
.article-sidebar.collapse-full, .article-sidebar.collapse-sharing-login {
    min-height: 450px;
    display: none;
}

.fbs-ad-rail,
.top-fbs-ad-sticky,
.article-gallery-embedded,
.article-trending-panel.default-expanded {
  display:none;
}

/* Remove this part if you want to keep the "Recommended by Forbes" bit. */
.vestpocket {
    display: none;
}
/* ----------------------------------------------------------------------*/

}
