css:
@-moz-document domain("deviantart.com") {

body.deviantart #overhead {
    background-color: #081E1A;
    border-bottom: 1px solid #1E251F;
    box-shadow: #000 0 0 1em;
}

#overhead #search7 input[name=q] {
    background-color: #2B2B2B;
    border: #5FAC75 1px solid;
}


div#output {
    background-color: #110C0D;
}

#output>.browse-container.newbrowse .browse-left-bar {
    background-color: #20221F;
    border-right: 1px solid rgba(5, 204, 167, 0.18);
}

.navbar-menu-inner {
    background: #20221F;
}

.navbar-menu-inner-scroll {
    background: #030E14;
}

#navbar-menu .navbar-menu-item {
    color: #2FAA90;
}

#output>.browse-container.newbrowse .browse-left-bar .browse-left-bar-inner {
    background-color: transparent;
}

.shadow>a.thumb>img:not(.lit) {
    box-shadow: 0 0 0 1px rgba(0,0,0,0.1),0 1px 1px rgba(0,0,0,0.2),0 10px 12px -4px rgba(5, 202, 204, 0.3);
    opacity: .6;
}

.tt-fh.tt-boxed .tt-bb {
    background-color: transparent;
    outline: rgba(5, 204, 176, 0.16) 1px solid;
}

#browse-sidemenu ul li a {
    color: #22C1A4;
}

.tt-fh .details a.t, .tt-fh .details span.t {
    color: #05CCA2;
}

#output>.browse-container.newbrowse .browse-left-bar {
    background-color: #071F1C;
    border-right: 1px solid rgb(33, 178, 155);
}

.tt-fh .details a.t:hover, .tt-fh .details span.t:hover .tt-fh .details a.tt-fh-pr:hover, .tt-fh .tt-fh-oe:hover {
    color: #C0FFED;
    text-shadow: #000 0 0 0.4em,#05CC94 0 0 1em;
}

#browse-sidemenu ul li a.selected {
    background: #18C196;
    color: #0D1211;
    box-shadow: #1883C1 0 0 1em;
}

.browse-search-container,.browse-search-input, input, textarea, input[type=text], input[type=password], select {
    color: #18C196;
    background-color: #110C0D !important;
    border-color: cyan !important;
}

.browse-search-container {
    text-shadow: 1px 1px 1px #fff;
    background: #fff;
}

#browse-sidemenu .noncollapseHeader {
    font-size: 1.2em;
    color: cyan;
}

* {
    border-radius: 0 !important;
}

.right-buttons .button,#browse-sidemenu .browse-facet.browse-facet-state .button {
    background-color: #110C0D;
    color: #00FFFF;
    box-shadow: none;
    border: 1px solid #00FFFF;
    transition:all .8s ease;
}

a {
    transition: all .8s ease;
}

body {
    background-color: #000;
    color: #555;
}

.dev-page-container.bubbleview, body.fullview .dev-page-container.bubbleview, body.gruze .dev-page-container.bubbleview, body.deviant .dev-page-container.bubbleview{
    background-color: rgba(182, 21, 21, 0);
}

.dev-view-meta {
    background-color: rgba(8, 30, 26, 0.39);
}

.bubbleview {
    background-color: transparent !important;
}

html {
    border-color: black;
}

.dev-page-view .dev-right-col-bg {
    background-color: transparent;
}

.dev-page-view .dev-page-button, .dev-page-view .dev-page-button.disabled:hover, .dev-page-view #fave-btn-wrap #gmi-ResourceViewFavouriteButton, .dev-page-view .dev-meta-producttabs #print-button, .dev-page-view .dev-meta-producttabs #buy-tabs,.dev-page-view #fave-btn-wrap #gmi-ResourceViewFavouriteButton,.deviation-share-new .share-button {
    font: 16px 'Trebuchet MS',sans-serif !important;
    color: #04F5E3 !important;
    text-decoration: none;
    text-shadow: 0 1px 0 rgba(0,0,0,0.05);
    border: 1px solid rgba(3, 249, 172, 0.35) !important;
    background: #d6e2d2;
    background: -webkit-linear-gradient(top, #1E1F20, #000000);
    background: -moz-linear-gradient(top, #e2ece1, #d4e0ce);
    background: linear-gradient(top, #e2ece1, #d4e0ce);
    border-radius: 4px;
    box-shadow: none;
    position: relative;
}

.dev-page-view .dev-right-bar-title {
    color: #00FFD4;
}

a.share-button.note {}

.submit_to_groups_link.dev-page-button {}

.dev-page-view .submit_to_groups_link>a {
    color: cyan;
}

:link, :visited {
    color: cyan;
}

.smbutton-textonly, .smbutton-textonly>span, .smbutton-textonly:hover, .smbutton-textonly:active, .smbutton-textonly:active>span {
    color: cyan;
}

h1 a, .dcats a.h {
    color: cyan !important;
    transition: all .8s ease;
}

.dev-page-view.view-mode-full .dev-view-deviation, .dev-page-view.view-mode-full_zoomed .dev-view-deviation {
    background: black;
}

.ccomment .ch-ctrl .ch-ctrl {
    background-color: #031720;
    color: #9EB9B9;
    border: 1px solid rgba(181, 191, 181, 0.26);
}
}
