css:
@-moz-document domain("safaribooksonline.com") {
#sbo-rt-content .chapter p,
.card-annotation .annotation-quote,
.resizer .draggable-containment-wrapper:after,
.resizer .draggable-containment-wrapper:before,
body.video,
#lesson-fragment,
#sbo-rt-content 
{
    font-family: sans-serif !important;
}

header,
footer,
.trigger,
#font-controls,
button,
.js-search-controls.search-controls
{
    background: Black !important;
}

section.sbo-toc-container,
a.prev.nav-link, 
a.next.nav-link
{
    background: Black !important;
    color: #00dd00 !important;
}
p, #container.application {
    background: Black !important;
    color: #00dd00 !important;
}
.sbo-title h1,
p a:link {
    color: #aae0fe !important
}
p a:visited {
    color: #3464cb !important
}
#sbo-rt-content code.literal,
h1, h2, h3, h4, h5 {
    color: #e09e14 !important
}
#lesson-fragment .pre,
#lesson-fragment .pre-ex,
#lesson-fragment .pre1,
#lesson-fragment .pre_w,
#lesson-fragment p.pre,
#lesson-fragment pre,
#sbo-rt-content .pre,
#sbo-rt-content .pre-ex,
#sbo-rt-content .pre1,
#sbo-rt-content .pre_w,
#sbo-rt-content p.pre,
#sbo-rt-content pre,
.note, .note .inner p
{
    background-color: #1b2426 !important;
    color: #fff !important
}

}
