css:
@-moz-document domain("mc.shockbyte.com") {
#server-form, .stdtable, .breadcrumb, #sidebar, #cfgfile-form, #content > .col-md-9 > div, #user-form, #content > .col-md-9 > form {
    background: #fdfdfd;
    border-radius: 4px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
    padding: 10px;
}

#sidebar {
    border-top: 0;
}

#sidebar .portlet-title {
    color: black;
}

#sidebar a {
    color: black;
    transition: 0.2s;
}

.detail-view > tbody > tr:nth-child(odd) > td, .detail-view > tbody > tr:nth-child(odd) > th {
    background: transparent;
}

body {
    color: #000000;
    background-color: #e6e6e6 !important;
}

.detail-view > thead > tr > th, .detail-view > tbody > tr > th, .detail-view > tfoot > tr > th, .detail-view > thead > tr > td, .detail-view > tbody > tr > td, .detail-view > tfoot > tr > td {
    border-top: 0;
}

table .titlerow, table .titlerow td, table .titlerow th {
    padding: 10px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.07);
}

#players-ajax div {
    padding: 10px 20px;
    background: transparent;
}
.form-control {
    background: #e6e6e6;
    border: 0;
    color: black;
}

.detail-view .hintRow a, .list-view .hintRow a {
    background: #e6e6e6;
    border: 0;
}

.popover {
    background-color: white !important;
    border: 0;
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2), 0 0 3px rgba(0,0,0,0.2);
}

.popover.left .arrow:after {
    border-left-color: #e6e6e6;
}

.popover.left .arrow {
    border-left: 0px solid white;
}

input[value="Start"] {
    background: #27AE60;
}

input[value="Stop"] {
    background: #d95753;
}

input[value="Restart"] {
    background: #2980B9;
}

#buttons input {
    border: 0;
    padding: 8px 20px;
    box-shadow: 0 1px 5px rgba(0,0,0,0.2);
}

.btn-primary[disabled] {
    background: #000000;
    color: #ffffff;
    opacity: 0.2;
    text-shadow: none;
}

#sidebar a:hover {
    color: black;
    background: rgba(0, 0, 0, 0.1);
}

#sidebar ul {
    margin: 0 -10px;
}

.items > thead > tr > th {
    background: rgba(0, 0, 0, 0.1);
}

#log-ajax {
    background: #161616 !important;
}

input#command {
    background: white;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075);
}
}
