.tab-stack{
	margin: 2px 1px ;	
	padding: 1px 2px ;
	background: rgba(255,255,255,.6) ;
	border: 1px solid rgba(110,110,110,.6) ;
	border-radius: 3px ;
	background-clip: padding-box ;
	min-height: 24px ;

}

.tab-stack:hover{
	margin: 2px 1px ;	
	padding: 1px 2px ;
	background: rgba(255,255,255,.8) ;
	border: 1px solid rgba(110,110,110,.6) ;
	border-radius: 3px ;
	background-clip: padding-box ;
	min-height: 24px ;
 
}



.tabbrowser-tab[selected="true"]{
  color: rgb(0,0,0) !important;
  text-shadow: 0px 0px 0px rgba(0,173,238,.75) !important;
  font-weight: bold !important;
  border-bottom: 2px solid #FF0000   !important;
}

/* grey */

tab .tab-icon, .tab-icon-image, .tab-throbber {
  filter: grayscale(1);
}


#TabsToolbar .tabbrowser-tab[selected] .tab-icon, 
#TabsToolbar .tabbrowser-tab[selected] .tab-icon-image, 
#TabsToolbar .tabbrowser-tab[selected] .tab-throbber {
  filter: grayscale(0);
}


/* find */



searchbar .search-go-button[hidden="true"] {
  display: -moz-box !important;
}



.tab-background-start, 
.tab-background-end {
  visibility: hidden !important;
  box-shadow: none !important;
 
}

.tab-background-middle { 
  background: none !important;

}




/* wight tab */
#main-window .tabbrowser-tab {
  height: 26px !important;
  min-height: 26px !important;
 
}

#plugins-notification-icon, #notification-anchor-icon, #notification-popup-box {
    display: none !important;
    
}

/* clear menu right button 0 row */
    #context-navigation{
      display:none !important;
    }
