css:
@-moz-document domain("worktile.com") {
/**
 *  Author: chuyik(https://github.com/chuyik)
 *  Copyright: 凹凸实验室(http://aotu.io/)
 *  Url: https://userstyles.org/styles/127959/worktile-o2team-theme
 */

body, button, html, input, select, textarea {
    color: #4d4d4d;
    font: -apple-system, "Helvetica Neue", "Arial", "PingFang SC", "Hiragino Sans GB", "STHeiti", "Microsoft YaHei", "Microsoft JhengHei", "Source Han Sans SC", "Noto Sans CJK SC", "Source Han Sans CN", "Noto Sans SC", "Source Han Sans TC", "Noto Sans CJK TC", "WenQuanYi Micro Hei", SimSun, sans-serif;
}

/*顶部的导航栏的背景色*/
.mod-navbar {
    background-color: #21201F;
    border: none;
}

.mod-navbar .main .title > h3 {
	color: #BFBFBF;
    font-size: 14px;
	line-height: 42px;
}

/*顶部的导航栏的小图标的阴影，去掉*/
.mod-navbar .main .title h3 i{
	text-shadow:none;
}

/*顶部的导航栏的标题文字颜色*/
.mod-navbar .main .title h3 a{
	color:#BFBFBF;
}

/*项目区域的背景色*/
.entries_panel {
	background-color:#838C91;
}

/*任务列表的背景色*/
.entries_panel .entries_panel_sortarea .entry{
	background-color:#E2E4E6;
}

/*点击任务后，弹出的任务详细面板的背景色*/
.entity-well{
	background-color:#F8F8F8;
}

/*去掉动画*/
.wt-animate, .ng-enter, .ng-leave {
    transform: none !important;
}

/*去掉动画*/
.slide-content {
    transition-duration: 0.01s;
}

/*改变任务描述的UI*/
.slide-content {
    border: none;
}
.slide-content, .slide-content .entity-well {
    background-color: #FFF;
}
.entity-well-content .entity-hr {
    display:none;
}
.entity-well .entity-well-header a.entity-project-name {
    display:none;
}

/*下拉菜单去掉描边*/
.dropdown-menu {
    border:none;
}

/*任务列表UI*/
.entries_panel .entries_panel_sortarea .entry {
    border-radius: 3px;
}
.entries_panel .entry-new {
    margin-left: 10px;
    background: rgba(0,0,0,.12);
    cursor: pointer;
    color: #fff;
}
/*卡片*/
.entries_panel .entries_panel_sortarea .entry .entry-task-panel {
    padding: 0 5px;
}
.entries_panel .entries_panel_sortarea .entry .entry-task-panel .entry-tasks .task {
    border-bottom: 1px solid #ccc;
    border-radius: 3px;
    margin-bottom: 6px;
    max-width: 300px;
}
.entries_panel .entries_panel_sortarea .entry a.js-add-task-composer {
    color: #8c8c8c;
}
.entries_panel .entries_panel_sortarea .entry .entry-task-footer {
    line-height: 0;
    padding: 10px 0;
}
.entries_panel .entries_panel_sortarea .entry a.js-add-task-composer:hover {
    color: #4d4d4d;
}
.entry-task-footer:hover {
    background-color: #cdd2d4;;
    text-decoration: underline;
}
.entries_panel .entries_panel_sortarea .entry {
    padding-bottom: 0;
    overflow: hidden;
}
/*卡片底部*/
.task-badges .task-badge {
    color: #51504D;
}
.entry-add-bottom-composer.entry-task-composer .composer-footer {
    padding: 8px 0;
}
/*卡片顶部*/
.entries_panel .entries_panel_sortarea .entry .entry_header .entry_header_title h2 {
    font-size: 14px;
    font-weight: 700;
    padding: 5px 10px 3px;
}
.entries_panel .entries_panel_sortarea .entry .entry_header a.entry_header_number {
    color: #999;
}
/*侧边栏*/
.layout_right_sidebar[style="width: 1px;"] {
    width: 0px !important;
}
#leftmenu #leftmenu_backdrop {
    display: none;
}

/*工作台顶部*/
.layout_content_main > .height-nonav {
    top: 50px;
}

/*项目顶部*/
.mod-navbar .sub {
    position: absolute;
    left: 250px;
    top: 17px;
}
.mod-navbar .sub .sub_toolbar {
    margin: -4px 15px 0 0;
}
.layout_content .layout_content_main .layout_content_main_nonav {
    top: 57px;
}

/*按钮*/
.btn.btn-link.btn-cancel {
    color: #4d4d4d;
}

/*缩小顶部菜单按钮的大小*/
.sub_toolbar > .btn-group > .btn {
    padding: 5px 10px;
    font-size: 12px;
    line-height: 1.5;
    border-radius: 2px;
}

/*缩小顶部菜单文字的大小和颜色*/
.mod-navbar .main .center-menu ul>li a, .mod-navbar .sub ul.mod_subnav>li a {
    color: #A7A7A7;
    font-size: 14px;
}

/*进一步减小顶部菜单的高度*/
.mod-navbar .main .title {
    padding: 8px 7px 2px 10px;
}
.mod-navbar .main {
    line-height: 50px;
    min-height: 50px;
}
.mod-navbar .main .center-menu {
    line-height: 50px;
    min-height: 50px; 
}
.mod-navbar {
    min-height: 50px;
}
.layout_content .layout_content_main .layout_content_main_tasks {
    top: 50px;
}

/*通知中心顶部*/
.mod-navbar .main.main-clickable:hover {
    background-color: #3E3E3E;
}

.mod-navbar .main.main-clickable .main-clickable-text {
    padding-right: 15px;
}

.mod-navbar .main.main-clickable:hover .main-clickable-text {
	color: #EEE;
    font-weight: bold;
}
.mod-navbar .main .title h3 a.prj_icon {
    display: inline-block;
    padding-bottom: 0px;
}

/*去掉顶部广告*/
#broadcast {
    display: none !important;
}
}
