css:
@-moz-document domain("zhihu.com") {
.top-nav-dropdown li a {
    border-top: 1px solid purple;
}
html.no-touchevents .top-nav-dropdown a:hover {
    background-color:Orchid;
}
.zu-top-nav-userinfo.selected, html.no-touchevents .top-nav-profile:hover .zu-top-nav-userinfo {
    height: 46px!important;
    background: DarkOrchid;
    background: -webkit-linear-gradient(top,DarkOrchid,DarkOrchid);
    background: linear-gradient(to bottom,DarkOrchid,DarkOrchid);
    box-shadow: inset 0 0 2px rgba(0,0,0,.12);
}
.zu-top-nav-userinfo:hover,.top-nav-profile:hover,.top-nav-profile:hover{
    background-color:DarkOrchid!important;
}
.top-nav-profile a {
    background-color: DarkOrchid;
}
.icon-ic_help:before {
    content: none;
}
.block-title .help {
   background-color: MistyRose;
}

.comment-box .comment-feature-description .block-title>span {
    background-color: MistyRose;
    font-weight: 400;
    padding: 0 1em;
}
.block-title>span {
    background-color: MistyRose;
    position: relative;
    z-index: 1;
    padding-right: 16px;
}
.comment-list-container.ui-pagination {
    position: relative;
    width: 100%;
    top: 0;
    bottom: 0;
    background-color: MistyRose;
}
.js-vote-count{
 color:white!important;
}
span.count{
 color:white!important;
}
span.name,a.name{
    color:purple!important;
}
a.zm-ac-link:hover,.copyright:hover{
    background-color:rgba(0,0,0,0)!important;
}
.ac-active{
	background-color:pink;
}
.zh-backtotop .btn-action:hover,.item-collapse:hover{
    background-color:purple!important;
}
.zm-votebar .up:hover, .zm-votebar .down:hover{
    background-color:purple!important;
}
._CommentBox_cannotCommentReason_1_q5 {
    padding: 1.5em 1em;
    border-radius: 4px;
    text-align: center;
    /* background: #fbfbfb; */
    border-top: 1px solid purple;
    background: rgba(0, 0, 0, 0);
}
.zg-icon-question-mark{
    opacity: 1;
    -ms-filter: "alpha(Opacity=100)";
    visibility:hidden;
}
.zg-btn-blue {
    color: #fff!important;
    /* text-shadow: 0 -1px 0 rgba(0,0,0,.5); */
    background: purple;
    /* background: -webkit-linear-gradient(top,#1d7fe2,#0865c2); */
    /* background: linear-gradient(to bottom,#1d7fe2,#0865c2); */
    /* border: 1px solid #0D6EB8; */
    /* box-shadow: 0 1px 0 rgba(255,255,255,.2) inset,0 1px 0 rgba(0,0,0,.2); */
}
._CommentForm_bordered_2isg {
    margin: 0;
    padding: 1em;
    border-top: 2px solid purple;
    border-radius: 0 0 4px 4px;
    background: rgba(0, 0, 0, 0);
}
.zm-item-vote-count {
    background-color: pink;
    color: white;
}
blockquote {
    border-left: 5px solid purple;
}
.zm-comment-box{
    border: 1px solid purple;
    background-color:MistyRose;
}
.zm-topic-cat-main .zm-topic-cat-item a{
    border: 1px solid pink;
}
html.no-touchevents .zm-topic-cat-main .zm-topic-cat-item:hover a, .zm-topic-cat-main .zm-topic-cat-item.current a{
    background: pink;
    border-color: pink;
}
.Sorter .lbl, .Sorter a.lbl{
color:white;
}
.zh-backtotop .stick{
background-color: white;
}
.zh-backtotop .arrow {
    border-bottom-color: white;
}
.zh-backtotop .btn-action{
background-color: pink;
}
.item-collapse.is-sticky {
    position: fixed;
    right: auto;
    z-index: 1;
    margin: 0;
    background-color: pink;
    color: #fff;
    opacity: .9;
    -ms-filter: "alpha(Opacity=90)";
}
pre.code-diff, body .highlight, .editable pre{
    /* background: #f8f8f8; */
	background: pink;

}
.Sorter .lbl, .Sorter a.lbl {
    padding: 0 19px 0 7px;
    text-decoration: none;
    display: block;
    /* color: #999; */
}
.lbl{
background-color:pink;
}
#zh-question-collapsed-wrap, #zh-question-answer-wrap, .zu-question-collap-title {
    padding: 0px 0!important;
    color: Purple;
    border-bottom: 1px solid purple;
}
.anno-box span {
    padding-left: 15px;
    margin-left: 15px;
    border-left: 1px solid purple;
}
.zg-btn-green, .zg-btn-follow {
    width: auto;
    _width: 78px;
    min-width: 78px;
    color: white!important;
    /* text-shadow: 0 1px 0 rgba(255,255,255,.3); */
    background: #8ab923;
    /* background: -webkit-linear-gradient(top,#adda4d,#86b846); */
    /* background: linear-gradient(to bottom,#adda4d,#86b846); */
    /* border: 1px solid #6d8f29; */
    /* box-shadow: 0 1px 0 rgba(255,255,255,.5) inset,0 1px 0 rgba(0,0,0,.2); */
}
.zg-btn-green, .zg-btn-follow {
    width: auto;
    _width: 78px;
    min-width: 78px;
    color: white!important;
    text-shadow: 0 1px 0 rgba(255,255,255,.3);
    background: #8ab923;
    background: -webkit-linear-gradient(top,#adda4d,#86b846);
    background: linear-gradient(to bottom,#adda4d,#86b846);
    border: 1px solid #6d8f29;
}
.zh-question-answer-wrapper .item-collapse, .awesome-answer-list .item-collapse,.zm-item-tag, .zm-tag-editor-edit-item{
background:pink;
}
.zu-top-search-input{
     border: 1px solid white;
}
.zu-top-nav-link,.name{
color:white!important;
}
.zg-btn-blue,.submit-buttonzg-btn-blue, .zg-btn-blue,.zg-btn-blue {
    /* color: #fff!important; */
    /* text-shadow: 0 -1px 0 rgba(0,0,0,.5); */
     background-color: purple;
    color:white;
    /* background: -webkit-linear-gradient(top,#1d7fe2,#0865c2); */
    /* background: linear-gradient(to bottom,#1d7fe2,#0865c2); */
    /* border: 1px solid #0D6EB8; */
    /* box-shadow: 0 1px 0 rgba(255,255,255,.2) inset,0 1px 0 rgba(0,0,0,.2); */
    /* background: -webkit-linear-gradient(top,#1d7fe2,#0865c2); */
    /* background: linear-gradient(to bottom,#1d7fe2,#0865c2); */
        /* border: 1px solid #0D6EB8; */
}
#zh-question-collapsed-wrap, #zh-question-answer-wrap,.zu-question-collap-title {
    padding: 10px 0;
    color: Purple;
    border-bottom: 1px solid purple;
}
.zu-main-feed-con,.zm-side-nav-group+.zm-side-nav-group,.SidebarSection,.feed-item+.feed-item,.zh-answers-title,#zh-question-answer-wrap .zm-item-answer, .zm-item-answer+.zm-item-answer, .zm-item+.zm-item {
    border-top: 1px solid purple;
}
._CommentBox_divider_3NgO {
    margin: 0 -1em;
    height: 0;
    color: MediumPurple;
    text-align: center;
    border-top: 1px solid purple;
}
._CommentBox_dividerText_eF9Y {
    position: relative;
    z-index: 2;
    top: -.8em;
    padding: 0 .8em;
    display: inline-block;
    background-color:MistyRose;
}
.icon-spike{
    width: 16px;
    height: 16px;
    background-image: url(http://o837j3tok.bkt.clouddn.com/sprites-1.9.2.4c54885a1.png);
    background-repeat: no-repeat;
    display: inline-block;
    vertical-align: middle;
}
._CommentBox_root_G_1m._CommentBox_bordered_3Fo- {
    border: 1px solid purple;
    border-radius: 8px;
    box-shadow: 0 2px 2px rgba(0,0,0,.05);
}
._CommentItem_root_PQNS+._CommentItem_root_PQNS {
    border-top: 1px solid purple;
}
button[id="zu-top-add-question"] {
    color: purple;
}
span[class="count"] {
    color: white;
}
#zh-question-answer-form-wrap .zu-edit-button,span {
    color: purple;
}
.up.pressed {
    background-color: purple!important;
}
.zm-votebar .up .vote-arrow {
    border-bottom-color: white;
}
.down.pressed {
    background-color: purple!important;
}
.down {
    background-color: pink!important;
}
.up {
    background-color: pink!important;
}
.icon.vote-arrow {
    border-top-color: white!important;
}
blockquote {
    color: purple;
}
.zm-votebar .down .vote-arrow {
    border-top-color: purple;
}
.zm-votebar .up,
.zm-votebar .down {
    color: purple;
}
.zu-top-add-question {
    background: linear-gradient(to bottom, Orchid, Orchid);
    border: Orchid;
}
.zu-top-nav-li.current {
    background: linear-gradient(to bottom, Orchid, Orchid);
}
.zu-top-search-form .zu-top-search-button {
    background: linear-gradient(to bottom, Orchid, Orchid);
    border: Orchid;
}
.feed-source a.follow-topic {
    color: purple;
}
body,
.zm-meta-panel {
    background-color: MistyRose;
    color: purple;
}
.zu-top {
    background: linear-gradient(to bottom, DarkOrchid, DarkOrchid);
    border: DarkOrchid;
}
/* unvisited link */

a:link {
    color: purple;
    text-decoration: none;
}
/* visited link */

a:visited {
    color: purple;
    text-decoration: none;
}

/* mouse over link */

a:hover {
    color: Indigo;
    text-decoration: none;
    background-color:pink;
}
/* selected link */
.zg-btn-blue:hover{
    background-color:purple;
}
.zu-top-link-logo:hover{
    background-color:rgba(0,0,0,0);
}
#zh-top-link-home:hover,.zu-top-nav-link:hover{
    background-color:Orchid;
}
a:active {
    color: blue;
    text-decoration: none;
}
.zg-num {
    background-color: darkOrchid;
}
.zm-side-nav-link.active,
html.no-touchevents .zm-side-nav-link:hover {
    color: Indigo;
}
.zm-side-nav-link.active .zg-num,
html.no-touchevents .zm-side-nav-li:hover .zg-num {
    background-color: darkOrchid;
}
.zm-side-nav-link.active,
html.no-touchevents .zm-side-nav-link:hover {
    background-color: plum;
}
}

@-moz-document url-prefix("https://www.zhihu.com/question/") {
header.Sticky.AppHeader {
  background: DarkOrchid;
}
.Button--primary.Button--blue, .Button--primary.Button--blue:disabled {
    background: Orchid;
    border: 1px solid Orchid;
}
.Button--primary:hover.Button--blue:hover, .Button--primary:hover.Button--blue:hover {
    background: DarkOrchid;
    border: 1px solid DarkOrchid;
}
.VoteButton,a.Button--plain{
	color:purple!important;
}
.Comments-container,.Comments{
	background-color:MistyRose;
}
body,.Card,.QuestionHeader-footer,.QuestionHeader{
	background-color:MistyRose;
}
.ContentItem-actions{
	margin: 0 0px 0px;
}
}
