@-moz-document domain(dubtrack.fm) {
    @media screen and (min-width: 72em) {
        #main-room {
            max-width: initial;
            padding: 3.6rem 0 3.5rem
        }
        #main-room .right_section {
            width: 450px!important;
            border-radius: 0;
            float: right!important;
            height: 100%
        }
        #main-room #chat .chat-container .chat-messages {
            height: inherit
        }
        #chat,
        #main-room #chat .chat-container {
            height: 100%
        }
    }
    #main-room .left_section #main_player .player_sharing .videoquality-el {
        transition: text-shadow, color;
        transition-duration: .25s
    }
    #main-room .left_section #main_player .player_sharing .videoquality-el.active-hd {
        text-shadow: 0 0 5px #fff
    }
    #main-room .left_section #main_player .player_sharing .videoquality-el:hover {
        text-shadow: 0 0 5px #BBB
    }
    #main-room .left_section #main_player .player_sharing span.icon-reload {
        transition: transform .5s cubic-bezier(0, 0, .33, 1.5);
        -webkit-transition: -webkit-transform .5s cubic-bezier(0, 0, .33, 1.5)
    }
    #main-room .left_section #main_player .player_sharing span.icon-reload:hover {
        transition: transform .5s cubic-bezier(.42, 1.2, 1, 1);
        -webkit-transition: -webkit-transform .5s cubic-bezier(.42, 1.2, 1, 1);
        transform: rotate(360deg);
        -webkit-transform: rotate(360deg)
    }
    @-moz-keyframes shaking {
        0% {
            transform: translate(-2px)
        }
        33.33% {
            transform: translate(1px)
        }
        66.66% {
            transform: translate(-1px)
        }
        100% {
            transform: translate(2px)
        }
    }
    @-webkit-keyframes shaking {
        0% {
            transform: translate(-2px)
        }
        33.33% {
            transform: translate(1px)
        }
        66.66% {
            transform: translate(-1px)
        }
        100% {
            transform: translate(2px)
        }
    }
    @keyframes shaking {
        0% {
            transform: translate(-2px)
        }
        33.33% {
            transform: translate(1px)
        }
        66.66% {
            transform: translate(-1px)
        }
        100% {
            transform: translate(2px)
        }
    }
    #main_player .player_sharing .mute .icon-sound-alt {
        -moz-animation: shaking .125s 5 linear;
        -webkit-animation: shaking .125s 5 linear;
        animation: shaking .125s 5 linear
    }
    @-moz-keyframes room-song_span-scroll {
        0% {
            right: -100%
        }
        100% {
            right: 150%
        }
    }
    @-webkit-keyframes room-song_span-scroll {
        0% {
            right: -100%
        }
        100% {
            right: 150%
        }
    }
    @keyframes room-song_span-scroll {
        0% {
            right: -100%
        }
        100% {
            right: 150%
        }
    }
    #room-list #container-room-list .room-item header {
        background: #1c1c1c
    }
    #room-list #container-room-list .room-item .current-song {
        position: absolute;
        z-index: 1;
        width: 100%;
        text-overflow: ellipsis
    }
    #room-list #container-room-list .room-item::after {
        content: ".";
        position: relative;
        display: block;
        padding: .5rem 1rem;
        background: #2f2f2f;
        font-size: .85rem;
        color: transparent;
        z-index: 0
    }
    #room-list #container-room-list .room-item:hover .current-song {
        display: table;
        animation: room-song_span-scroll 8s linear;
        animation-iteration-count: infinite
    }
    #player-controller .right ul li .add-to-playlist-button:hover .icon-heart::before {
        -webkit-text-stroke: none;
        -webkit-text-fill-color: initial;
        transition: text-shadow .25s linear, -webkit-text-fill-color .15s linear;
        text-shadow: 0 0 1rem #b3e790
    }
    #player-controller .right ul li .add-to-playlist-button.grabbed .icon-heart::before {
        -webkit-text-stroke: none;
        -webkit-text-fill-color: #b3e790
    }
    #player-controller .right ul li .add-to-playlist-button .icon-heart::before {
        -webkit-text-stroke: 2px #b3e790;
        -webkit-text-fill-color: transparent
    }
    #room-main-player-container-soundcloud .playerElement.soundcloud .redirect-link {
        opacity: 0;
        transition: opacity .1s linear
    }
    #room-main-player-container-soundcloud:hover .playerElement.soundcloud .redirect-link {
        opacity: .75
    }
    #room-main-player-container-soundcloud:hover .playerElement.soundcloud .redirect-link:hover {
        opacity: 1
    }
    @keyframes you-got-mail {
        0% {
            transform: translateY(-3px)
        }
        6.25% {
            transform: translateY(0)
        }
        12.5% {
            transform: translateY(-3px)
        }
        18.75% {
            transform: translateY(0)
        }
        25% {
            transform: translateY(-3px)
        }
        100%,
        31.25% {
            transform: translateY(0)
        }
    }
    @keyframes blink {
        0%, 31.25% {
            opacity: .33
        }
        100%,
        32% {
            opacity: 1
        }
    }
    #header-global .header-right-navigation .user-messages .message-counter {
        animation: you-got-mail 1.5s linear infinite, blink 1.5s linear infinite
    }
    #browser {
        z-index: 5
    }
    #browser.animate {
        bottom: 7rem;
		left: 0px!important;
        padding-top: 10.6rem
    }
    @media screen and (min-width: 72em) {
        #browser.animate {
            bottom: 3.5rem;
			left: 0px!important;
            padding-top: 7.1rem
        }
    }
    span.close-browser {
        font-size: 1.125rem!important;
        padding-top: 1.0rem!important;
		height: 100%!important;
		background-color: #e1e1e1!important;
		border-right: 1px solid #878c8e!important
    }
    span.close-browser:before {
        content: "\e903"
    }
	#browser .browser-content .browser-content-header .form {
		padding: .35rem 6.8rem .35rem .3rem!important
	}
	#browser .browser-content .browser-content-header .form .music-type-select {
		right: 0px!important;
		top: 5px!important;
		width: 104px!important
	}
    #browser .browser-content .browser-content-header .form .music-type-select .music-type-dropdown span {
        line-height: .8
    }
    #browser .browser-content .browser-content-header .form .music-type-select .music-type-dropdown span:hover {
        background: #6D6D6D
    }
	#browser .browser-content .browser-content-header {
		height: 50px!important
	}
    #browser .browser-content {
        border-radius: 0;
        background-color: rgba(0, 0, 0, .5);
        transition: background-color .25s linear;
		max-width: 1495px!important;
		margin: 0
    }
    #browser .browser-content:hover {
        background-color: rgba(0, 0, 0, .7)
    }
    #browser .browser-content .browser-content-main {
		padding-top: 50px!important
	}	
	#browser .browser-content .browser-content-main .nano {
        opacity: .6;
        transition: opacity .25s linear
    }
    #browser .browser-content:hover .browser-content-main .nano {
        opacity: 1
    }
    #browser .browser-content .browser-content-main .nano .content-videos #results_video_api .browserPlaylistItems {
        counter-reset: browser-item-counter
    }
    #browser .browser-content .browser-content-main .nano .content-videos #results_video_api .browserPlaylistItems>[class$="-item"] figure {
        left: .2em
    }
    #browser .browser-content .browser-content-main .nano .content-videos #results_video_api .browserPlaylistItems>[class$="-item"] {
        border-left: 1em solid rgba(255, 255, 255, .07);
        padding-left: 0
    }
    #browser .browser-content .browser-content-main .nano .content-videos #results_video_api .browserPlaylistItems>[class$="-item"]::before {
        counter-increment: browser-item-counter;
        content: counter(browser-item-counter);
        color: #fff;
        font-size: .5rem;
        position: absolute;
        top: 50%;
        left: -1rem;
        transform: translateY(-50%);
        width: 1rem;
        text-align: center;
        line-height: 1
    }
    #room-list #container-room-list .room-item {
        border-radius: 0;
        cursor: pointer;
        border: 2px solid transparent;
        box-shadow: 0 0 15px 1px #000
    }
    #room-list #container-room-list .room-item header .description .name,
    #room-list #container-room-list .room-item header .user-info .room-user {
        cursor: text
    }
    #room-list #container-room-list .room-item header .user-info .room-user a {
        color: #0ff;
        font-weight: 400
    }
    #room-list #container-room-list .room-item.userRoom {
        border-color: #5f9ea0;
        box-shadow: 0 0 5px 2px #5f9ea0
    }
    #room-list #container-room-list .room-item:not(:hover) .current-song {
        text-align: center
    }
    #roomFormUpdate {
        top: 58px;
        width: 70rem;
        margin: 0;
        padding-right: 1px;
        border-radius: 0
    }
    #roomFormUpdate .modal-header h3 {
        margin: 0;
        text-align: center
    }
    #roomFormUpdate .modal-footer,
    #roomFormUpdate .modal-header {
        background: rgba(84, 86, 85, .8)
    }
    #roomFormUpdate .closebtn {
        color: #fff;
        top: .75rem
    }
    #roomFormUpdate .closebtn:hover::before {
        color: #0ff
    }
    #roomFormUpdate .mid {
        overflow-y: scroll;
        height: calc(100vh - 58px - 56px - 83px)
    }
    #roomFormUpdate .modal-footer {
        margin-top: 0;
        padding: .25rem 1rem
    }
    #roomFormUpdate .modal-footer button.btn {
        border: 2px solid #878c8e!important;
        padding: .5rem .75rem!important;
        line-height: 1
    }
    #roomFormUpdate .mid select {
        padding: 0
    }
    #roomFormUpdate .mid select option:not(selected) {
        color: #000
    }
    #roomFormUpdate::-webkit-scrollbar {
        width: 12px;
        background-color: #f1f1f1
    }
    #roomFormUpdate::-webkit-scrollbar-thumb {
        background-color: #bcbcbc;
        border: 1px solid #a8a8a8;
        background-clip: content-box
    }
    chat .pusher-chat-widget-input p {
        padding: 4px 0!important
    }
    #chat .pusher-chat-widget-input p {
        text-align: center;
        font-weight: 700;
        color: #AB0000;
        cursor: default;
        -ms-user-select: none;
        -moz-user-select: none;
        -webkit-user-select: none;
        user-select: none;
        text-transform: uppercase
    }
    #chat .pusher-chat-widget-input p::after {
        content: " (You can't send messages)";
        text-transform: none
    }
    #addToPlaylistFloatContainer .create-playlist-header .icon-close {
        position: absolute;
        top: 15px;
        right: 15px
    }
    #addToPlaylistFloatContainer .create-playlist-input {
        background: #666A6B;
        border-bottom: 2px solid #878c8e
    }
    #addToPlaylistFloatContainer .create-playlist-input .playlist-input {
        width: calc(100% - 2.75rem);
        border-right: 1px solid #878C8E;
        border-bottom: none
    }
    #addToPlaylistFloatContainer .create-playlist-input .icon-add {
        top: 4px
    }
    #addToPlaylistFloatContainer .playlist-list-action {
        padding-bottom: .5rem;
        margin-bottom: .75rem;
        max-height: 352px
    }
    #addToPlaylistFloatContainer .playlist-list-action li:last-of-type {
        border-bottom: 1px solid #878c8e
    }
    #chat .chat-container .chat-messages .chat-main li .stream-item-content .image_row {
        border-radius: 10%
    }
    #chat .chat-container .chat-messages .chat-main li .stream-item-content .image_row>img {
        max-width: none;
        height: 100%;
        display: block;
        margin: auto;
        border-radius: 10%
    }
    img.emoji.twitch-emoji[alt=fishmoley] {
        min-width: 2.5rem
    }
    img.emoji.twitch-emoji[alt=yetiz] {
        min-width: 3rem
    }
    img.emoji.twitch-emoji[alt=lenny] {
        min-width: 3.5rem
    }
    img.emoji.twitch-emoji[alt=firespeed] {
        min-width: 4rem
    }
    img.emoji.twitch-emoji[alt=shrug] {
        min-width: 5rem
    }
    img.emoji.twitch-emoji[alt=angelthump],
    img.emoji.twitch-emoji[alt=taxibro] {
        min-width: 4.5rem
    }
    #profileMainSection {
        top: 3.4rem;
        min-height: initial;
        height: calc(100vh - 6.9rem);
        overflow-y: auto
    }
    #profileMainSection .profileWrapper {
        margin-top: 3rem
    }
    #profileMainSection #profileSidebar #followersEl {
        margin-bottom: 3rem
    }
    #chat .chat-container .chat-messages .chat-main li.deleted-message .stream-item-content .image_row>img {
        opacity: .01
    }
    #chat .chat-container .chat-messages .chat-main li.deleted-message .stream-item-content .image_row::before {
        content: "X";
        font-family: cursive, Arial, sans-serif;
        position: absolute;
        top: 3px;
        left: 13px;
        font-weight: 900;
        color: red;
        font-size: 1.5rem
    }
    #room-main-player-container-soundcloud {
        background-color: #111;
        cursor: default
    }
    #main_player .player_container #room-main-player-container .playerElement.soundcloud img {
        pointer-events: none
    }
    .eta_tooltip,
    .snooze_tooltip {
        transform: translateX(-7px)
    }
    .eta_tooltip::before,
    .snooze_tooltip::before {
        content: "";
        position: absolute;
        border: 8px solid transparent;
        border-bottom: 15px solid #fff
    }
    #chat .chat-main .system,
    #chat .chat-main li[class^=chat-system-] {
        color: #0090cc!important
    }
    #chat .chat-container .chat-messages .chat-main li.chat-system-grabbed {
        color: #d026d0!important
    }
    #chat .chat-container .chat-messages .chat-main li.chat-system-grabbed,
    #chat .chat-container .chat-messages .chat-main li.chat-system-joined,
    #chat .chat-container .chat-messages .chat-main li.chat-system-left {
        text-align: center;
        font-style: normal
    }
    #chat .chat-container .chat-messages .chat-main li.user-560b4a33d9057a0300cc2fa0 .activity-row .text a.username {
        opacity: 0
    }
    #chat .chat-main li.user-560b4a33d9057a0300cc2fa0:not(.deleted-message) .activity-row .text p:first-of-type:before {
        content: "Å‹etux";
        position: absolute;
        text-shadow: 0 -1px 0!important;
        line-height: 1.2;
        color: #878c8e;
        padding-top: 3px
    }
    #chat .chat-main li.user-560b4a33d9057a0300cc2fa0:not(.deleted-message) .activity-row .text p.deleted:first-of-type:before {
        content: ""
    }
    #chat .chat-main li.user-560b4a33d9057a0300cc2fa0.current-chat-user .activity-row .text p:first-of-type:before {
        color: gold
    }
    #chat .chat-container .chat-messages .chat-main li .chatDelete {
        z-index: 2
    }
    #chat .chat-container .chat-messages .chat-main li.user-561d4abb00939732008b68bf .activity-row .text a.username {
        padding-left: 1.25em;
        position: relative
    }
    #chat .chat-container .chat-messages .chat-main li.user-561d4abb00939732008b68bf .activity-row .text a.username::before {
        content: "";
        background: url(https://raw.githubusercontent.com/sinfulBA/DubX-Script/master/emotes/misc/147.png) left center no-repeat;
        background-size: cover;
        width: 1em;
        height: 1.5em;
        position: absolute;
        left: 0;
        top: -.5em
    }
    .ps-container .ps-scrollbar-x,
    .ps-container .ps-scrollbar-x-rail:hover .ps-scrollbar-x,
    .ps-container .ps-scrollbar-y,
    .ps-container .ps-scrollbar-y-rail:hover .ps-scrollbar-y {
        background-color: #B1B1B1
    }
    #main-menu-left {
        top: 58px;
        height: calc(100% - 58px);
        padding-top: 3rem
    }
    #chat .chat-container .chat-messages .chat-main li:not([class^=user-]) {
        padding-bottom: .5rem;
        min-height: initial;
        color: #fff;
        font-weight: 700
    }
    #chat .chat-container .chat-messages .chat-main li .activity-row .text img {
        display: block;
        max-height: 35vh
    }
    #chat .chat-options .chat-option-buttons span {
        -webkit-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        user-select: none
    }
    @media screen and (min-width: 72em) {
        #browser .browser-content .browser-content-main .sidebar .title {
            text-align: center;
            margin-top: 3.5rem;
            padding: 1rem
        }
        #main-room .left_section #main_player .player_sharing .videoquality-el {
            top: -3px
        }
    }
    time.timeago {
        cursor: default;
        -ms-user-select: none;
        -moz-user-select: none;
        -webkit-user-select: none;
        user-select: none
    }
    #main_player .placeholder h5,
    #player-controller .left ul li.imgEl {
        cursor: pointer
    }
    #avatar-list p.dubs span {
        font-family: monospace;
        font-size: 1.2rem
    }
    img.emoji.twitch-emoji[alt=facepalm] {
        background-color: #fff
    }
    #chat .chat-container li.isDefault.current-chat-user a.username,
    #chat .chat-container li.isDj.current-chat-user a.username {
        color: gold!important
    }
    body.no-song-playing #player-controller .infoContainerInner:after {
        display: none
    }
    #main-loading {
        background: linear-gradient(to bottom, transparent 66px, #0ff 66px, #0ff 100%)!important
    }
    #player-controller .right ul li .dubdown.voted {
        background-color: #f0a
    }
    #chat .chat_tools span:hover {
        color: #000!important
    }
    #player-controller .right ul li .add-to-playlist-button,
    #player-controller .right ul li .display-browser,
    #player-controller .right ul li .dubdown,
    #player-controller .right ul li .dubup {
        padding-left: 0;
        padding-right: 0;
        min-width: 100%
    }
    .not-logged-in #player-controller .right::after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%
    }
    #main-room .left_section #main_player .play-song-link {
        border-radius: .5rem
    }
    #room-main-player-container-soundcloud .playerElement.soundcloud .redirect-link {
        font-size: 3.5rem!important;
        line-height: 1
    }
    #main_player .player_header span.display-queue::before {
        content: "Room "
    }
    #player-controller .left ul li.infoContainer .infoContainerInner span {
        text-overflow: ellipsis
    }
    body.no-song-playing #main_player .player_container #room-main-player-container:before {
        content: none
    }
    #user_popover[style*="left: 0"] {
        border-radius: 0 1rem 0 0
    }
    #private-messages .message-details ul.message-list li.message-item .message-content p {
        word-wrap: break-word
    }
    #chat .chat-container .chat-messages .chat-main li .activity-row .text .username-handle,
    #chat .chat-container .chat-messages .chat-main li .activity-row .text a.username,
    #chat .chat-container .chat-messages .chat-main li .activity-row .text p a,
    #main_avatar_room #main-user-list-room #avatar-list li .dubs::before,
    #main_avatar_room #main-user-list-room #avatar-list li .username,
    #main_avatar_room #main-user-list-room #avatar-list li .username::after {
        font-weight: 400!important;
        text-shadow: 0 -1px 0!important
    }
    #chat .chat-container .chat-messages .chat-main li:not(.deleted-message) .stream-item-content .image_row {
        background: url(https://www.dubtrack.fm/assets/images/media/pic_notfound.jpg) center no-repeat;
        background-size: 75%
    }
    #chat .chat-container .chat-messages .chat-main li .stream-item-content .image_row>img[src="/assets/images/media/chat_image_load_error.png"] {
        opacity: .01
    }
    @media screen and (max-width: 72em) {
        #browser .browser-content .browser-content-main .nano .loading {
            z-index: 1
        }
        #addToPlaylistFloatContainer {
            border-radius: 0;
            padding: .5rem 0;
            background: rgba(84, 86, 85, .9)
        }
        #profileMainSection {
            top: 92px;
            max-height: calc(100vh - 203px)
        }
        .diplay-chat #main_player>:not(.play-song-link-container) {
            display: none
        }
    }
    @media screen and (max-height: 30em) {
        #main-room .left_section {
            margin-top: 4rem
        }
    }
    #main_player .player_header #topbanner {
        position: fixed;
        top: 0;
        left: 50%;
        transform: translateX(-50%);
        width: auto;
        max-width: 50vw;
        height: 3.6rem
    }
    #main-room .main-room-wrapper {
        min-height: initial
    }
    #main-room .left_section {
        padding: 0 22.18% 0 0!important;
        margin: 0!important
    }
    #main-room .left_section .main-player-wrapper {
        width: 100%;
        height: 100%;
        margin: auto;
        background-color: rgba(0, 0, 0, .7)
    }
    #main_player,
    #main_player #mods-controllers,
    #main_player #room-info-display,
    #main_player .player_container {
        padding: 0;
        height: inherit
    }
    #main_player {
        border-radius: 0;
        background-color: rgba(0, 0, 0, 0)
    }
    #main_player #mods-controllers,
    #main_player #room-info-display .room-info-display-wrapper {
        padding: 0 1rem
    }
    #main_player #mods-controllers,
    #mods-controllers .mods-controllers-main-wrapper {
        position: relative
    }
    #main_player #mods-controllers {
        padding-right: 0
    }
    #main_player .player_header span {
        width: 100%;
        height: 100%;
        line-height: 1;
        border-radius: 0;
        margin: 0;
        padding: 1rem 0;
        color: #878c8e;
        opacity: 1;
        background-color: rgba(0, 0, 0, 0)!important;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis
    }
    #main_player .player_header span.active,
    #main_player .player_header span:hover {
        color: #fff
    }
    #main_player .player_header span.edit-room i {
        position: initial;
        line-height: .4
    }
    #main_player .player_header span.skip-el,
    #main_player .player_header span.skip-el:hover {
        color: red;
        width: 25%
    }
    #main_player .player_sharing {
        position: absolute;
        margin: 0
    }
    @media screen and (min-width: 72em) {
        #main-room .right_section {
            width: 22.12%!important
