@-moz-document url-prefix("https://toontownalley.com/"), domain("toontownalley.com") {
html {
    background: rgb(29, 29, 29);
}

.blockhead {
    font: normal 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    color: rgb(181, 181, 181);
    background: rgb(25, 25, 25);
