css:
@-moz-document domain(pinkbike.com){
    html {
        background-color: white !important;
    }
    body {
        font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
    }

    .page_news {
        overflow-x: hidden;
    }
    /* adds */

    #header,
    #outer-container > div:nth-child(2) {
        display: none !important;
    }
    /* login container */

    #login-container {
        padding-top: 10px;
        height: 40px;
        position: fixed;
        left: 0;
        top: 0;
        z-index: 200;
    }
    /* nav container */

    #nav-container {
        width: 100%;
        background: white !important;
        border-top: 0;
        border-bottom: 2px solid black;
        padding-top: 0;
        height: 40px;
        position: fixed;
        left: 0;
        top: 50px;
        z-index: 200;
    }
    /* nav */

    #nav {
        height: 40px;
    }
    #nav li {
        height: 100%;
        display: inline-block;
        margin: 0;
    }
    /* nav link */

    #nav a {
        display: block;
        height: 100%;
        color: black;
        padding: 0px 13px !important;
        line-height: 40px;
        font-weight: 400;
    }
    /* nav link active */

    #nav a:hover,
    .navon {
        color: white !important;
        border-radius: 0;
        background: black;
    }
    /* top bar nav */

    #login li a span {
        border-radius: 0;
    }
    /* outer container */

    #outer-container {
        background-image: none !important;
        background-color: white !important;
        margin-top: 120px;
        position: relative;
    }
    #content-container {
        margin-top: 150px !important;
    }
    #edit-bar {
        display: none !important;   
    }
    /* title bar */

    .hboxr-c3-h,
    .hboxb-c3-h {
        border-radius: 0;
        border: 0 !important;
        padding: 0;
        background: transparent !important;
        color: black;
        font-size: 16px;
        font-weight: 400;
    }
    /* box border */

    .hbox-c3-m {
        background: #F2F2F2;
        border-radius: 0;
        border: 0;
    }
    input {
      border-radius: 0;
        border: 0;  
    }
    #inner-m .hbox-c3-m {
        background: transparent;
    }
    .hbox-c3,
    .hbox2-c3,
    .hbox-c3-forum {
        padding-bottom: 30px;
    }
    /* left bar */

    #col3-lmr-news-l {
        position: absolute;
        left: 0;
        top: 0;
        margin: 0;
    }
    /* right bar */

    #col3-lmr-news-r {
        position: absolute;
        right: 0;
        top: 0;
    }
    .hboxb-c3-h a,
    .hboxb-c3-h a:visited,
    .hboxb-c3-h a:hover {
        color: black;
    }
    .hboxb-c3-h.h3 {
        padding: 0 0 10px 0;
        border-bottom: 2px solid black !important;
    }
    .hboxb-c3-h h1 {
        font-weight: 400;
    }
    #sm1-hover {
        position: relative;
    }
    #sm1-hover img {
        display: none;
    }
    #sm1-hover::after {
        content: '';
        display: block;
        position: absolute;
        top: 10px;
        right: -20px;
        width: 0;
        height: 0;
        border: 5px solid transparent;
        border-top: 5px solid black;
    }
    #middlebar .menu li {
        z-index: 100;
    }
    #middlebar ul .submenu {
        left: 0;
    }
    #middlebar .submenu li a {
        border: 0;
        border: 1px solid #B8B8B8;
        border-bottom: 0;
        background: white !important;
    }
    #middlebar .submenu li a:hover {
        text-decoration: none;
        background: #B8B8B8 !important;
    }
    #news-container {
        position: relative;
    }
    #news-container > div:nth-child(1) {
        position: absolute;
        right: -5px;
        top: -38px;
        border: 0 !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    #news-container > div:nth-child(1) form input,
    #news-container > div:nth-child(1) form button {
        border-radius: 0;
        border: 0;
        outline: none !important;
    }
    #news-container > div:nth-child(1) form button {
        background: #F2F2F2;
    }
    .news-mb3 {
        position: absolute;
        bottom: 0;
        left: 0;
    }
    .news-box2 {
        position: relative;
        margin-left: 290px !important;
    }
    .news-box2 > a {
        display: block;
        padding-top: 20px;
        font-weight: 400;
    }
    .news-box2 > a > img {
        margin-top: 0 !important;
        margin-left: -290px !important;
    }
    .news-style2 {
        border-radius: 0;
    }
    /* tags */
    .fgrey2 .highlight,
    .fblack {
        display: inline-block;
        padding: 2px 4px;
        background-color: yellow;
        color: black;
    }
    .f10.bold.news-style2,
    .fblack {
        background-color: #F2F2F2;
    }
    .news-style3 {
        border: 0;
    }
    .news-box {
        border: 0 !important;
    }
    .news-comments {
        padding: 0 !important;
        width: 784px !important;
    }
    .nav-sub {
        background-color: white !important;
        margin-top: 2px !important;
        border-radius: 0 !important;
    }
    #nav .has-sub:hover > a {
        margin: 0 !important;  
        background: black !important;
        color: white !important;
    }
    .news-mb3 > img {
        padding-top: 2px;
    }
    .pb-button {
        border-radius: 0;
        border: 0;
        background: #CCC !important;
    }
    .pb-button.grey {
        background: #999 !important;
    }
    .user-action {
        border-radius: 0 !important;
    }
    #dashboard-filter > a {
        border-radius: 0;
    }
    @media (min-width: 1025px) {
        #nav-container,
        #login-container {
            z-index: 9999;
        }

        #col3-lmr-news-l,
        #col3-lmr-news-r {  
            transition: all .2s;
            -webkit-transition: all .2s;
            -moz-transition: all .2s;
            -o-transition: all .2s;
            background-color: white;
            z-index: 8888;
        }

        #col3-lmr-news-l {
            left: -192px;
            border-right: 12px solid #D70303 !important;
        }

        #col3-lmr-news-l:hover {
            left: 0; 
        }

        #col3-lmr-news-r {
            right: -307px;
            border-left: 12px solid #D70303 !important;
        }

        #col3-lmr-news-r:hover {
            right: 0;   
        }

        #col3-lmr-news-m {
            width: 1000px !important;
            margin: 0 auto !important;                             
        }
        #login {
            width: 980px;
        }
    }
}
