css:
@namespace url(http://www.w3.org/1999/xhtml);
/* hides menu box on map */
@-moz-document domain("openstreetmap.org") ,domain("maps.sputnik.ru")
{
div#content div#sidebar:before,
div.main.ng-scope:before{
position: fixed !important;
visibility: visible !important;
top: 10px  !important;
left: 10px !important;
right: auto !important; bottom: auto !important;
z-index: 247247 !important;
background-color: rgba(240, 240, 240, .7) !important;
opacity: .9 !important;
width:  64px !important; 
height: 44px !important;
text-align: center !important;
border: thin solid grey !important;
border-radius: 16px 0px 16px 0px !important;
content: url(data:image/gif;base64,
) !important;
}
div#content div#sidebar,
div.main.ng-scope{
position: fixed !important;
top: 10px  !important;
left: 10px !important;
/*right: auto !important; bottom: auto !important;
z-index: 247 !important;
border: 1px solid grey  !important;
border-radius: 16px 0px 16px 0px !important;
*/
opacity: .9 !important;
visibility: hidden !important;
margin: 0 !important;
padding: 0 !important;
transition-delay:  0.75s !important;
-webkit-transition-delay:  0.75s !important;
}
div#content div#sidebar,
div#content div#sidebar:before{
top: 55px  !important;
left: 0 !important;
z-index: 20160629 !important;
}
div#content div#sidebar:hover,
div.main.ng-scope:hover{
visibility: visible !important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}
div#content div#sidebar:hover:before,
div.main.ng-scope:hover:before { 
visibility: hidden !important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}
.leaflet-top.leaflet-right,.leaflet-control-zoom,.leaflet-control-sm-geolocation,
span.map-copyright,a.map-copyright{
 opacity: .33 !important;
  z-index:9999!important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}
.leaflet-top.leaflet-right:hover,.leaflet-control-zoom:hover,.leaflet-control-sm-geolocation:hover,
span.map-copyright:hover,a.map-copyright:hover{
  opacity:1 !important; 
transition-delay:  0.0s !important;
-webkit-transition-delay:  0.0s !important;
}

}

@-moz-document domain("openstreetmap.org") {

body.map-layout header{
background: rgba(240,240,240,.7) !important;
opacity: .4 !important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}

body.map-layout header:hover
{
opacity: 1 !important;
transition-delay:  0.2s !important;
-webkit-transition-delay:  0.2s !important;
}
.map-layout #content { top: 0 !important;}
div#map-ui,
.leaflet-top {
    top: 50px !important;
    height: 92% !important;
}
nav.secondary li:hover {background: #D5D5D5 !important;}
}

@-moz-document url-prefix("https://yandex.ru/maps/") {

.header-view__main-layout
,.header-view__controls-layout
,.map-controls-view
{
 opacity: .3 !important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}
.map-controls-view:hover
,.header-view__main-layout:hover
,.header-view__controls-layout:hover
,.sidebar-panel-view:hover
{
 opacity: 1 !important;
transition-delay:  0.2s !important;
-webkit-transition-delay:  0.2s !important;
}

.sidebar-panel-view
{
 opacity: .2 !important;
transition-delay:  0.3s !important;
-webkit-transition-delay:  0.3s !important;
}

}
