css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spwiki.net") {
	/* AD */
	iframe[id*="google_ads"],ins.adsbygoogle{display: none !important;}
	div#body iframe[name="spgoods"]{display: none !important;}
	td.rightbody{display: none !important;}

/* デザイン */
	html{background: #f5f5f5 !important;}
	body{background: white !important;
			width: 90% !important;
		margin: 0.5em auto !important;
		box-sizing: border-box !important;
		    box-shadow: 0 1px 2px 1px #eee;
	}
	iframe[src="http://spwiki.net/foot.html"]{display: none !important;}

	#waku,.editmenu,#footer{
		background: white !important;
border: none !important;
		padding: 0.5em !important;
	}
	
	.menu2_th,.menu2_td{border: none !important;
}
	
	
	img[src*="title.gif"]{display: none !important;}
	div#topright{height: auto !important;}
	div#topright h1{
		font-weight: bold !important;
		font-size: 2em !important;
	text-align: center !important;
	}
	div#topright h1 a{color: #0080c9 !important;}

div.menu_th{border: none !important;
background: none !important;
color: #211917 !important;}

div.menu2_th{background-color: #33476a !important;}
h2.title{border-color:#0080c9 !important;
background-image: none !important;
	background-color: #44aeea !important;
color: white !important;
text-shadow: none !important;}

h3.title{border: none !important;
text-shadow: none !important;
background-image: none !important;
}
	td.menu_th{border: none !important;
	background: white !important;}
	/* red */
span.Bred,span[style="color:red"]{color: #c50030 !important;}
		/* green */
	span.new5,span[style="color:green"]{color:#6c7822 !important;}
		/*orange */
	span[style="color:OrangeRed"]{color: #d16b16 !important;}
	
	ul.snsb{display: none !important;}
	div#footer{display: none !important;}
	
	/* トップページ */
	#topright{float: none !important;
	margin: 0 auto !important;
	padding: 0.5em !important;}

}
