css:
@-moz-document domain("viaplay.dk") {
::-webkit-scrollbar {
    height: 10px;
    width: 10px
}

::-webkit-scrollbar-track {
    background: transparent
}

::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, .2);
    border-radius: 5px
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, .3)
}

::-webkit-scrollbar-thumb:active {
    background: rgba(255, 255, 255, .4)
}

body {
    background: #202020;
}

.block.collection {
    background: #202020;
}

.block.collection.carousel .page {
    background: transparent;
}

.block.collection.carousel .page:hover:after {
    background-image: url(http://assets.viaplay.tv/frontend-2015113001/images/carousel_arrow.svg);
}

.block.collection .collection-header h2 {
    color: #F95151;
}

.block.collection .collection-header .see-all:hover {
    background: #2C2C2C;
}

.line-clamp {
    color: #848484;
}

.block.collection .collection-header .see-all,
.block.collection .see-all {
    border: none;
    color: #848484;
}

.block.collection.carousel .page:hover {
    background-color: #2C2C2C;
}

.collection-item .labels .line-clamp:after,
.block.collection .item .labels .line-clamp:after {
    background: linear-gradient(to right, rgba(255, 255, 255, 0), #202020 50%);
}
/*Hover Popup*/

#tooltip .tooltip {
    color: #848484;
    background: #202020;
    box-shadow: 0 0 8px 0 rgba(0, 0, 0, .5);
}

#tooltip .tooltip header h1 a {
    color: #F95151;
}

#tooltip .tooltip header .genres a,
#tooltip .tooltip header p,
#tooltip .tooltip footer:last-child a {
    color: #848484;
}

#tooltip .tooltip footer:last-child {
    background: #202020 content-box;
    border-top: solid 1px #525252;
}

#tooltip .tooltip .body .synopsis>a {
    background: -webkit-gradient(linear, left top, right top, from(rgba(255, 255, 255, 0)), to(#202020), color-stop(50%, #525252));
    background: -moz-linear-gradient(to right, rgba(255, 255, 255, 0), #202020 50%, #525252);
    background: -o-linear-gradient(to right, rgba(255, 255, 255, 0), #202020 50%, #525252);
    background: -ms-linear-gradient(to right, rgba(255, 255, 255, 0), #202020 50%, #525252);
    background: linear-gradient(to right, rgba(255, 255, 255, 0), #202020 50%, #525252);
}
/*Header Menu*/

.section-menu .dock {
    background: #2c2c2c;
}

.section-menu .dock .section-wrapper {
    border-bottom: 1px solid #525252;
}

.section-menu .details .summary button {
    border-left: solid 1px #525252;
    border-right: solid 1px #525252;
    border-bottom: 0;
    border-top: 0;
    color: #F95151;
    background: #2C2C2C;
}

.section-menu .details .dropdown .box-shadow {
    border-top: solid 1px #525252;
    background: #2C2C2C;
}

.section-menu .dock .inner .categories.active-hover button,
.section-menu .dock .inner .sortby.active-hover button,
.section-menu .dock .inner .filters.active-hover button {
    border-bottom: 0;
}

.section-menu .categories .dropdown strong {
    color: #F95151;
}

.section-menu .categories .dropdown a {
    color: #848484;
}

.section-menu .categories .footer a {
    background: #2C2C2C;
}

.section-menu .categories .footer {
    border-top: 1px solid #525252;
}

.page-header .sections a.active {
    background: #2C2C2C;
    color: #F95151;
}

.page-header .sections a:hover:not(.active) {
    background: #202020;
}
/*User Dropdown*/

.page-header .user .dropdown .box-shadow {
    background: #212D33;
}

.page-header .user .dropdown .footer {
    background: #212D33;
}

.page-header .user .dropdown .box-shadow>*+* {
    border-top: solid 1px #525252;
}

.page-header .user .dropdown a {
    color: #848484;
}

.page-header .user .details.active-hover .summary button {
    background: #212D33;
    color: #F95151;
}

.page-header .user .authenticated {
    border-left: 1px solid #525252;
    border-right: 1px solid #525252;
}
/*Slidshow*/

.featurebox {
    background: #202020;
}

.featurebox .prev-btn,
.featurebox .next-btn {
    background: rgba(0, 0, 0, 0.15) no-repeat center center;
}

.featurebox .prev-btn:hover,
.featurebox .next-btn:hover {
    background-color: rgba(0, 0, 0, 0.5);
}

.featurebox .paginator .positions li.active a,
.featurebox .paginator .positions li a:hover {
    background-color: #525252;
}

.featurebox .paginator .positions li a {
    border: solid 2px #525252;
}
/*Info Site*/

.product {
    background: #202020;
}

.product .metadata h1 {
    color: #F95151;
}

.product .metadata .summary span,
.product .metadata .summary a {
    color: #848484;
}

.product .metadata .summary .divider:after {
    background: #525252;
}

.product .metadata h2 {
    color: #F95151;
}

.product .metadata p {
    color: #848484;
}

.product .metadata .icon {
    border: solid 1px #525252;
    background: #202020;
}

.season .episode:hover {
    background: #2C2C2C;
}

.season .episode {
    border-bottom: solid 1px #525252;
}

.season .episode .meta .title {
    color: #F95151;
}

.season .episode .synopsis span,
.season .episode .meta .episode-length,
.product .metadata .synopsis {
    color: #848484;
}

.season-navigation {
    background: #202020;
}

.season {
    background: #202020;
}

.season-navigation .label {
    color: #F95151;
}

.season-navigation .tabs a {
    color: #525252;
}
/*Sport Site*/

.sport-schedule {
    background: #202020;
}

.sport-schedule nav h1 {
    color: #F95151;
}

.sport-schedule .viewport .days h2 {
    background: #2C2C2C;
    color: #F95151;
}

.sport-schedule .timestamps li {
    background: #1D1D1D;
}

.sport-schedule .timestamps li:nth-child(even) {
    background: #2C2C2C;
}

.sport-schedule .timestamps li .showtime {
    color: #848484;
}

.sport-schedule .timestamps li h3.line-clamp {
    color: #565656;
}

.sport-schedule .viewport .days .column {
    background: #202020;
}

.sport-schedule .timestamps li:last-child:after {
    background: none;
}

.sport-schedule .timestamps li h3.line-clamp:after {
    background: linear-gradient(to right, rgba(255, 255, 255, 0), #1D1D1D 50%);
}

.sport-schedule .timestamps li .event-container+.event-container .event {
    border-top: solid 1px #525252;
}

.sport-schedule nav a.next:before,
.sport-schedule nav a.prev:before {
    background-color: transparent;
    box-shadow: none;
}

.sport-schedule nav a.next:hover:before,
.sport-schedule nav a.prev:hover:before {
    background: #2C2C2C;
}
/*Kids Site*/

.block.children,
.block.children.carousel,
.block.collection.children-watching,
.block.collection.children-landscape,
.block.collection.children-portrait,
.season-navigation.children,
.season.children {
    background: #202020;
}

.block.children.product {
    background: #202020;
}
/*Search*/

.page-header .search .result .list ul {
    background: #212D33;
}

.page-header .search .result .list h4 {
    color: #F95151;
}

.page-header .search .result .list p {
    color: #848484;
}

.page-header .search .result .list li+li {
    border-top: solid 1px #525252;
}

.page-header .search .result .list a:hover {
    background: #2C2C2C;
}

.page-header .search .result .show-all {
    background: #212D33;
    border-top: solid 1px #525252;
}

.page-header .search .result .show-all p {
    color: #848484;
}

.page-header .search.focus .searchfield,
.page-header .search.focus input[type=search],
.page-header .search.expand .searchfield,
.page-header .search.expand input[type=search] {
    background: #202020;
}

.page-header .search .search-abort {
    background-color: #202020;
}

.page-header .search .searchfield,
.page-header .search input[type=search] {
    background: #202020;
}

.page-header .search .result .show-all .loading {
    background-color: #212D33;
}

.hero-vignette {
    -moz-box-shadow: inset 350px 0 390px 0 #202020, inset -200px 0 170px 0 #202020, inset 0 -100px 130px 0 #202020;
    -webkit-box-shadow: inset 350px 0 390px 0 #202020, inset -200px 0 170px 0 #202020, inset 0 -100px 130px 0 #202020;
    box-shadow: inset 350px 0 390px 0 #202020, inset -200px 0 170px 0 #202020, inset 0 -100px 130px 0 #202020;
}
}
