css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("btm.asie.pl") {
  body{
    background: #fdf6e3;
    font-family: sans-serif;
  }
  li{
    list-style-image: none;
  }
  .imgi{
    display: none;
  }
	h1 {
		padding-bottom: 0em;
		margin-bottom: 0em;
	}
	h3 {
		padding: 0em;
		margin: 0em;
		font-weight: normal;
	}
	.container {
		max-width: 960px;
		padding-left: 5%;
		padding-right: 5%;
		margin: 0 auto;
	}
	.cordel {
		color: #aa2222;
	}
	.coradd {
		color: #FF0000;
	}

	h1, h2, h3 {
		text-align: center;
		transform: rotate(0deg);
		animation: none !important;
	}

	.blazeit {
		width: 100%;
		text-align: center;
	}


	.blazeit li {
		margin-left: auto;
		margin-right: auto;
		transform-origin: 50% 50%;
		animation: none !important;

	}

	.blazeit li:hover {
		animation-play-state: paused;
	}

	.srs {
		display: block;
		width: 800px;
		animation: none !important;
	}
}
