css:
@-moz-document domain("synergia.librus.pl") {
#body {
    background-color: #1c1c1c;
    color:#FFFFFF;
}
#page.altum #header #top-banner-container {
    background:transparent;
    background-color:#1c1c1c;
    color:#FFFFFF;
}
#page.altum #header {
    background: -webkit-linear-gradient(left, #f1f1f4, #bbcbd3);
    background: -o-linear-gradient(right, #f1f1f4, #bbcbd3);
    background: -moz-linear-gradient(right, #f1f1f4, #bbcbd3);
    background: linear-gradient(to right, #1C1C1C, #1C1C1C);
}
.container-background {
    border: 1px #515151 solid;
    background: #191919;
    box-shadow: 1px 1px 1px #B2B2B3;
}
#page {
    background: #1C1C1C;
}
html {
background:#1c1c1c;

}
#tab-bezpieczny a {
    color: #667171;
}
#page #header #top-banner-container #graphic-menu ul li a .circle {
    border-radius: 100%;
    width: 47px;
    height: 47px;
    background-color: #000000;
    background-repeat: no-repeat;
    -webkit-box-shadow: -3px 3px 3px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: -3px 3px 3px rgba(0, 0, 0, 0.2);
    box-shadow: 0px 0px 5px rgb(255, 255, 255);
    -webkit-opacity: 0.65;
    -moz-opacity: 0.65;
    /* opacity: 0.65; */
    position: relative;
    border: white 100px;
    display: block;
    margin: 0 auto;
    top: -7px;
    font-size: 65px;
}
#page #header #top-banner-container #graphic-menu ul li a {
    text-align: center;
    line-height: 15px;
    color: #FFFFFF;
    background: none;
    max-width: 95px;
    width: 65px;
    padding-top: 7px;
    font-size: 11px;
}
#user-section{
color:#FFFFFF;
}
table.decorated tbody td {
    color: #7E7E82;
}
table.decorated tbody tr.line1 {
    background-color: #232323;
}
table.decorated tbody tr.line0 {
    background-color: #313131;
}
select {
    
    background-color: #232323;
   border:#1c1c1c;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: #232323;
   border:#1c1c1c;
}
table.decorated tfoot tr:last-child td:last-child {
    border-bottom-right-radius: 5px;
    border-color: #fff;
    background-color: #313131;
}
.container h2.inside {
    
    background-color: #313131;
}
table.decorated thead td {
    background-color: #313131;
    color: #fff;
}
table.decorated thead td a {
    color: #FFFFFF;
}
table.message-folders ,td.tree-first-branch:FIRST-CHILD ,td.tree-next-branch:FIRST-CHILD ,td.tree-last-branch:FIRST-CHILD{
     background:transparent !important;
}
table.decorated tbody tr.line0:hover {
    background-color: #000000;
    color: #FFFFFF;
}
table.decorated tbody tr.line1:hover {
    background-color: #000000;
    color: #FFFFFF;
}
select.black {
    color: #717171;
    background-color: #232323;
}
table.kalendarz tbody tr.line1 td.weekend , table.kalendarz tbody tr.line0 td.weekend{
    background-color: #00072B;
}
table.kalendarz tbody tr.line0:hover td.weekend , table.kalendarz tbody tr.line1:hover td.weekend  {
    background-color: #010117;
    color: #FFFFFF;
}
table.kalendarz thead td.weekend {
    background-color: #00072B;
    border-left: 3px #ffffff solid;
}
table.decorated.form tbody tr.line1:hover ,table.decorated.form tbody tr.line0:hover {
    background-color: #000000;
    color: #FFFFFF;
}

table.decorated thead tr:first-child td:last-child {
    border-top-right-radius: 5px;
    background: #232323;
}
table.decorated.form tbody tr.line1 th {
    background-color: #232323;
    color: #5a5b5c;
}
table.decorated.form tbody tr.line1:hover th {
    background-color: #000000;
    color: #FFFFFF;
}
table.decorated.form tbody tr.line0 th {
    background-color:#313131 ;
    color: #5a5b5c;
}
table.decorated.form tbody tr.line0:hover th {
    background-color: #000000;
    color: #FFFFFF;
}
table.decorated thead td.colspan span {
    background-color: #585858;
}
table.decorated thead th {
    background-color: #313131;
    color: #313332;
}
.container-icon table tr td:first-child {
    display: none;
}
table.decorated tbody tr.line0 th {
    background-color: #313131;
    color: #5a5b5c;
}
table.decorated tbody tr.line0:hover th {
    background-color: #000000;
    color: #FFFFFF;
}
table.decorated tbody tr.line1 th {
    background-color: #232323;
}
table.decorated tbody tr.line1:hover th {
    background-color: #000000;
    color: #FFFFFF;
}
table.decorated tbody tr.bolded td {
    color: #D9D9D9;
    background: #313131;
}
.legend ul li span {
    background-color: #313131;
}
.box {
    background: #3E3E3E !important;
    color: #FFF;
    border: 1px solid #808080;
}
table.decorated.filters tbody th,table.decorated.filters tbody td {
    background-color: #313131;
    color: #A2A2A2;
}
table.decorated tbody tr td.bolded {
    color: #FFFFFF;
    background: #777777;
}
textarea, input[type=text], input[type=password]{
    background: #424242;
    color: #818181;
    
}
table.message-recipients-detail tbody tr.line0 ,table.message-recipients-detail tbody tr.line1{
    background-color: #212121;
}
table.message-recipients-detail thead td , table.message-recipients-detail tfoot td:first-child{
    background-color: #191919;
 
}
table.message-recipients-detail tbody tr.line0:hover, table.message-recipients-detail tbody tr.line1:hover {
    background-color: #000000;
    color: #FFFFFF;
}
.warning-content>p {
    color: #A5A5A5;
}
.warning-box {
    background-color: #313131;
    color: #FFFFFF;
}
}
