css:
/* i really want this to be global */
/* El color de fuente de los tabs debe ser blanco por un tema de contraste */
.kdtabhandle.kddraggable .kdview.tab-handle-text {
    color: #EFE8E8 !important;
}
body.ide .dark .kdtabhandle.active .kdview.tab-handle-text{
    font-weight: bold !important;
}
/* El fondo de los tabs activos debe ser mas llamativo */
body.ide .dark .kdtabhandle.active {
	background-color: #3a3737;
}

/* Tabs apilables */
.kdtabhandle.kddraggable{
    
    min-width:15% !important;
    float:left;
}

.panel-1 .kdview.application-tab-handle-holder, body.ide .dark .idetabhandle-holder .kdtabhandle-tabs {
	height: 42px !important;
}
.panel-1 .kdview.application-tab-handle-holder:hover, body.ide .dark .idetabhandle-holder .kdtabhandle-tabs:hover {
	min-height: 42px !important;
    height: auto !important;
}

.panel-1 .kdtabhandlecontainer.application-tab-handle-holder .kdtabhandle {
    height: 20px;
    line-height: 20px;
}

body.ide .dark .kdtabhandle .options, body.ide .application-page .kdtabhandlecontainer.application-tab-handle-holder .kdtabhandle .close-tab {
    top: 0px;
}

body.ide .dark .kdtabhandle.plus {
    height: 31px;
    margin: -2px;
}

.ace-find-replace-view {
position: absolute;
    top: 50%;
    width: 60%;
    left: 20%;
    opacity: .85;
}

body.ide .kdlistitemview-finderitem {
    min-height: 20px;
    margin-bottom: -4px;
}

body.ide .file-container .file, body.ide .file-container .mount, body.ide .file-container .folder, body.ide .file-container .broken, body.ide .file-container .section {
    font-size: 11px;
}


.kdtabhandlecontainer.application-tab-handle-holder .kdtabhandle {
    font-size: 12px;
}

.panel-1 .kdtabhandlecontainer.application-tab-handle-holder .kdtabhandle.modified:before {
    content: "";
    width: 100%;
    height: 20%;
    border-radius: 0;
    top: 80%;
    left: 0;
    background-color: #73b7d0;
}

.panel-1 .kdview.ws-tabview .kdtabhandlecontainer .kdtabhandle-tabs {
	height: 42px !important;
}

.kdtabhandlecontainer.application-tab-handle-holder {
    padding-top: 1px !important;
}
.ide .kdsplitview-vertical .panel-0 {
    border-right: 11px solid #131313;
}
body.ide .application-page {
    border-left: 11px solid #131313;
}
