@-moz-document domain("www.messletters.com") {

  
  #waves li div span:hover {
	background: #003F3F;
	transition: 0.3s;
}
   body, td, table {

	color: #2c0e25 !important;
	background: #000 !important;

}
  #content {
	display: table-cell;
	padding: 0 10px;
	vertical-align: top;
	background-color: black;
}
  .stripes #options span:hover, .stripes #options span.active {
	background: #00902B;
	transition: 0.3s;
}
	.stripes #options span, .stripes #options span.active {
	background: #004743;
	transition: 0.3s;
}
	#input .addmargin {
	margin: 10px;
	box-shadow: 0px 15px 20px #003F32, 0px 0px 0px #005652;
}
	
  .gradientGray {
	background-color: #f3f3f3;
	background: -moz-linear-gradient(top, #fefefe 0%, #ececec 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fefefe), color-stop(100%,#ececec));
	background: -webkit-linear-gradient(top, #fefefe 0%,#ececec 100%);
	background: -o-linear-gradient(top, #fefefe 0%,#ececec 100%);
	background: -ms-linear-gradient(top, #fefefe 0%,#ececec 100%);
	background: linear-gradient(to bottom, #000 0%,#001a19 100%);
	filter: none;
}
  .article {
	margin-bottom: 10px;
	padding: 5px;
	background: #000;
	border-radius: 4px;
	box-shadow: 2px 2px 4px 0px #BBB;
}
  .inputTextad {
	overflow: hidden;
	height: 0px;
	margin-top: 0px;
	padding: 0px 0px;
}
  .control {
	border: 1px solid #007A71;
	border-radius: 3px;
	background: #000;
}
  .menu ul li a {
	display: block;
	padding: 7px 7px 7px 10px;
	border-bottom: 1px solid #003F4D;
	background: #000;
	color: #00B399;
	font-size: 13px;
}
  .messletters #output {
	margin: 0;
	padding: 0;
	list-style: none;
	margin-top: 50;
	margin-top: 30px;
}
  #header {
	position: fixed;
	z-index: 9999;
	top: 0;
	left: 0;
	height: 46px;
	width: 100%;
	min-width: 150px;
	color: #076;
	box-shadow: 0px 2px 4px 0px #007A8F;
}
  .menu {
	overflow: hidden;
	margin-bottom: 10px;
	border-radius: 4px;
	box-shadow: none;
}
  .gradientRed {
	background-color: #002f2d;
	background: -moz-linear-gradient(top, #d73531 0%, #aa0804 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d73531), color-stop(100%,#aa0804));
	background: -webkit-linear-gradient(top, #d73531 0%,#aa0804 100%);
	background: -o-linear-gradient(top, #d73531 0%,#aa0804 100%);
	background: -ms-linear-gradient(top, #d73531 0%,#aa0804 100%);
	background: linear-gradient(to bottom, #005a4d 0%,#00383b 100%);
	
}
  
  .inputStyler {
	position: relative;
	border: 1px solid #00E0B8;
	border-radius: 3px;
}
  
  .menu h2 span {
	display: block;
	padding: 6px 7px 9px 35px;
	background:none;
	font-size: 13px;
	color: #00D5DD;
	text-shadow: 0 1px 0 #000;
}
  
  .messletters #options li {
	float: left;
	width: 40px;
	height: 21px;
	margin: 0px 0px 3px 3px;
	border-radius: 3px;
	background: #00727B;
	transition: 0.3s;
	font-family: 'lucida grande',tahoma,verdana;
	font-size: 11px;
	color: #333;
	text-align: center;
	line-height: 21px;
	cursor: default;
}
  .menu ul li a:hover, .menu ul li a.active {
	background: #000;
	color: #00D1EA;
	border-bottom: 5px solid #00FF27;
	transition: 0.3s;
}
  .messletters #options li {
	float: left;
	width: 40px;
	height: 21px;
	margin: 0px 0px 3px 3px;
	border-radius: 3px;
	background: #00727B;
	transition: 0.3s;
	font-family: 'lucida grande',tahoma,verdana;
	font-size: 11px;
	color: #333;
	text-align: center;
	line-height: 21px;
	cursor: default;
}
  .messletters #output li.gradientGray {
	position: relative;
	min-height: 18px;
	margin: 0 52px 25px 0;
	padding: 5px;
	border: 1px solid #005754;
	border-radius: 3px;
	line-height: 18px;
	box-shadow: none;
}
  #hLangButton{display: none;}
  .gradientBlue {
	background-color: #5f6975;
	background: -moz-linear-gradient(top, #5f6975 0%, #4b545f 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5f6975), color-stop(100%,#4b545f));
	background: -webkit-linear-gradient(top, #5f6975 0%,#4b545f 100%);
	background: -o-linear-gradient(top, #5f6975 0%,#4b545f 100%);
	background: -ms-linear-gradient(top, #5f6975 0%,#4b545f 100%);
	background: linear-gradient(to bottom, #003836 0%,#000 100%);

}
  
  i.empty {
	display: block;
	position: absolute;
	right: 3px;
	top: 3px;
	width: 19px;
	height: 19px;
	background: none;
	cursor: pointer;
}
  i.empty:hover {
	background-color: #00FFF6;
}
  
  /* MAIN (wrapper) */
#main{display:table;table-layout:fixed;width:100%;text-shadow:none;}

  body, td, table {
	color: #0ff !important;
	background: #000 !important;
}
  #hShare, .footer,.messletters > p:nth-child(4),.messletters > p:nth-child(6),.vote gradientGray {
	position: relative;
	top: 13px;
	left: 13px;
	float: left;
	height: 20px;
	display: none;
}
  
  .messletters .vote {
	position: absolute;
	top: -1px;
	right: -53px;
	width: 47px;
	height: 28px;
	border: 1px solid #E2E2E2;
	border-radius: 3px;
	box-shadow: 0 5px 5px -5px #000;
	display: none;
}
  
  
  
  
  
  #ERASE_EVERYTHING_BELOW_EXCEPT_LAST_CLOSE_BRACKET_CHARACTER_THING,
  #sidebarLeft, #sidebarRight,#ticker{opacity:0}
  
  #sidebarLeft:hover, #sidebarRight:hover,#ticker:hover,#hLogo:hover{opacity:1}
  
  li.ad{display: none;}
  
  
  
  
  
}

