css:
@-moz-document url-prefix("http://elearning.szu.edu.cn/webapps/") {
/* i really want this to be global */
* {
    box-shadow: none !important;
    outline: none !important;
}

body {
	background: #F6F6F6;
}

.topTabs {
	background: #547fab;
}

.brandingImgWrap {
	height: 70px;
}

.bannerImage {
    max-height: 65px;
}

.nav > li > a {
	color: #FFF;
}

#loggedInUserName {
    font-style: normal;
    color: #FFF;
}

.topGlobalLinks {
    background: none;
}

.tabWrapper-right {
	display: block;
}

.frameResizeToggle {
    display: none;
}

.tabWrapper-left, .tabWrapper-center, .tabWrapper-right {
    width: auto;
    padding: 0 28px;
    margin-top: -20px;
}

#topTabActive, .appTabs .active a, .appTabs .active a:hover, .appTabs .active a:focus {
    color: #F6F6F6;
    font-weight: bold;
    text-shadow: 0 0 6px rgba(0, 0, 0, .2);
    z-index: 1000;
}

.appTabs .active .hideoff, .appTabs .active a:hover .hideoff, .toppTabs-compact .appTabs .active .hideoff, .toppTabs-compact .appTabs .active a:hover .hideoff  {
    border-bottom: 14px solid rgba(255, 255, 255, .4);
}

.localViewToggle ul {
    background: #547fab;
}

.localViewToggle ul a {
    color: #FFF;
}

.localViewToggle a.active, div.localViewToggle h2 {
    font-weight: bold;
    margin: 0;
    color: #FFF;
    background: rgba(255, 255, 255, .2);
    border: none;
    border-top: 0;
}

.localViewToggle a.active:before, div.localViewToggle h2:before {
    content: "";
    position: absolute;
    bottom: 0;
    border-bottom: 8px solid rgba(255, 255, 255, .2);
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-top: 0;
    display: block;
    left: 50%;
    margin-left: -8px;
    z-index: 3;
}

#breadcrumbs {
    margin: 0;
    background: #4b7299;
    border: none;
    border-radius: 0;
}

.contentPaneWide, .portalPane {
    margin: 0 0;
    padding: 0;
}

.paneTabs {
    width: 100%;
    margin: -12px 0 12px;
    border: none;
    border-top: 0;
    border-radius: 0;
    background: #4b7299;
    zoom: 1;
}

.paneTabs .active a, .paneTabs .active a:hover, .paneTabs .active a:focus, .paneTabs .activepersonal a, .paneTabs .activepersonal a:hover, .paneTabs .active h3 {
    position: relative;
    font-weight: bold;
    color: #FFF;
    text-decoration: none;
    text-shadow: none;
}

.paneTabs a {
    display: block;
    padding: 12px 24px;
    text-decoration: none;
    border-left: 1px solid rgba(255, 255, 255, .2);
}

.paneTabs .active a:after, .paneTabs .active a:before {
    content: "";
    position: absolute;
    border-bottom: 8px solid rgba(255, 255, 255, .2);
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-top: 0;
    display: block;
    left: 50%;
    margin-left: -8px;
}

.actionBar, .actionBarMicro {
    background: #4b7299;
}

.mainButton > a {
    color: #E9E9E9;
    display: block;
    padding: 12px 18px 13px;
    text-decoration: none;
    text-shadow: none;
    font-size: 105%;
    background: #547fab;
    border-left: 1px solid rgba(255, 255, 255, .1);
}

.termToggleLink {
    color: #547fab;
}

a {
    color: #547fab;
    text-decoration: none;
}

.portal .contentBox {
    background: rgba(255,255,255,.7);
    -moz-box-shadow: 0 1px 4px rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 1px 4px rgba(0,0,0,0.15);
    box-shadow: 0 1px 4px rgba(0,0,0,0.15);
    width: 98%;
    margin-left: 1%;
}

.mainButton h2>a:hover, .secondaryButton h2>a:hover, li.mainButton:hover>a, .mainButton>a:hover, .secondaryButton>a:hover, li.mainButton:hover h2>a, .mainButton h2>a:focus, .secondaryButton h2>a:focus, li.mainButton:focus>a, .mainButton>a:focus, .secondaryButton>a:focus, li.mainButton:focus h2>a {
    background: #547fab;
    color: #FFF;
    text-shadow: none;
    border: none;
    border-color: transparent !important;
    border-left: 1px solid rgba(255, 255, 255, .1);
}

.mainButton>a, .mainButton h2>a {
    border-right: 1px solid rgba(255, 255, 255, .1);
    border-left: 1px solid transparent!important;
}

.actionBar {
    border-radius: 0;
    border-top: none;
    z-index: 110;
}

.vtbegenerated a {
	color: #4b7299;
}

.path .root {
    border: none;
}

.path .placeholder {
    color: #FFF;
}

.contentPaneWide {
	width: 98%;
    margin-left: 1%;
}

.helphelp {
    color: rgba(255, 255, 255, 0.5)!important;
}

.helpLink a.browseIcon {
    background: rgba(0, 0, 0, .3);
}

.paletteItems li.selectedItem, .paletteItems li.selectedItem:hover, .paletteItems li.active, .paletteItems li.active:hover {
    background: rgba(0, 0, 0, .05);
    border-radius: 2px 0 0 2px;
    color: #000;
}

.path .root a:before, .simplePath .root a:before {
    color: #FFF;
    top: 0;
}

.reorderableModule:hover {
    border-color: #4b7299;
}

.path .root a:hover:before, .simplePath .root a:hover:before {
    color: #F0F0F0;
}

.menuWrap {
	border: 1px solid #4b7299;
    border-left: none;
    border-radius: 4px;
}

.actionBarMicro {
    background: #547fab;
}

.secondaryButton>a, .secondaryButton h2>a {
    border-left: 1px solid #4b7299;
    margin: 0;
}

#puller {
    position: absolute;
    right: -8px;
    z-index: 3;
    opacity: 0;
    top: 0;
    bottom: 0;
    -webkit-transition: opacity 300ms cubic-bezier(0.420,0.000,1.000,1.000);
    -moz-transition: opacity 300ms cubic-bezier(0.420,0.000,1.000,1.000);
}

#puller a {
    height: 100%;
    width: 8px;
    background: #eee;
    -webkit-border-radius: 0 3px 3px 0;
    -moz-border-radius: 0 3px 3px 0;
    border: none;
    border-left: 0;
    cursor: w-resize;
}

.mainButton>a, .secondaryButton>a, .mainButton h2>a, .secondaryButton h2>a {
	color: #E9E9E9;
    display: block;
    padding: 12px 18px 13px;
    text-decoration: none;
    text-shadow: none;
    font-size: 105%;
    background: #4b7299;
    border-left: 1px solid rgba(255, 255, 255, .1);
}

.actionBarMicro .active>a, .actionBarMicro .active>a:hover, .actionBarMicro .active>a:focus {
    background: #4b7299;
    position: relative;
    border-color: #4b7299!important;
}

#courseMenuPalette {
    margin-bottom: 0;
    border-radius: 0 2px 2px 0;
    overflow: hidden;
}

#contentArea, #schemePreviewBody, .contentBox {
    border: 1px solid #4b7299;
    background: #FFF;
    background: rgba(255,255,255,1);
}

.pageTitle {
    background: #547fab;
    border-radius: 0;
}

#pageTitleText {
    color: #FFF !important;
    text-shadow: none;
}


#copyright {
    display: none;
}

.shadow:after {
    content: "Theme By Jason. Copyright © 2015 Jason Design.";
    height: 30px;
    width: 100%;
    font-size: 14px;
    color: #aaa;
    font-weight: normal;
    text-align: center;
    display: block;
}

.contentBox:after {
    content: "Theme By Jason. Copyright © 2015 Jason Design.";
    height: 30px;
    width: 100%;
    font-size: 14px;
    color: #aaa;
    font-weight: normal;
    text-align: center;
    display: block;
}

.contentPaneWide .paneTabs {
    border-radius: 0 0 3px 3px
}

#contentArea, #schemePreviewBody, .contentBox {
    border: 1px solid #4b7299;
    background: #FFF;
    background: rgba(255,255,255,1);
    margin-bottom: 10px;
}

.portlet {
    margin: 0 10px 20px;
    padding: 0;
    position: relative;
    -moz-box-shadow: 0 2px 4px rgba(0,0,0,.05);
    -webkit-box-shadow: 0 2px 4px rgba(0,0,0,.05);
    box-shadow: 0 2px 4px rgba(0,0,0,.05);
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    border: 1px solid #CCC;
    color: #444;
    background-color: #F6F6F6;
}

.path a {
    color: #FFF;
}

.button-6 {
    color: #4b7299;
}

.headerText {
    color: #4b7299;
}

#secondaryButton, .button-2, .button-2-img {
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    border-radius: 4px;
    background: #F0F0F0;
    border: 1px solid #BBB;
    color: #666;
    font-size: 105%;
    text-shadow: 0 1px 0 rgba(255,255,255,0.7);
}

#primaryButton, input[type="submit"].submit, .rumble .mainButton>a, .rumble_top .mainButton>a, .button-1, .button-1-img {
    background: #547fab;
    color: #FFF;
    font-size: 105%;
    font-weight: bold;
    text-shadow: none;
    border: 1px solid #4b7299;
}

#pageTitleText > span {
	color: #FFF !important;
}

input[type="submit"].submit:hover, .rumble .mainButton>a:hover, .rumble .mainButton>a:focus, .rumble_top .mainButton>a:hover, .rumble_top .mainButton>a:focus, .button-1:hover, .button-1:focus, .button-1-img:hover, .button-1-img:focus {
    background: #4b7299;
}

.steptitle, .submittitle {
    color: #4b7299;
}
}
