css:
@-moz-document url-prefix("https://auth.szu.edu.cn/") {
html {
    background: url(https://szdx.sinaapp.com/api/auth_theme_bg.php) no-repeat center center fixed; 
  	-webkit-background-size: cover;
  	-moz-background-size: cover;
  	-o-background-size: cover;
  	background-size: cover;
}

body {
    background: none;
}

fieldset {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 300px;
    min-height: 400px;
    max-height: 450px;
    margin-left: -170px;
    margin-top: -240px;
    padding: 15px 30px 20px 30px;
    opacity: .95;
    background-color: #FFFFFF;
    border: none;
    border-radius: 5px;
    box-shadow: 0 3px 5px rgba(0, 0, 0, .2);
    transition: all 200ms;
}

fieldset:hover, fieldset:active, fieldset:focus {
	opacity: 1;
}

fieldset:after {
    position: absolute;
    left: 0;
    width: 100%;
	content: "Themed By Jason.";
    margin-top: 20px;
    font-size: 12px;
    color: rgba(255, 255, 255, 1);
    text-align: center;
    text-shadow: 0 2px 5px rgba(0, 0, 0, .5);
    display: block;
}

fieldset b font {
	font-size: 20px !important;
    line-height: 30px;
}

fieldset td:nth-child(n) {
    width: 30% !important;
}

fieldset td:nth-child(2n) {
    width: 70% !important;
}

fieldset input {
	width: 95% !important;
    height: 32px !important;
    padding: 0 10px !important;
    margin: 2px 0;
    margin-right: 5%;
    border: 1px solid #aaa !important;
    background: #FFF !important;
    border-radius: 3px !important;
    font-size: 14px !important;
    line-height: 30px !important;
    outline: none !important;
    box-sizing: border-box;
    transition: all 200ms;
}

fieldset input:focus {
	border: 1px solid #547fab !important;
}

fieldset #btnSubmit {
    margin: 5px 0;
    float: left;
    height: 40px !important;
	width: 100% !important;
    color: #FFF !important;
    font-size: 16px !important;
    background: #60d06d !important;
    border: 1px solid #4db559 !important;
    cursor: pointer;
    outline: none;
}

fieldset #btnSubmit:hover, fieldset #btnSubmit:active, fieldset #btnSubmit:focus {
    background: #4db559 !important;
}

fieldset #btnReset {
    margin: 5px auto;
    height: 36px !important;
	width: 48% !important;
    background: #FFF;
}

fieldset #btnReset:hover, fieldset #btnReset:active, fieldset #btnReset:focus {
    border: 1px solid #ccc !important;
	background: #eee !important;
}

fieldset input:-webkit-autofill,
fieldset input:-webkit-autofill:hover,
fieldset input:-webkit-autofill:active,
fieldset input:-webkit-autofill:focus {
    -webkit-box-shadow: 0 0 0 1000px white inset !important;
    background-color: #FFF !important;
    color: #000 !important;
}
}
