css:
@-moz-document domain("animeonline.io"), domain("animeonline.to"), domain("anime/online.vip"), regexp("http://animeonline.*/*"), regexp("http://www.animeonline.*/*") {
* {
    color: #fff !important;
}
body {
    background-image: url("http://fc00.deviantart.net/fs70/i/2010/001/a/6/Tiling_Tech_Circles_by_TJ09.png") !important;
    height: 100% !important;
    background-attachment: fixed;
}
.anime_name {
    background-color: rgba(238, 238, 238, 0.18);
    border-radius: 4px 4px 0 0;
    padding: 11px 10px;
    background-image: url("http://animeonline.io/themes/default/img/bg/menu_top_blue.png") !important;
}
.anime_name.manga {
    background-color: rgba(238, 238, 238, 0.18);
}
#wrapper {
    float: left;
    width: 100%;
    padding: 0;
    background: #1A1A1A !important;
    color: #fff !important;
}
#wrapper_inside {
    width: 1000px !important;
}
.header {
    width: 1000px !important;
}
nav.menu_top ul li a .center {
    padding: 12px 8px !important;
}
section.content {
    margin: 15px 0 0px 0 !important;
}
section.content section.content_right {
    visibility: hidden !important;
    display: none !important;
}
section.content section.content_left {
    float: left;
    width: 1000px;
    margin-left: 0px;
}
.anime_video_body {
    width: 1000px !important;
}
.content_episode ul.items li {
    width: 185px;
}
.list_episode .row.even a {
    background-color: #232323;
}
nav.menu_series {
    display: none;
}
.wrapper_nen {
    height: 89.6vh;
}
.form .row input[type="text"] {
    color: #000 !important;
}
.anime_share_unti {
    float: none;
    width: 205px;
    position: absolute;
    right: -210px;
    top: 0;
    padding: 0;
}
.anime_share_unti_right {
    float: left !important;
}
.prev {
    position: fixed;
    top: 55vh;
    width: 200px;
    height: 50px;
    background: #c11;
}
.next {
    position: fixed;
    top: 55vh;
    margin-top: -100px;
    width: 200px;
    height: 50px;
    background: #c0c;
}
.anime_share_unti_right a.next span {
    content: '';
    display: inline-block;
    margin-top: 0;
    margin-left: 0;
    width: 54px;
    height: 54px;
    border-top: 7px solid #333;
    border-right: 7px solid #333;
    -moz-transform: rotate(45deg);
    -webkit-transform: rotate(45deg);
    transform: rotate(45deg);
    position: absolute;
    right: 0;
    top: -3.5px;
}
.anime_share_unti_right a.prev span {
    content: '';
    display: inline-block;
    margin-top: 0;
    margin-left: 0;
    width: 54px;
    height: 54px;
    border-top: 7px solid #333;
    border-right: 7px solid #333;
    -moz-transform: rotate(-135deg);
    -webkit-transform: rotate(-135deg);
    transform: rotate(-135deg);
    position: absolute;
    left: 0;
    top: -3.5px;
}
.anime_share_unti_right a.prev {
    text-align: center;
    overflow: hidden;
    /* corners */
    
    background: -webkit-linear-gradient(45deg, transparent 10px, #c00 10px), -webkit-linear-gradient(135deg, transparent 0px, #c00 0px), -webkit-linear-gradient(225deg, transparent 0px, #c00 0px), -webkit-linear-gradient(315deg, transparent 10px, #c00 10px);
    background-position: bottom left, bottom right, top right, top left;
    -moz-background-size: 50% 50%;
    -webkit-background-size: 50% 50%;
    background-size: 50% 50%;
    background-repeat: no-repeat;
    /* corners */
}
.anime_share_unti_right a.next {
    text-align: center;
    overflow: hidden;
    /* corners */
    
    background: -webkit-linear-gradient(45deg, transparent 0px, #c00 0px), -webkit-linear-gradient(135deg, transparent 10px, #c00 10px), -webkit-linear-gradient(225deg, transparent 10px, #c00 10px), -webkit-linear-gradient(315deg, transparent 0px, #c00 0px);
    background-position: bottom left, bottom right, top right, top left;
    -moz-background-size: 50% 50%;
    -webkit-background-size: 50% 50%;
    background-size: 50% 50%;
    background-repeat: no-repeat;
    /* corners */
}
.anime_share_unti_left a {
    position: relative;
    top: 1739px;
    right: 342px;
}
.list_search {
    background-color: #404040;
}
}

@-moz-document regexp("http://animeonline.*/movies.*"), regexp("http://animeonline.*/popular.*"), regexp("http://animeonline.*/new-season.*"), regexp("http://animeonline.*/calendar"), regexp("http://animeonline.*/latest-.*"), regexp("http://animeonline.*/list-.*") {
.next {
    position: static !important;
    top: 55vh;
    margin-top: -100px;
    width: 200px;
    height: 50px;
    background: none;
}

#calendar table.fc-border-separate {
    background: #404040 !important;
    border-radius: 8px;
}

.fc-header-right .fc-state-default {
    background: #404040 !important;
}
}
