css:
/* Name          : Supremacy 1914 additional Simple styles
 * Author        : Bergjnl
 * Version       : 0.13.1
 * Latest update : 2016-12-29
 */

@-moz-document
	domain("supremacy1914.com"),
	domain("supremacy1914.de"),
	domain("supremacy1914.nl"),
	domain("supremacy1914.es"),
	domain("supremacy1914.pl"),
	domain("supremacy1914.it"),
	domain("supremacy1914.fr"),
	domain("supremacy1914.gr")
	{

	/* Removes AdsContainer on the left side of the sceen */
	#inGameAdsContainer
	{
		display: none !important;
	}

	/* Stretches the game to fit the screen if AdsContainer is removed */
	#s1914
	{
		width: 100% !important;
	}

	/* Removes facebookshare button */
	#func_btn_facebookshare,
	#func_invite_button
	{
		display: none;
	}

	/* Removes incorrect popups from screen */
	#helperErrorContainer
	{
		display: none !important;
	}

	/* Profile window height */
	#playerDetailContainer
	{
		height: 512px !important;
	}

	#unit_selection_actions .default_button
	{
		margin: 0 4px !important;
	}

	.echo-table-cell-large
	{
		width: 240px !important;
	}

	.iga-applicant-list .echo-table-cell-large
	{
		width: 225px !important;
	}

	/* Easy fix for the client controls that didn't align properly */
	#clientControlsContainer .client_controls
	{
		margin-left: 75px;
	}

	/* Changes the color of the text field in diplamacy window */
	#func_diplomacy_messages .diplomacy_message_creation textarea
	{
		color: black;
	}

	/* Add resource bar and spybar shadow */
	.glasspanel
	{
		-webkit-box-shadow: 0 0 0 1px #a0a0a0, 1px 1px 0 1px #575757, -1px 0 2px 0 rgba(12, 12, 12, 0.44) inset, 0 0 35px 0 rgba(0, 0, 0, 0.5);
		box-shadow: 0 0 0 1px #a0a0a0, 0 0 0 1px #575757, 0 0 2px 0 rgba(12, 12, 12, 0.44) inset, 0 0 25px 0 rgba(0, 0, 0, 0.5);
	}

	/* Add footer shadows */
	.info_footer
	{
		-webkit-box-shadow: 0 0 25px 0 rgba(0, 0, 0, 0.5);
		box-shadow: 0 0 25px 0 rgba(0, 0, 0, 0.5);
	}

	.func_map_overlay_province
	{
		-webkit-box-shadow: 0 20px 20px -15px rgba(0, 0, 0, 0.5);
		box-shadow: 0 20px 20px -10px rgba(0, 0, 0, 0.5);
	}

	/*
	 * Start bottom bar
	 */

	/* Removes left and right buttons from provincie footer menu */
	#func_provbar_iter_left,
	#func_provbar_iter_right
	{
		display: none !important;
	}

	/* Removes left and right buttons from troops footer menu */
	#func_armybar_iter_left,
	#func_armybar_iter_right
	{
		display: none !important;
	}

	/* Remove scrollbar, but leave scrollable */
	#army_bar #status_segment .scrollbar
	{
		display: none;
	}

	#helperTooltipContainer .minimal_tooltip
	{
		max-width: 350px;
	}

	#helperTooltipContainer #morale_info
	{
		width: 350px;
	}

	/*
	 * End bottom bar
	 */

	/*
	 * Begin newspaper
	 */

	/* Make the newspaper fullscreen */
	#newspaperContainer
	{
		top: 0 !important;
		left: 0 !important;
		width: 100% !important;
		height: 100% !important;
		z-index: 1006 !important;
	}

	#func_newspaper_map
	{
		margin-top: 17px !important;
		height: 283px !important;
	}

	#newspaperContainer .paper_panel,
	#newspaperContainer .wooden_panel
	{
		top: 0;
		bottom: 0;
		left: 0;
		right: 0;
	}

	#map_section.newspaper_overview_section
	{
		width: 33%;
	}

	#func_newspaper_map
	{
		width: 100%;
	}

	#func_newspaper_map
	{
		position: relative;
	}

	#newspaper_cat_filter
	{
		border-bottom: 1px solid rgb(175, 173, 169);
		width: 100% !important;
		float: none;
		margin-left: 0;
		margin: 16px 0 0;
	}

	#newspaper_articles .overview .sg_float_l
	{
		float: right;
	}

	#writeArticleContainer
	{
		z-index: 1007 !important;
	}

	/* Change article layout */
	#articles #articles
	{
		column-count: 2;
		column-gap: 0;
	}

	.newspaper_article
	{
		width: 100%;
		page-break-inside: avoid;
		display: inline-block;
	}

	.newspaper_article_pad_right
	{
		padding: 0;
	}

	.newspaper_tabs li
	{
		border-bottom: 1px solid transparent !important;
	}

	#article_dialog_body textarea
	{
		height: 175px !important;
	}

	.new_article_dialog .select_image_button
	{
		right: 0;
		left: 287px;
	}

	/*
	 * End newspaper
	 */

	/*
	 * Start province sidebar
	 */

	/* Change the button height of the sorting options in the sidebar */
	#provinceListContainer tr #func_sort_by_upgrades div
	{
		margin-top: -8px;
	}

	#province_list_upgrade_icon_header
	{
		bottom: 0;
	}

	/* Fixes the arrows position after the height change */
	.gui-arrow_sorting_down_white
	{
		margin-top: 0 !important;
	}

	/* Adds a better border to the sorting options */
	.province_list_upgrade_sorting_divider
	{
		height: 8px;
		border-left: 1px solid;
		border-color: rgba(163, 161, 157, 0.82);
		background-color: rgba(101, 101, 101, 0.3);
	}

	#func_provinceWidgetTableHead td.active .province_list_upgrade_sorting_divider
	{
		border-left: 0;
		border-right: 1px solid !important;
		border-color: rgba(97, 97, 97, 0.93) !important;
	}

	#func_provinceWidgetTableHead td.active .province_list_upgrade_sorting_divider_active
	{
		background-color: rgba(101, 101, 101, 0.7);
	}

	/* Fix sidebar order */
	.upgrade_icon
	{
		margin: 0;
	}

	.prov_list_building_slot
	{
		min-width: 37px;
	}

	#province_list_upgrade_icon_header,
	.column_upgrades > div
	{
		display: flex;
	}

	/* Removes the arrow on the wrong location */
	.prov_list_sort_marker
	{
		display: none !important;
	}

	.province_list_upgrade_sorting_divider
	{
		left: auto !important;
		position: initial;
	}

	#province_list_upgrade_icon_header div:nth-child(4),
	.prov_list_building_slot:nth-child(4)
	{
		order: 7
	}

	/*
	 * End province sidebar
	 */

	/*
	 * Start production menu
	 */

	/* Change hight of province production viewport */
	#provinceProductionsContainer
	{
		top: 80px !important;
		height: 770px !important;
	}

	/* Change width of option menu */
	#table_prov_popup .action_cell
	{
		width: 210px;
	}

	.building_popup_content .building_details > div
	{
		padding: 5px !important;
	}

	.building_popup_content .building_details .construction_bottom
	{
		right: 10px;
		left: 10px;
	}

	/*
	 * End production menu
	 */

	/*
	 * Begin coalition window
	 */

	.delta-table-text-area-input
	{
		padding: 5px;
		width: 99.25%;
	}

	.iga-info-container
	{
		margin: 10px;
	}

	#inGameAllianceDetailContainer
	{
		top: 83.5px !important;
		height: 677px !important;
	}

	.iga-member-list-table
	{
		height: 345px;
	}

	.iga-member-list-table .func_scrollable
	{
		height: 282px;
	}

	/*
	 * End coalition window
	 */

	@media (min-width: 1880px) {
		#articles #articles
		{
			column-count: 3;
		}
	}

	@media (max-width: 700px) {
		#articles #articles
		{
			column-count: 1;
		}
	}
}
