css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {
  
  /*
  * Home Page
  */
  
  /* Default bkgd. */
  body {
    background-color: #202020;
    color: #c0c0c0;
    background-image: none;
  }
  
  /* Top Pane */
  div.header {
    background-color: #202020;
  }
  
  /* Delicious Logo */
  div.logo a {
    color: #aaaaaa;
    opacity: 0.8;
  }
  
  /* 'Trending', 'Profile', 'Add Bookmark' */
  div.navPanel ul li, div.navPanel ul li a {
    color: #aaaaaa;
  }
  
  /* Profile dropdown */
  .expanding {
    background-color: #262626;
  }
  
  .expanding li a {
    color: #0075e5 !important;
    background-color: #333333;
  }
  
  /* Search
  div.tagsSeacrhPanel input {
    background-color: #333333;
    background-image: none;
  } */
  div.searchPanel input {
    background-color: #333333;
    border-color: #666;
    background-image: invert(80%);
  }
  
  /* 'Search', 'Search by Tag' */
  div.searchPanel button {
    background-color: #3f3f3f;
    color: #aaaaaa;
  }
  
  /* Profile Box */
  div.profileInfo {
    background-image: none;
  }
  
  /* Following Profiles */
  .profileList li a img[src="https://del.icio.us/static/img/profile/default-avatar-2.jpg"] {
    filter: invert(93%);
  }
  
  /* 'Advertisement' */
  .google-da-disclaimer {
    background-color: #202020;
  }
  
  /* 'Me', 'Feed', 'Following', 'Followers' */
  div.tabMenu ul li a {
    color: #aaaaaa;
  }
  
  div.tabMenu ul {
    border-bottom-color: #666;
  }
  
  /* Check Box - 'Make Public', 'Make Private', etc. */
  div.tabSubMenu input {
    filter: invert(89%);
  }
  
  /* Search by Tag */
  div.tagsSeacrhPanel input {
    background-color: #333333 !important;
    background-image: none;
    color: #999999;
  }
  
  /* Tags List */
  .shuffleFontSize {
    background-color: #282828;
  }
  
  /* Tags List - dark color Tags */
  .shuffleFontSize > a:nth-child(7n){
    color: #777777;
  }
  
  .shuffleFontSize > a:nth-child(2n){
    color: #336666;
  }
  
  .shuffleFontSize > a:nth-child(11n) {
    color: #884545;
  }
  
  /*** List of Links ****/
  
  /* bkgd */
  div.articleThumbBlock {
    background-color: #282828;
  }
  
  /* private*/
  .privateArticle {
    background-color: #363636 !important;
  }
  
  .privateArticle input[data-parent-check="bulkchk"] {
    filter: invert(80%);
  }
  
  /* Title */
  div.articleThumbBlock h3 a {
    color: #0075e5; /* #0076e8; */
  }
  
  div.articleThumbBlock h3 a:hover {
    color: #3291ec;
  }
  
  /* Checkbox */
  input[data-parent-check="bulkchk"] {
    filter: invert(85%);
  }
  
  /* 'n' Save */
  div.savesCount {
    background-color: #333333;
    opacity:0.1;
  }
  
  div.savesCount:hover {
    background-color: #505050;
    opacity:1;
  }
  
  div.savesCount::before {
    background-image: none; /* to remove tiny corner.png */
  }
  
  /* Tag */
  ul.tagName li a {
    /* color: #0283ff; */
    font-size: 20px;
  }
  
  ul.tagName li a:hover {
    color: #369cff;
    font-size: 20px;
  }
  
  /* 'Edit', 'Delete', 'Share' pane */
  div.sharePanel {
    background-color: #242424;
    border-top-color: #242424;
    border-bottom-color: #181818;
  }
  
  div.sharePanel ul li a {
    color: #999999;
  }
  
  div.sharePanel ul li a:hover {
    color: #cccccc;
  }
  
  /* Private lock */
  div.sharePanel ul li {
    color: #666;
  }
  
  /* Share */
  div.popInner { /* also in 'Save Link' pop-up pane */
    background-color: #202020; /* #333333; */
  }
  
  .sharePopup {
    background-color: #242424; /* #5b5b5b; */
  }
  
  .saveLinkPop label {
    color: #666;
  }
  
  /* Page No.s */
  .pagination > li > a {
    background-color: #333333;
    border-color: #666;
  }
  
  .pagination > .active > a {
    color: #c0c0c0;
  }
  
  .pagination > li > a:hover {
    color: #c0c0c0;
  }
  
  /* Footer */
  div.footer {
    background-color: #202020;
    border-top-color: #666;
  }
  
  /* Save Link */
  .popcon .pop {
    background-color: #202020; /* #333333; */
  }
  
  input[name="title"], input[name="tags"], textarea[name="note"] {
    border-color: #666 !important;
    color: #c0c0c0;
  }
  
  /* Delete Link */
  #delAlert .alert .con {
    background-color: #333333;
  }
  
  #delAlert .alert .con .btns {
    background-color: #262626;
    border-top-color: #191919;
  }
  
  /*
  * Pop-up Window for Add/Edit Link
  */
  
  /* Title and Comment */
  #DELI_link_info input, #DELI_link_info textarea {
    background-color: #474747;
    color: #cccccc;
  }
  
  /* URL */
  #DELI_link_info #DELI_link_url input {
    background-color: #474747;
    color: #cccccc;
  }
  
  /* Tags */
  .DELI_tag_container {
    background-color: #474747;
  }
  
  .DELI_tag {
    background-color: #3A3432 !important;
    border-top-color: #3E3C3C;
    border-bottom-color: #474544;
    border-right-color: #474646;
    color: #cccccc;
  }
  
  .DELI_tag .ar {
    filter: invert(80%);
  }
  
/*   / * Tags Scheme 2 * /
  
  .DELI_tag {
    background-color: #302A28 !important;
    border-top-color: #373737;
    border-bottom-color: #464646;
    border-right-color: #3C3A3A;
    color: #b0b0b0;
  }
  
  .DELI_tag .ar {
    filter: invert(84%);
  } */
  
/*   / * Tags Scheme 3 * /
  
  .DELI_tag {
    background-color: #170F0C !important;
    border-top-color: #202020;
    border-bottom-color: #343433;
    border-right-color: #393838;
    color: #aaaaaa;
  }
  
  .DELI_tag .ar {
    filter: invert(96%);
  } */
  
  /* Private button at bottom left */
  #DELI_banner #DELI_make_link_private {
    border-color: #f4f4f4; /* for 90% invert */
    filter: invert(90%);
  }
  
  /* Tag dropdown */
  .DELI_dropdown {
    background-color: #474747;
    color: #cccccc;
  }
  
  p[style="font-weight: 700; line-height:16px; padding:7px; background:whitesmoke"] { /* for No Match */
    background-color: #474747 !important;
  }
  
  /* Post-save Msg */
  #DELI_save_result {
    background-color: #474747;
    color: #cccccc;
  }
  
  /*** del.icio.us/trending ***/
  .deliciousMessage {
    background-color: #262626;
  }

}
