css:
@-moz-document domain("3dsiso.com") {
/*A simple code snippet to remove excess junk from the top of every page*/
div.latestust,
div.donatorlist,
li#navbar_notice_28.restore,
li#navbar_notice_22.restore,
li#navbar_notice_23.restore,
li#navbar_notice_24.restore,
li#navbar_notice_20.restore,
li#navbar_notice_21.restore,
li#navbar_notice_29.restore,
li#fb_headerbox {
    display: none;
}
/*[[BetterNightly]]*/
}

style_setting_options.value:
body {
    background-color: #303030;
}
div.above_body[style] {
    background: url(http://i.imgur.com/IVSWpjh.jpg)!important;
}
a.logo-image {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    -m-filter: grayscale(100%);
}
.forumhead font[color^="ef733f"] {
    color: #B9C3CC!important;
}
.navbar,
.forumhead {
    background: #1F1F1F;
}
.body_wrapper {
    background: #101010;
}
.forumrowdata p.subforumdescription font[color^="000000"],
p.forumdescription font[color^="000000"],
#forums a.username,
#forums a.username:hover,
body,
.threadbit,
li.navbit.lastnavbit span font[color^="ef733f"],
.forum_info,
.thread_info,
.postbit,
.lastedited,
.time,
.wgo_block .section,
.above_postlist .pagination_top .pagination,
.above_postlist .pagination_top .postpagestats,
.above_threadlist .threadpagenav .threadpagestats {
    color: #C8C8C8;
}
#pagetitle p.description font[color^="000000"] {
    color: #C8C8C8!important;
}
.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow,
.forumhead + .childforum .L2:first-child .forumrow,
.forumhead + .L2 .forumrow,
.forumrow + .childsubforum .forumrow,
.forumhead + .childforum .L2:first-child .L2 .forumrow,
.forumhead + .childforum .L2 .L2:first-child .forumrow {
    color: #C8C8C8;
    background-color: #404040;
}
h2 a font[color^="ef733f"],
body a,
body a:hover,
li.subforum a font[color^="ef733f"],
.forumtitle font[color^="ef733f"],
li.navbit a font[color^="ef733f"],
.lastedited a {
    color: #B9C3CC;
}
.threadbit .sticky {
    background-color: #1D1F2F;
    border: 1px solid #000;
    border-top: 0;
}
.threadbit .nonsticky {
    background-color: #404040;
    border: 1px solid #000;
    border-top: 0;
}
.forum_info .blockbody, .thread_info .blockbody, .wgo_block .blockbody {
    background: #404040;
}
.postbit .userinfo,
.postbit .userinfo_noavatar {
    background: #404040;
}
.postbit .postbody {
    color: #C8C8C8;
    background-color: #202020;
}
.bbcode_container div.bbcode_quote {
    background: #191919;
}
.bbcode_container div.bbcode_quote_container {
    display: none;
}
.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols {
    background-color: #606060;
}
.formcontrols .blockrow {
    background-color: #1F1F1F;
}
.cke_skin_kama .cke_wrapper.cke_ltr,
.cke_skin_kama .cke_wrapper.cke_rtl {
    background: #000;
}
.navlinks {
    background: #202020;
}
