css:
@-moz-document domain("theoldreader.com") {
.post .content .content-body {
    overflow: hidden;
    font-family: helvetica, Arial, "Hiragino Sans GB", "Microsoft YaHei", simsun;
    font-size: 16px;
    word-wrap: break-word;
    line-height: 1.8125em;
    color: #333;
    text-indent: 2em;
}
.post .content .content-body p {
    line-height: inherit;
}
.navbar {
    overflow: visible;
    margin-bottom: 20px;
    height: 2px;
        z-index: 0;
}
.navbar .navbar-inner {
    background-color: #f7f7f7;
    background-image: none;
    position: relative;
    padding: 0;
    display: none;
}
.navbar:hover{
	 z-index: 1000;
}
.navbar:hover .navbar-inner {
    display: block !important;
}

.main-container{
	background-color: #FFFFFF;
    padding-top: 2px;
}

.cell{
    background-color: #FFFFFF;
}



@media (min-width: 768px) {
    .reader .container-fluid {
        top: 7px;
    }
    .reader .floating {
        background: #fff;
        padding: 0;
    }
    .reader .body-fixed-top .slide {
        padding: 0 10px 0 0;
    }
    .reader .body-fixed-top {
    	top: 2px;
	}
}
}
