css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.translate.ru/dictionary/) {
  
  header,
  .s1,
  .s3,
  .s4,
  #gramFormNav,
  .dictBlockHeader,
  #findPhraseBlock,
  .printBlock,
  #SiteContent_autoInfo,
  #rightSection,
  #topMenu,
  #dmenu,
  #btr_web,
  #socialServices,
  .subMenu {
    display: none !important;
  }
  
  body {
    background: none !important;
  }

  .perfective {
    background-color: #ffd !important;
  }
  
  .source_only_wf,
  #mfi_grammInfo p span b {
    font-size: 150% !important;
  }
  
  #mfi_grammInfo p span {
    border: none !important;
  }
  
  #sourceBlockDict {
    float: none !important;
    margin: auto !important;
  }
  
  #mfi_grammInfo p {
    margin: 10px 0 0 0 !important;
  }
  
  #mfi_grammInfo p,
  #mfi_grammInfo span {
    text-align: center !important;
  }
  
  #mfi_grammInfo table {
    margin: 5px auto 40px !important;
  }
  
  #directionSection {
    margin: 80px auto 30px !important;
  }
  
  .vert1 {
    width: 35% !important;
  }
  
  #sourceDict {
    margin: auto !important;
  }
  
  .transl_form_des,
  .tr_f_d {
    font-size: 12px !important;
  }
  
  #contentBlock {
    background-color: #fff;
    border: unset !important;
    border-radius:  unset !important;
    box-shadow: unset !important;
    margin: 50px 140px !important;
    padding: 0 !important;
  }
  
  .s2 {
    border-top: none !important;
    padding-top: 30px !important;
  }
  
  #mainSection {
    margin: 0 !important;
  }
  
  .wrapp {
    padding: 0 !important;
  }
  
  #mfi_grammInfoBlock {
    margin-left: 0 !important;
  }
  
  #aspnetForm {
    background-color: #fff !important;
  }
  
  #mfi_grammInfo p {
    margin-bottom: 20px !important;
  }
  
  #SiteContent_sourceTextDict,
  .tr_f {
    font-size: 15px !important;
  }
  
  .wfSpan {
    padding: 20px !important;
    width: 19% !important;
  }
  
  #sourceDict {
    border: 1px solid #ddd;
    border-radius: unset !important;
    padding: 10px 20px !important;
  }
    
}
