css:
@-moz-document domain("mbti-databank.com") {
div#mbti_vote{
	color: #DDDDDD;
    font-family: Segoe UI,sans-serif;
}
#gauchedemi a:hover {
	color : #9090A5;
	text-decoration:none;
}
font{
	color : #9090A5 !important;
}
div#ennea_vote{
	color: #DDDDDD;
    font-family: Segoe UI,sans-serif;
}
#area a:hover {
	color : #9090A5; 
	text-decoration:none;
}
#gauchedemi li a:hover {
	text-decoration: none;
	font-size: 0.8em;
	color : #9090A5;
}
#area a{
	font-family: Segoe UI,sans-serif;
}
h5 {
	font-family: Segoe UI,sans-serif;
    font-size: 16px;
	color: #FFFFFF;
    text-decoration: underline;
    font-weight: 500;
}
#right a:hover {
	color : #9090A5; 
	text-decoration:none;
}
#right a{
	font-family: Segoe UI,sans-serif;
}
body {
	color: #999;
	font-family: Segoe UI,sans-serif;
}
h2 {
	font-family: Century gothic,sans-serif;
	color: #FFF;
	margin-bottom: 1px;
    font-weight: 600;
}
.barre {
	background-color: #000;
	border:1px solid #999;
	height:18px;
	width:180px;
	color: #999;
    font-family: Segoe UI,sans-serif;
}
form input.text, form textarea{
	width:90%;
	border:1px #999 solid;
	color:#999;
	padding: 4px 5px;
	font:12px 'Segoe UI','lucida sans unicode','gill sans','verdana',sans-serif;
	background-color: #111;
}
h3 {
	font: 400 1.0em Segoe UI,sans-serif;
	color: #FFF;
	margin: 5px 0;
}
div.nav2{
	font-family: Century gothic,sans-serif;
}
div.nav{
	font-family: Century gothic,sans-serif;
}
.nav ul {
	width: 850px;
	border-top: 1px solid #333;
	border-bottom: 1px solid #333;
	padding: 3px 0 3px 0;
	background-color: #CCCCCC;
}
.bouton{
        height:2em;
        padding:1px 4px;
        margin:10px 10px;
        cursor:pointer;
        display:inline-block;
        color:#555555;
        font-size:1.3em;
        border:1px solid #555;
        background:#CCC;
        border-radius:4px;
        line-height:2em;
        text-decoration:none;
        font-family: Segoe UI,sans-serif;
        font-weight: 500;
}
.nav li a {
	float: left;
	display: block;
	color: #111;
    text-align: center;
	text-decoration: none;
	background-color: #FFF;
	border: 1px solid #1F1F1F;
	padding: 5px 10px 5px 10px;
	font-size: 0.9em;
	margin: 0 0 0 2px;
	}
	
.nav li a:hover {
	background-color: #DDD;
    color: #444;
    font-weight: 600;
	}

.nav li a:hover span {
	color: #333;
	}
	
.nav li a span {
	font-size: 0.8em;
	color: #666;
	}
table {
    display: table;
    border-collapse: separate;
    border-spacing: 0px;
    border-color: #EEEEEE;
    border-top-width: 2px;
    border-right-width: 2px;
    border-bottom-width: 2px;
    border-left-width: 2px;
}
body {
	background: #000000 url('http://wallpaperstock.net/night-moon-mountains--sea-wallpapers_40781_1920x1080.jpg') repeat-x top center;
	}
ul.links li a {
	color: #EEE;
	text-decoration: none;
	display: block;
	border-top: 1px #000;
	padding: 5px 0;
	}
ul.links li a:hover {
	color: #FFF;
	border-top: 1px #FFF;
    text-decoration: underline;
	}
.entry p, #main p {
	font-size: 0.8em;
	margin: 10px 0 20px 0;
    color: #EEE;
	}
#gauchequart {
	vertical-align:top;
	background:#000;
	display:inline-block;
	clear: both;
	width: 240px;
	top:0;
	margin: 3px 0px 0 0;
	}
#main a, .credit a {
	color: #EEEEEE;
	text-decoration: underline;
	border-bottom: 1px #FFF;
}
h1 {
	color: #FFF;	
	font-size: 3.1em;
	margin-left: 10px;
	vertical-align:middle;
	display: inline;
	text-decoration:none; 
    font-weight: 600;
    font-family: "Century gothic", sans-serif;
}
	
}
