css:
@-moz-document url-prefix("https://filewarez.tv/") {
/* 
 * Name:    Filewarez
 * Author:  NoUs3r
 
 */

/* i really want this to be global */

/*common*/

body {
	width:960px;
	min-width:650px;
	max-width:auto;
	margin: auto auto auto auto;
	font-size:11px;
	color:#fff;
	line-height:1.230;
}   
.threadbit .sticky{
	background: #333 ;
	border: 1px solid #dddddd;
	border-top: 0;
	width: 100%;
	float: left;
	display:block;
	clear:both;
	position: relative;
}
.memberprofiletabunder {
	background-color: #222;
	background-repeat: repeat;
}
.bbcode_container div.bbcode_quote, blockquote.preview .bbcode_container div.bbcode_quote {
	margin:0;
	margin-bottom: 0;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	background: #222 none   ;
	border-radius: 5px;
	border: 1px solid #dddddd;
	font: normal  normal 13px Tahoma, Helvetica,Geneva,sans-serif;;
	position: relative;
	top: 0;
}
body,
html {
	margin: 0 auto;
	padding: 0;
	width: auto;
}
.live-results li {
	background-color: #333;
	padding: 3px;
	border: 1px solid #E7E7E7;
}
.live-results li.selected, .live-results li:hover {
	background-color: #333;
	color: #5D5D5D;
}
.live-results .search-all {
	background-color: #333;
	display: block;
	width: 100%;
	text-align: center;
	border-radius: 4px;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	-box-sizing: border-box;
}

.doc_header {
	border: none;
	color: #ffffff;
	font:normal normal normal 11px Tahoma, Helvetica, Geneva;
	position:relative;
	min-height:35px;
}
.doc_header a { color:#fff; }
.doc_header a:hover { color:#fff; }
.clear {
	clear: both;
}

img {
	vertical-align: middle;
}

.main_wrap {
	width: 960px;
	margin: auto auto auto auto;
}

.outer_border .main_wrap {
	padding: 0 5px;
	width: auto;
}

.outer_border {
	background-color: #FFF;
	border-right: 5px solid #4a4a4a;
	border-left: 5px solid #4a4a4a;
	padding: 0 1px;
	-webkit-box-shadow: inset 1px 0 0 #f8f8ff, inset -1px 0 0 #f8f8ff;
	-moz-box-shadow: inset 1px 0 0 #f8f8ff, inset -1px 0 0 #f8f8ff;
	box-shadow: inset 1px 0 0 #f8f8ff, inset -1px 0 0 #f8f8ff;
}

.above_body {
	margin-bottom: 0px;
}

.doc_header {
	background: #DE9530 url(http://static.filewarez.tv/images/filewarez/misc/t_bg.jpg) repeat-x top center;
}

.banner-info {
	background-color: rgba(0,0,0,0.5);
	color: rgba(255,255,255,0.8);
	font-size: 9px;
	position: absolute;
	padding: 4px;
	border-radius: 0 0 5px 0;
	-moz-border-radius: 0 0 5px 0;
	-webkit-border-radius: 0 0 5px 0;
}

.banner-info a, .banner-info a:hover {
	text-shadow: 0 0 10px #FFFFFF;
	text-decoration: none;
}

.logo-image img {
	width: 960px;
}

.ad_global_header {
	float: right;
}

#body_bg {
	background: #333;
	padding: 20px 0px;
}

#content_container {
	padding: 0px;
}

#content_inner {
	margin-left: -10px;
}

.textbox,
textarea,
select {
	text-transform: none;
	border: 1px solid #aaaaaa;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	border-radius: 0px;
}

.notices {
	margin-top: 0px;
	margin-bottom: 15px;
}

.notices li {
	background: #333;
	border: 1px solid #dddddd;
	color: inherit;
	font-size: 13px;
	line-height: 19px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
	-moz-background-clip: padding;
	-webkit-background-clip: padding-box;
	background-clip: padding-box;
	box-shadow: none;
}

.columnsort a:hover {
	color: #4D4D4D;
}

.mini .othermonth a {
	background: #cccccc;
	color: #333333;
}

#forum_icon_legend h5 {
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}

#forum_icon_legend .formcontrols {
	background: #222;
}

#forums .L1 .forumhead a,
.forumbit_nopost .forumhead .forumtitle,
.forumbit_nopost .forumhead span,
.forumbit_nopost .forumhead .collapse,
.forumbit_post .forumhead h2 span,
.threadlisthead span.threadinfo,
.threadlisthead span.threadinfo_withnotification,
.threadlisthead span.threadstats,
.threadlisthead span.threadreplies,
.threadlisthead span.threadviews,
.threadlisthead a,
.threadlisthead span.threadnotification,
.threadlisthead span.threadlastpost,
.threadlisthead,
.wgo_block .blockhead,
.forum_info .blockhead,
#usercp_nav h2.blockhead,
.blockhead,
