	line-height: 24px;
	border-bottom: none;
	background-clip: padding-box;
	-moz-background-clip: padding;
	-webkit-background-clip: padding-box;
	text-shadow: 0 1px 0 #000;
}
.shade {
	color:#E19B38;
}
.wgo_block .section dl dt {
	display:inline;
	color: #E19B38;
}
.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
	float: right;
	clear:left;
	vertical-align:middle;
	font-size: 11px;
	color: #fff;
	_display:inline;
}
.postbitlegacy .postdetails, .eventbit .eventdetails {
	float: left;
	width: 100%;
	background: #333 none   ;
	_background-image: none;
}
.postbitlegacy .userinfo .rank, .postbitlegacy .userinfo .usertitle {
	font-size: 11px;
	display:block;
	clear:both;
	position: relative;
	text-align: center;
	width: 100%;
	color:#E19B38;
}
.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
	margin-left: 200px;
	border-left: 1px solid #E5E5E5;
	background: #333 none   ;
	_background-image: none;
	padding-bottom: 1em;
}
.postbitlegacy .userinfo {
	float:left;
	position: relative;
	width:180px; 
	padding: 2px 10px 5px;
	font-size: 11px;
	display:block;
        background:#333;
	clear:both;
}
.postbit .posthead .nodecontrols a, .postbitlegacy .posthead .nodecontrols a, .eventbit .eventhead .nodecontrols a {
	color: #E19B38	;
        top: -3px;
        position:relative;
}
.postbitlegacy .posthead .time {
	font:   normal 11px Tahoma, Helvetica, Geneva;;
	color: #E19B38;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
	padding: 5px 10px 10px;
	font:    12px Tahoma, Helvetica,Arial,Calibri,Geneva,sans-serif;
	color: #fff;
}
.restore h1,
.restore h2,
.restore h3,
.restore h4,
.restore h5,
.restore h6,
.restore strong,
.restore dt {
	/* Bringing boldness back to headers and the strong element. */
	font-weight: bold;
    color:#fff;
}
#postador_upload_display .blockrow dd, #postador_upload_display .blockrow dt {
	display: inline-block;
	color:#fff;
}
#postador_upload_display .blockrow {
	padding-bottom: 4px;
	padding-top: 4px;
	color:#fff;
}
.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
	background: #333 url(http://i.imgur.com/xOgGntqt.jpg) repeat-x top   ;
	_background-image: none;
	border: 1px solid #dddddd;
	clear:both;
	display:block;
	float: left;
	width: 100%;
	color: #fff;
	margin: -1px -1px 0;
	font:   normal 11px Tahoma, Helvetica, Geneva;;
	padding: 4px 0;
}
.wgo_block .blockbody {
	border: 1px solid #dddddd;
	background: #333 ;
	_background-image: none;
}
.wgo_block .blockhead {
	font-size:11px;
	font-weight:bold;
	color: #fff;
	background: #333333 url(http://i.imgur.com/xOgGntqt.jpg);
	_background-image: none;
	border: 1px solid #dddddd;
}

.wgo_block .time {
	color: #fff;
}

.wgo_block .section {
	display:block;
	padding: 0 0 0.5em;
	font:    12px;
	color: #fff;
	clear:both;
	width: 100%;
}
.forumhead {
	background-clip: padding-box;
	-moz-background-clip: padding;
	-webkit-background-clip: padding-box;
}

.blockhead a {
	text-decoration: none;
	color: #fff !important;
}

.blockhead a:hover {
	text-shadow: 0 0 0 transparent, 0px 0px 5px #000;
	-webkit-transition: all 0.15s ease-in-out 0s;
	-moz-transition: all 0.15s ease-in-out 0s;
	-o-transition: all 0.15s ease-in-out 0s;
	transition: all 0.15s ease-in-out 0s;
}
.blockhead {
	font:  bold 11px Tahoma, Helvetica, Geneva;;
	color:#fff;
	background: url( http://i.imgur.com/xOgGntqt.jpg );
	_background-image: none;
	padding:4px 10px 4px 10px;
	border:1px solid #999;
	-moz-border-radius-topright: 5px;
	-moz-border-radius-topleft: 5px;
	-webkit-border-top-right-radius: 5px;
	-webkit-border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	border-top-left-radius: 5px;
}

.albumlist_entry {
	background: #F6F8F9;
	border: 1px solid #dddddd;
	margin-bottom: 15px;
}

#album .blockbody:first-child {
	background: #f2f2f2 url(http://static.filewarez.tv/images/filewarez/misc/tdg_bg.png) repeat-x top;
	border-top: 1px solid #dddddd;
	border-bottom: none;
}

#album .blockbody {
	padding: 10px;
	border-left: 1px solid #dddddd;
	border-right: 1px solid #dddddd;
	border-bottom: 1px solid #dddddd;
}

#picture_comment_inlinemod_form .blockbody {
	background: transparent;
}

.postbit.postbit-lite {
	margin-top: 10px;
}

#searchform {
	margin-bottom: 20px;
}

h2.searchlisthead a {
	text-decoration: none;
}

h2.searchlisthead a:hover {
	color: #ffffff;
}

a.collapse {
	float: right;
}

#searchtypeswitcher {
	border-bottom: 2px solid #E19B38;
}

#searchtypeswitcher li.selected a, 
#searchtypeswitcher li.selected a:hover {
	background: none repeat scroll 0 0 #E19B38;
	border: 1px solid #E19B38;
	color: #FFFFFF;
	position: relative;
}

#searchtypeswitcher li a {
	background-color: #F2F2F2;
	border: 1px solid #F2F2F2;
	border-top-left-radius: 3px;
	border-top-right-radius: 11px;
	color: #4D4D4D;
	display: block;
	font-size: 9px;
	height: 25px;
	line-height: 25px;
	padding: 0 10px;
	text-align: center;
	text-decoration: none;
	text-transform: uppercase;
}


#searchtypeswitcher li a:hover {
	background-color: #F2F2F2;
	border-left: 1px solid #E19B38;
	border-right: 1px solid #E19B38;
	border-top: 1px solid #E19B38;
	color: #E19B38;
}

.navlinks,
#navlinks,
.postbit-lite .postbithead,
.userprof_vmright .member_blockrow {
	background: #f2f2f2 url(http://i.imgur.com/xOgGntqt.jpg);
	border: 1px solid #dddddd;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
	-moz-background-clip: padding;
	-webkit-background-clip: padding-box;
	background-clip: padding-box;
}
.thread_info h4 {
	font-size:11px;
	font-weight:bold;
	color: #fff;
	background: #333333 url(http://i.imgur.com/xOgGntqt.jpg) repeat-x top   ;
	_background-image: none;
	border: 1px solid #dddddd;
	margin-top: 5px;
	-moz-box-shadow: -2px 2px 2px none;
	-webkit-box-shadow: -2px 2px 2px none;
	box-shadow: -2px 2px 2px none;
}

.userprof_vmright .member_blockrow {
	padding: 0px 0px 3px 0px;
}

