}

/*toplinks*/

.toplinks {
	margin: 0px;
	padding: 2px;
	border-radius: 0 0 0 5px;
	-moz-border-radius: 0 0 0 5px;
	-webkit-border-radius: 0 0 0 5px;
	z-index: 999;
}

.toplinks ul.isuser li#fb_headerbox a {
	margin-top: 0px;
}

.toplinks ul.nouser li#fb_headerbox a {
	margin-top: 2px;
}

.toplinks ul.isuser li.welcomelink {
	color: #ffffff;
}

.toplinks ul.isuser li {
	background: none;
}

.toplinks .logindetails {
	background: transparent;
	padding-top: 0px;
}

.toplinks ul.nouser li a {
	font-weight: normal;
	text-transform: uppercase;
	background: transparent;
	padding: 6px 6px 0px 6px;
	border-radius: inherit inherit 0 0;
	-moz-border-radius: inherit inherit 0 0;
	-webkit-border-radius: inherit inherit 0 0;
}

.toplinks ul.nouser li a:hover {
	color: #FFF;
	-webkit-transition: background 0.15s ease-in-out 0s;
	-moz-transition: background 0.15s ease-in-out 0s;
	-o-transition: background 0.15s ease-in-out 0s;
	transition: background 0.15s ease-in-out 0s;
}

.toplinks .notifications a.popupctrl {
	background: #DE9530 url(http://static.filewarez.tv/images/filewarez/misc/navtab_arrow_gray.png) no-repeat center right;
	color: #ffffff;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	border-radius: 0px;
}

.toplinks .notifications a.popupctrl:hover {
	background: #DE9530 url(http://static.filewarez.tv/images/filewarez/misc/navtab_arrow.png) no-repeat center right;
}

.toplinks ul.isuser .notifications .popupbody li a {
	background: transparent;
	border-top: none;
	font-family: Tahoma, Helvetica, Geneva;
	text-transform: uppercase;
	color: #333333;
	font-size: 10px;
}

.toplinks ul.isuser .notifications .popupbody li a:hover {
	background-color: #E19B38;
	color: #ffffff;
	text-decoration: none;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	-moz-background-clip: padding;
	-webkit-background-clip: padding-box;
	background-clip: padding-box;
}

.toplinks ul.isuser li a:hover{
	background: #111111;
	color: #ffffff;
	-webkit-transition: background 0.15s ease-in-out 0s;
	-moz-transition: background 0.15s ease-in-out 0s;
	-o-transition: background 0.15s ease-in-out 0s;
	transition: background 0.15s ease-in-out 0s;
}

.toplinks .nonotifications a.popupctrl {
	background: url(http://static.filewarez.tv/images/filewarez/misc/navtab_arrow_gray.png) no-repeat center right;
}

.toplinks .notifications a.popupctrl:hover,
.toplinks .nonotifications a.popupctrl:hover,
.toplinks .nonotifications a.popupctrl.active,
.toplinks .notifications a.popupctrl.active {
	background: #111111 url(http://static.filewarez.tv/images/filewarez/misc/navtab_arrow.png) no-repeat center right;
}

#fb_headerbox {
	float: left;
	margin: 1px 5px;
}

/*navbar*/

