css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ex.ua") {
  body {background: #171717!important; background-color: #171717!important; background-blend-mode: normal!important; background-attachment: fixed!important; font-family:'Helvetica Neue', sans-serif!important; color: #9f9e99!important;} 
  body > table {position: relative!important; max-width: 80%!important; margin: auto!important; background: #171717!important; box-shadow: 0 0 150px rgba(0,0,0,.99)} 
  font {color: #9f9e99!important;} 
  .fox_col1, .fox_col2, #body_element > center > div {background: #171717!important;} 
  #body_element, .table tr {padding: 30px 40px 0 40px!important;}
  span {font-size: 10pt!important; color: #a1a1a1!important;	font-weight: normal!important;}
  select {border: 1px solid #000000!important; border-radius: 4px; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; margin: 0!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #d7d7d6!important;}
  #announce, #ad_block_1, #ad_block_2, #ex_block_1, #ex_block_2, div[id*="admaster_0"], div[id*="adriver_banner"], #search_help, #html-id, .list td.small p:nth-child(2n+2), span.small, #claim_button, div[id*="admixer_"], div[id*="mixer_"] {display: none!important;} 
  #search_help, .list td.small p:nth-child(2n+2), span.small, #claim_button, div[id*="admixer_"] {display: inline!important;}
  td {font-size: 12pt!important;} 
  td#body_element > center {margin: -16px -16px -16px!important;}
  div.gsAddInfo, div.gsLinks, td.col.h24, div.col_h.today, div.gsAddInfo3, div.gsTemp, div.gsCity, div.gsWeatherIcon, div.gsAddInfo2, td#colsDays {background: #171717!important;} 
  div#gsInformerID-ovY10eyK0RbLq2.gsInformer {border:none!important;}
  .gsInformer .gsLinks .rightCol a, .gsInformer .gsLinks .leftCol b {color: #a3a3a3!important;}
  .gsInformer .gsLinks .rightCol a:hover {color: #ffffff!important;}
  .gsInformer .gsLinks .leftCol b:hover {color: #ffffff!important;}
  .gsInformer .cols .col_h.today {color: #dbdbdb!important;}
  .gsInformer .cols .col_h.tomorrow {color: #dbdbdb!important;}
  .gsInformer .gsAddInfo {color: #dbdbdb!important;}
  .menu {position: fixed!important; max-width: 100%!important; left:0!important; top:0!important; height:43px!important; padding-right: 10.5%!important; background: #171717!important; box-shadow: 0 4px 8px rgb(23,23,23) inset, 0 4px 7px rgba(0,0,0,.80); z-index: 1000!important; border:none!important;} 
  .menu a {font-size: 12pt!important;	color: #cfcfcf!important;	font-weight: normal!important;}  
  .menu a:hover {color: #978a61!important;}
  .menu_text {color: transparent;}
  .menu_login, td.menu_text select {border: none!important; background: none!important; font-weight: normal; color: #978a61!important;}   
  .include_0 td {background: #1d1d1d!important; padding: 8px!important; border: 1px solid #222222!important; border-radius: 6px; box-shadow: 0 0 12px rgba(0,0,0,.55);} 
  .include_0 td:active,.include_1 td:active, .panel td:active { position: relative; top: 2px;}
  .include_1 td, .panel td {background: #1a1a1a!important; padding: 10px 10px 2px 11px !important; border: 1px solid #222222!important; border-radius: 6px; box-shadow: 0 0 10px rgba(0,0,0,.50);}  
  .include_0 td:hover, .include_1 td:hover, .panel td:hover {box-shadow: 0 1px 16px rgba(0,0,0,.60);}
  .include_0 td > a, .include_0 td > small {position: relative!important; top: 3px!important;} 
  .include_0 div, .panel .r_button_small {position: relative!important; bottom: 3px!important;}
  .include_1 div {position: relative!important; bottom: 8px!important;}
  .panel p, .include_0 p {margin: 3px 0!important;} 
  .include_0 img,.include_1 img,.panel img {filter: drop-shadow(0 0 18px rgba(0,0,0,.80)) contrast(105%); -webkit-filter: drop-shadow(0 0 18px rgba(0,0,0,.80)) contrast(105%);}
  .include_0 img:hover,.include_1 img:hover,.panel img:hover {filter: drop-shadow(0 0 20px rgba(0,0,0,.99)) brightness(105%) contrast(110%); -webkit-filter: drop-shadow(0 0 20px rgba(0,0,0,.99)) brightness(105%) contrast(110%);}
  #search_box, #search_form, #search_link {position: inherit!important;} 
  #body_element > div:nth-child(4) {padding: 0!important;}
  #search_form > div:nth-child(2) {max-height: 0!important; max-width: 0!important; padding: 0!important;}
  #search_form > div:nth-child(2) > div:nth-child(1) {left: 4px!important; height: 0!important;} 
  #search_form > div > div:nth-child(1) {right: 11px!important; bottom: 16px}
  div:nth-child(4) form {position: relative!important; top: 8px!important; left: 9px!important;}
  form:nth-child(5) {position: inherit!important;}
  #body_element>p:nth-child(3), div:nth-child(4)>p {margin-top: 20px!important; margin-bottom: 5px!important;}
  #search_text {width: 400px!important; height: 31px!important; box-sizing: border-box!important; border-radius: 3px!important; font-weight: bold!important; font-size: 16px!important; box-shadow: 0 1px 8px rgba(0,0,0,.24)}
  #search_line {width: 400px!important; top: 7px!important;}
  #search_hint {box-shadow: 0 1px 10px rgba(0,0,0,.45); font-weight: bold!important;	font-size: 12pt!important;}
  #search_button {top: 7px!important; left: 405px!important; height: 31px!important; width: 140px!important; font-size: 16px!important; color: #cfcfcf!important; font-weight: bold!important;} 
  #search_button:hover {color: white!important;}
  div:nth-child(4) > p, #body_element > p:nth-child(3) {padding: 0 0 0 580px!important;} 
  div.news-bottom-note {background: #171717!important;}
  div#search_hint {background: #171717!important; border: 1px solid #000000!important; border-radius: 2px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important;}
  div.mlist {background: #171717!important; border: 1px solid #000000!important; border-radius: 1px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important;}
  div.fox_body {background: #171717!important; border: 1px solid #cfcfcf; border-radius: 1px; margin: 0!important; color: #e0e0e0!important;}
  div.fox_h { color: #a8a8a8!important;} 
  div.button {background: url("http://static.ex.ua/morphine_suffering/button.png")!important;} 
  div.button:hover {background: url(http://static.ex.ua/morphine_suffering/button_hover.png)!important;} 
  button#fox_create.fox_btn {border: 1px solid #000000!important; border-radius: 2px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #878787!important; font-weight: bold!important;}
  button#fox_create.fox_btn:hover {color: #e0e0e0!important; background: #171717!important;}
  button#fox_exp_send.fox_btn{border: 1px solid #000000!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #878787!important; font-weight: bold!important;}
  button#fox_exp_send.fox_btn:hover {color: #e0e0e0!important; background: #171717!important;}
  li.country {background: #000000;}
  .list {max-width: 100%!important; font-style: italic!important;}
  .list td, .comment td {padding: 0!important; border-bottom: 1px dashed #4f4f4f!important; color: #a5a5a5!important;}  
  .list tr td {background : #171717!important;}
  .list tr th {background : #171717!important; border-bottom: 1px dotted #878787!important; border-top: 1px dotted #878787!important;} 
  #body_element>p:nth-child(10) {display: inline-block!important; position: relative!important; top: 4px!important; margin: 0!important;}
  #body_element a:nth-child(9),.list td.small b {padding: 0 10px 0 0!important;}  
  .vjs-poster {z-index: 1!important;}  
  a {color: #a3a3a3!important;} a:hover {color: #d4d4d4!important;}
  .include_0 b,.list b {font-size: 14px!important;} small {font-size: 12px!important;}
  h1 {color: #999999!important;	font-size: 14pt!important; font-weight: 600!important;} 
  h2:hover {color: #d4d4d4!important;}
  h2, #body_element > table:nth-child(9) h1 {color: #bdbdbd!important; font-size: 22pt!important; font-weight: 700!important;}  
  a.info {font-size: 13px!important; border: none!important;}
  .button, .r_button a {border: 1px solid #000000!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #878787!important;}
  .button:hover, .r_button a:hover {color: #e0e0e0!important; background: #171717!important;}
  .small_button, .list .r_button_small a {border: 1px solid #000000!important; border-radius: 4px!important; box-shadow: 0 1px 2px rgba(255,255,255,.3) inset, 0 1px 3px rgba(0,0,0,.5)!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.03))!important; font-weight: 500!important; color: #949494!important;}
  .small_button:hover, .r_button_small a:hover {color: #e0e0e0!important; background: #171717!important;}
  .panel .r_button_small a, div.r_button_small a, .comment .r_button_small a {background: #171717!important; border: 1px solid #474747; border-radius: 2px; margin: 0!important;}
  .panel .r_button_small a:hover, div.r_button_small a:hover, .comment .r_button_small a:hover {background: #171717!important; border: 1px solid #878787; border-radius: 2px; margin: 0!important;}
  input {background: #171717!important; color: #a5a5a5!important; font-family: sans-serif!important; font-size: 14px!important; font-weight: 600!important; border: 1px solid #4f4f4f!important; padding-left: 10px;}
  .intl-tel-input .country-list .country {background: #171717!important;}
  textarea {background: #171717!important; color: #878787!important; border: 1px solid #4f4f4f!important; font-family: 'Helvetica Neue', sans-serif!important; font-size: 11pt!important; padding: 0 4px!important;}
  tbody > tr > td > table > tbody > tr > td {padding-top: 4px!important;}
  #body_element > table > tbody > tr > td > table > tbody > tr > td:nth-child(2){min-width:610px!important;}
  img {filter: drop-shadow(0 0 10px rgba(0,0,0,.99)) contrast(115%); -webkit-filter: drop-shadow(0 0 10px rgba(0,0,0,.99)) contrast(115%);}
  img#viewer_img {filter: none; -webkit-filter: none;}
  ul.country-list {background: #171717!important;} 
  .ex_title {font-size: 18pt!important;} 
  .copyright {border-top: solid 1px #444444!important;}
  #news .top li span {border-bottom: 1px solid #878787; font-size: 18px!important;color: #878787!important;}
  #news .watch {border: none!important;} 
  #news #ti li {border-bottom: 1px dashed #4f4f4f!important;} 
  #news #ti .of a {color: #878787!important;}
  #news #ti .of a:hover {color: #cfcfcf!important;} 
  #news #ti .of p {color: #757575!important;} 
  .news {color: #a5a5a5!important; font-size: 24px!important;}
  .link_body .top_right div {border-bottom: 1px dotted #878787!important; margin-left: 15px; padding: 5px 0;} 
  .link_body .top_right span {border-bottom: 1px solid #878787!important; font-size: 20px!important;} 
  [contentEditable="true"]:empty:not(:focus)::before {color: #787878!important;}
  div.fox_body {border: 1px solid #676767!important;} 
  #content_page {margin: 65px!important; font-size: 12pt!important; color: #a3a3a3!important;} 
  #usrpg .wr {margin-top: -15px!important;}
  element.style {position: relative; z-index: 1;} #uploadButton {border: 1px solid #000000!important; border-radius: 4px!important; box-shadow: 0 1px 3px rgba(255,255,255,.15) inset, 0 1px 3px rgba(0,0,0,.55)!important; background: #000000 linear-gradient(rgba(255,255,255,.18), rgba(255,255,255,.01))!important; color: #878787!important; font-size: 12pt !important;} 
  #uploadButton:hover {color: #e0e0e0!important; background: #171717!important;} 
  .small {color: #949494!important;} 
  .pos_up {color: #23bb16!important;} 
  .pos_down {color: #e12014!important;} 
  td.menu_text:nth-child(1){text-align: center!important; padding-left:55px!important;} 
  .r_browse_selected {background-color: #171717!important;}
}
