css:
@-moz-document domain("www.imzy.com") {
ul.comments-feed__comments .comment__content p,
ul.comments-feed__comments .comment__content li {
    font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
    font-size: 1.1em;
    line-height: 1.25em;
}

.md-editor .editor-preview ul, .md-editor__field.is-preview ul, .u-html ul {
    list-style: disc;
    margin-left: 1.3em;
}

body .comments-feed__comment {
    padding: 5px;
    margin-bottom: 5px;
}

ul.comments-feed__comments>.comments-feed__comment {
    margin-bottom: 10px;
}

body .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment
 {
    background-color: #F5F5F5 !important;
}

body .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment,
body .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment .comments-feed__comment body .comments-feed__comment .comments-feed__comment {
    background-color: #fff !important;

}

 .comment__meta {
    padding-top: 7px;
 }

 .comments-feed__comments .comments-feed__comments .comment__meta {
    padding-top: 3px;
 }

.comment__actions-bar * {
    font-size: 95%;
    color: #D0D0D0 !important;
}

.comments-feed__comments.is-top-level>.comments-feed__comment.is-with-children:after,
.comments-feed__comments .comments-feed__comments.has-depth-1>.comments-feed__comment.is-with-children:after{
    border: 0;
}

/* experiment for compact frontpage */ 

.feed-item.is-post .feed-item__main__meta .feed-item__main__meta__date, 
.feed-item.is-post .feed-item__main__meta .feed-item__main__meta__link-community, 
.feed-item.is-post .feed-item__main__meta .feed-item__main__meta__link-user {
    display: inline-block;
    margin-top: 2px;
    margin-right: 8px;
}





.post__content__participation {
    display: none;
}


ul.feed-post__content__tags {
    display: none;
}

.post-actions * {
    font-size: 0.85em;
}

.post-actions__item {
    margin-left: 5px !important;
}

h1.feed-post__content__heading {
    margin: 0;
}

.feed-post__content__ext-link__text {
    background: none !important;
}

@media screen and (max-width:500px) {
    
html[data-page-name="home-feed"] .feed-post__content__img {
    width: 43px !important;
    height: 43px !important;
    top: 14px !important;
    left: 14px !important;
}


html[data-page-name="home-feed"] .feed-post__content__img-wrapper .feed-post__content__img {
    top: -53px !important;
    left: 0px !important;
}
    

html[data-page-name="community-view"] .feed-post__content__img-wrapper .feed-post__content__img {
    top: -53px !important;
    left: 0px !important;
}
html[data-page-name="community-view"] .feed-post__content__img {
    width: 43px !important;
    height: 43px !important;
    top: 14px !important;
    left: 14px !important;
}
    
a.feed-post__content__ext-link {
    position: absolute;
    top: -33px !important;
    left: 45px !important;
}

a.feed-post__content__ext-link-inline {
    position: absolute;
    top: 30px !important;
    left: 65px !important;
}

.feed-post__content__ext-link-inline__text:first-child,
a.feed-post__content__ext-link span,
a.feed-post__content__ext-link-inline span {
    font-size: 15px !important;

}
    
}

html[data-page-name="home-feed"] .feed-post__content__img {
    height: 61px;
    width: 61px;
    position: absolute;
    display: block;
    top: 0px;
    left: -80px;
}


html[data-page-name="home-feed"] .feed-post__content__img-wrapper .feed-post__content__img {
    top: -50px;
    left: -100px;
}

html[data-page-name="community-view"] .feed-post__content__img {
    height: 61px;
    width: 61px;
    position: absolute;
    display: block;
    top: 0px;
    left: -80px;
    border-radius: 100px;
}

html[data-page-name="community-view"] .feed-post__content__img-wrapper .feed-post__content__img {
    top: -50px;
    left: -100px;
}



 a.feed-post__content__ext-link {
    position: absolute;
    top: -49px;
    left: -5px;
}

a.feed-post__content__ext-link-inline {
    position: absolute;
    top: 0px;
    left: 22px;
}

.feed-post__content__ext-link-inline__text:first-child,
 a.feed-post__content__ext-link span,
 a.feed-post__content__ext-link-inline span {
    font-size: 12px;
    color: #B2C3D5;
    text-shadow: none; 
}
}
