css:
@-moz-document domain("cupoflinux.com") {
/************ Cup of Linux Stylesheet made by Artex ************/

/**************** MAIN ******************/

body,
html {
    background: #262626 !important;
}
div.blockform h2 span,
div#quickpost h2 span {
    display: none !important;
}
div#viewprofile div.box {
    background: #3e3e3e !important;
    border: none !important;
}
div#viewprofile div.inform * {
    border-color: #262626 !important;
}



/***************** INPUT ******************/

input[type*="text"],
textarea,
input.button,
input[type*="submit"],
input[type*="reset"],
input[type*="password"],
input.password {
    background: #3e3e3e !important;
    border-color: #3e3e3e !important;
}
fieldset {
    border: none !important;
}
select {
    background: none !important;
    border-color: #3e3e3e !important;
}
button {
    background: none !important;
    background-image: none !important;
    border-color: #3e3e3e !important;
}
div.txtarea label textarea,
div.txtarea label textarea * {
    background: #262626 !important;
    border: 1px solid #3e3e3e !important;
}
/***************** TEXT *******************/

* {
    color: #dddddd !important;
}
a:link {
    color: #b2b2b2
                  !important;
}
a:visited {
    color: #898989!important;
}

/* Styles for the standard button lists.
------------------------------------------------------- */

/* the active button */

.dropmenu li ul
{
	z-index: 90;
	display: none;
	position: absolute;
	width: 19.2em;
	font-weight: normal;
	border-bottom: 1px solid #9e9e9e;
	background: url(http://i.imgur.com/rqCRkUf.png) 0 -100px no-repeat;
	padding: 7px 0 0 0;
}

.dropmenu li a.active
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 100% 0;
	color: #fff;
	font-weight: bold;
	text-shadow: 0px 0px 0 #415164;
}
.dropmenu li a.active span.firstlevel
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 0 0;
}
/* the hover effects */
.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 100% -30px;
	color: #fff;
	cursor: pointer;
	text-decoration: none;
	text-shadow: 0px 0px 0 #334960;
}
.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 0 -30px;
}
.buttonlist ul
{
	z-index: 100;
	padding: 5px;
	margin: 0 0.2em 5px 0;
}
.buttonlist ul li
{
	margin: 0;
	padding: 0;
	list-style: none;
	float: left;
}
.buttonlist ul li a
{
	display: block;
	font-size: 11px;
	color: #f5f5f5;
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 0 -60px;
	padding: 0;
	margin-left: 12px;
	cursor: pointer;
	text-shadow: 1px -1px 0 #0f1420;
}
.buttonlist ul li a:hover
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 0 -30px;
	color: #fff;
	text-decoration: none;
}
.buttonlist ul li a span
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 100% -60px;
	display: block;
	height: 28px;
	line-height: 26px;
	padding: 0 9px 0 0;
	position: relative;
	right: -8px;
}
.buttonlist ul li a:hover span
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 100% -30px;
}
/* the active one */
.buttonlist ul li a.active
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 0 0px;
	color: #fff;
	font-weight: bold;
	text-shadow: 1px -1px 0 #415164;
}
.buttonlist ul li a.active span
{
	background: url(http://i.imgur.com/rqCRkUf.png) no-repeat 100% 0px;
}
/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */
.signature, .attachments
{
	width: 98%;
	overflow: auto;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: 1px solid #595959;
	line-height: 1.4em;
	font-size: 0.85em;
}
.custom_fields_above_signature
{
	width: 98%;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: 1px solid #595959;
	line-height: 1.4em;
	font-size: 0.85em;
}

/****************** COLOR *****************/

/* Changes Bar Colors */
.titlebg, .titlebg2, tr.titlebg th, tr.titlebg2 th, tr.titlebg td, tr.titlebg2 td
{
	color: #111;
	text-shadow: 1px 1px 0 #5E83A6;
	font-size: 1em;
	font-weight: normal;
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat -10px -120px;
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
	color: #fff;
	font-size: 1.1em;
	font-weight: normal;
	background: url(http://imgur.com/KJw3uUA) repeat -10px -160px;
}
h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 100% -160px;
	padding-right: 9px;
}


h4.titlebg, h3.titlebg
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 100% -210px;
	padding-right: 9px;
}
h4.titlebg img.icon
{
	float: left;
	margin: 9px 8px 0 0;
}
div.cat_bar
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 0 -160px;
	padding-left: 9px;
	height: 36px;
	overflow: hidden;
	margin-bottom: 1px;
}
div.title_bar
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 0 -210px;
	padding-left: 9px;
	height: 36px;
	overflow: hidden;
	margin-bottom: 1px;
}

tr.catbg th.first_th
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 0 -160px;
}
tr.catbg th.last_th
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 100% -160px;
}
tr.titlebg th.first_th
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 0 -210px;
}
tr.titlebg th.last_th
{
	background: url(http://i.imgur.com/KJw3uUAs.jpg) repeat 100% -210px;
}

h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg
{
	overflow: hidden;
	height: 36px;
	line-height: 36px;
	font-size: 1.2em;
	font-weight: normal;
	text-shadow: 0px 0px 0 #000000;
}


/* Normal, standard links. */
a:link, a:visited
{
	color: #4d8c8a;
	text-decoration: none;
}
a:hover
{
	text-decoration: none;
	color: #4d8c8a;
	cursor: pointer;
}



#detailedinfo div.content dl, #tracking div.content dl
{
	clear: right;
	overflow: auto;
	margin: 0 0 18px 0;
	padding: 0 0 15px 0;
	border-bottom: 1px #9b9b9b solid;
}

.list_posts
{
	border-top: 2px solid #686868;
	padding-top: 12px;
	margin-top: 6px;
	overflow: auto;
}
/* Message background on User Bar */
.pm_count {
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	background: #686868;
	color: #222;
	font-size: 11px;
	padding: 0 5px;
}

.sceditor-container {
    position: relative;
    background: #2b2b2b;
    border: 1px solid #111;
    font-size: 13px;
    font-family: Arial,"Helvetica Neue",Helvetica,sans-serif;
    color: #111;
    line-height: 1;
    font-weight: 700;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
}
/*Styles for (fatal) errors*/
.errorbox
{
	padding: 1em;
	border: 0px solid #939393;
	color: #000;
	background-color: #280001;
	margin-bottom: 1em;
}
/* Borders. */
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote
{
	font-size: small;
	color: #aaa;
	line-height: 1.4em;
	/* background: url(../images/theme/quote.png) 0.1em 0.1em no-repeat; */
	border-top: 2px solid #919191;
	border-bottom: 2px solid #919191;
	padding: 1.3em 1.4em;
	margin: 0.1em 0 0.3em 0;
	overflow: auto;
}

.information
{
	padding: 0.5em 1em;
	font-size: 0.9em;
	line-height: 1.3em;
	border: 1px solid #919191;
	background: #939393;
	margin: 0.2em 1px 1em 1px;
	color: #f3f3f3;
}

table.table_grid td
{
	padding: 6px;
	border-bottom: 1px solid #939393;
	border-right: 1px solid #939393;
}
/* Gets rid of text-shadow in Forum posts*/

.titlebg, .titlebg2, tr.titlebg th, tr.titlebg2 th, tr.titlebg td, tr.titlebg2 td
{
	color: #111;
	text-shadow: 0px 0px 0 #5E83A6;
	font-size: 1em;
	font-weight: normal;
	background: url(http://imgur.com/E6KBvts.png) no-repeat -10px -120px;
}
/* Styling for BBC tags */
.bbc_link:link, .bbc_link:visited
{
	color: #b6b8ba;
}

/* Buttons Black and White */

#button_wrap a.login {
	background: url(http://i.imgur.com/EMVsTeo.png) 0 -60px no-repeat;
	margin-right: 10px;
	width: 90px;
}
#button_wrap .login:hover {
	background-position: 0 -90px;
}
#button_wrap a.register {
	background: url(http://i.imgur.com/EMVsTeo.png) 0 -120px no-repeat;
	width: 100px;
}
#button_wrap .register:hover {
	background-position: 0 -150px;
}
#button_wrap a.logout {
	background: url(http://i.imgur.com/EMVsTeo.pngg) 0 -180px no-repeat;
	width: 95px;
}
/* Sticky topics get a different background */
.stickybg {
	background: url(http://i.imgur.com/E6KBvtss.jpg);
}
.stickybg2 {
	background: url(http://i.imgur.com/E6KBvtss.jpg);
}

/* Locked posts get a different shade, too! */
.lockedbg {
	background: url(http://i.imgur.com/E6KBvtss.jpg);
	font-style: italic;
}
.lockedbg2 {
	background: url(http://i.imgur.com/E6KBvtss.jpg);
	font-style: italic;
} 
/* Alternating backgrounds for posts, and several other sections of the forum. */
.windowbg
{
	background: url(http://imgur.com/E6KBvts.png);
}
.windowbg2
{
	background: url(http://imgur.com/ftc2TH3/.png);
}
.windowbg3
{
	background: url(http://imgur.com/E6KBvts.png);
}

#header_box {
	background: url(http://i.imgur.com/E6KBvts.png) 0 0 repeat-x;
	height: 92px;
}
#topnav li a.active {
	background: url(http://i.imgur.com/E6KBvts.png) 0 0 no-repeat;
}
#topnav li ul {
	background: url(http://i.imgur.com/E6KBvts.png);
	position: absolute;
	width: 200px;
	left: -999em;
	z-index: 900;
	border-bottom: 1px solid #374558;
	border-left: 1px solid #374558;
	border-right: 1px solid #374558;
	margin-left: -1px;
}

#userbar {
	background: url(http://i.imgur.com/E6KBvts.png) 0 0 repeat-x;
	height: 50px;
}
#userbar_l {
	background: url(http://i.imgur.com/E6KBvts.png) 0 0 no-repeat;
}
#userbar_r {
	background: url(http://i.imgur.com/E6KBvts.png) 100% 0 no-repeat;
	padding: 0 6px;
}


/***************** ICONS ******************/

div.icon {
    border-color: #3e3e3e #3e3e3e #3e3e3e #262626 !important;
}
tr.inew div.icon {
    border-color: #3e3e3e #3e3e3e #3e3e3e #1793d1 !important;
}
a[title="Arch Linux (Home)"],
a[title="Arch Linux (Planet)"],
div#logo h1 a[title="Arch Linux"] {
    width: 0px !important;
    height: 40px !important;
    background: url("https://bbs.archlinux.org/style/ArchLinux/archlogo.png") no-repeat !important;
    padding-left: 190px !important;
}

table.shoutbox_container
{
	width: 100%;
	font-size: x-large; 
}
}
