css:
@-moz-document domain("www.google.ru"), domain("www.google.com.ua"), domain("www.google.com") {
* {font-family:Open Sans, Segoe UI !important; color:#333 !important}
body {background:#ccc !important}
a:link {color:#2980B9 !important}
a:hover {color:#3498DB !important}
a:visited {color:#999 !important}
b, em {font-weight:600 !important}
b > i {font-weight:normal !important}

.flyr-o, .flyr-w {background-color:#ccc !important}


/*home*/
.sbib_a {background:#ccc !important}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {background:#fff !important; transition:background 0.3s; font-size:16px !important; color:#3498DB !important; font-weight:100 !important}
.jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover {background:#ececec !important; border-color:rgba(0,0,0,.0) !important; box-shadow:none !important}
.sbibod {border:solid 1px #ececec !important}
.sbfcn {border:solid 1px #fff !important}
#fbar {background:none !important; border:none !important}

/*results*/
.sfbgg, .sfbg, #hdtb, #hdtbSum, #appbar, #hdtbMenus {background:#ccc!important; border:none !important}
#sblsbb {background:#fff !important; transition:background 0.3s}
.sbico {-webkit-filter:invert(60%)}
#sfdiv {border:none !important; height:40px !important}
#sblsbb:hover {background:#ececec !important}
#sfdiv .sbib_a {background:#fff !important; height:40px !important}
.sbdd_a {min-width:630px !important}
#sblsbb {border-radius:0 !important; border-left:solid 1px #ececec !important}
.sbdd_b {border:none !important; box-shadow:none !important; padding-bottom:5px}
#hdtb-tls {transition:all 0.3s}
#hdtb-tls:hover {background:rgba(255,255,255,.1) !important; border:solid 1px #ccc !important; border-radius:3px !important; box-shadow:#999 0 0 5px !important; color:#333 !important}
#hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover {background:rgba(255,255,255,.1) !important; border:solid 1px #ccc !important; border-radius:3px !important; color:#333 !important}
.gsfi, .lst {left:0 !Important}

.g {animation:slide_top 1s}
.g {background:#ececec !important; border:solid 1px #ececec !important; border-radius:10px !important; padding:15px 15px 20px 15px !important; margin-bottom:10px !important; transition:box-shadow, background 0.3s}
.g:hover {background:#fff !important; box-shadow:#999 0 0 10px !important}
#res h3.r {padding-bottom:5px !important}
._Fmb {background-color:rgba(0,0,0,.0) !important; opacity:0.6; transition:opacity 0.3s}
._Fmb:hover, ._Fmb.selected, ._Fmb.selected:hover {opacity:1 !important; border-bottom:none !important}

.rgsep {margin:15px -10px 10px -10px !important}
#res a {padding:0 3px !important; margin-left:-3px; border-bottom:solid 1px rgba(0,0,0,.0); transition:all 0.3s}
#res a:hover {color:#3498DB !important; text-decoration:none !important; border-bottom:solid 1px #3498DB}

#ires .kv {padding:5px 0 !important}

#rhs .kp-blk {margin:0 !important; box-shadow:none !important}
.kno-ftr.rhsvw {margin:0 !important; padding:5px 0 0 0 !important}
._xdb {font-weight:600 !important}

body > div[style*="background: rgb(45, 45, 45)"] {background:#ececec !important; border:none !important; box-shadow:#999 0 0 5px !important; border-radius:0 !important; font-weight:normal !important; font-size:13px !important; height:30px !important; line-height:30px !important}

#hdtb-msb .hdtb-mitem.hdtb-msel, #hdtb-msb .hdtb-mitem.hdtb-msel-pre {font-weight:600 !important}
#hdtb .hdtb-mitem a {border-bottom:solid 3px rgba(231,76,60,.0); transition:all 0.3s}
#hdtb .hdtb-mitem a:hover {border-color:rgba(231,76,60,1); color:#333 !important}

._OKe {padding:15px}
#rhs ._tdf {padding:0 !important}
#rhs .rhsvw {padding:10px 0 0 0 !important; width:auto !important}
#rhs .g-blk.rhsvw {padding:15px !important}
._wD:hover {background:none !important}

/*notis*/
.aac, .Rzc, .nfe, .SPc.rTvtwe.aac {background-color:#ececec !important}

/*video time*/
._dwc, .rg_ilmn {color:#fff !important; font-weight:normal !important}

/*animation*/
@keyframes slide_top {
    from {
        margin-top:200px;
        opacity:0;
    }
    to {
        margin-top:0px;
        opacity:1;
    }
}

/*images*/
#cnt {background:#ccc !important}
}
