css:
@-moz-document url-prefix(http://mail.google.com/mail/), url-prefix(https://mail.google.com/mail/), domain(mail.google.com) {

/* PAGE BACKGROUND, INPUT, TEXTAREA SETTINGS*/

.chc, .ci, .tbo, HTML,BODY, TABLE, INPUT, TEXTAREA, SELECT { 
color: #FFFFFF !important;
background-color: #000000 !important;
font-family: arial !important;
font-variant: small-caps !important;
font-size: 13pt !important;
}

/*LINKS*/
a:link,a:visited,a:hover,a:active {
  color: #00FF00 !important; 
}

/* HEADER IMAGE */
body div#ds_inbox img {	display: none !important;}
body div#ds_inbox {background-image: url('data:image/gif;base64,') !important;
	width: 135px !important;
	height: 135px !important;
	background-repeat: no-repeat !important;
}

/* HIDE BOLD SPAM COUNT */
#ds_spam b { visibility: hidden; }
#ds_spam b::before { content: "Spam"; visibility: visible; font-weight: normal; }

/* THE BUTTONS ATTRIBS*/
BUTTON { border-color:#FFFFFF !important; 
background-color:#000000 !important;  
color:#00FF00 !important }

/* Hide GMail invites */
span[class~=ilc][class~=sxs] {
  display: none !important;
}
div#nb_1 {
  display: none !important;
}

/*rounded borders*/
 .rnd,  .rnd1, .rnd2, .thc  {
color: #FFFFFF !important;
background-color: #000000 !important;
border-color:#FFFFFF !important;
}
.rnd1l, .rnd2l, .rnd1r, .rnd2r {
color: #FFFFFF !important;
background-color: #000000 !important;
border-color:#FFFFFF !important;
}

/* HIDE PAGE FOOTER*/
div#ft {
background-color: #000000 !important;
color: #00FF00 !important;
display: none !important;
}

/* MAIL ZEBRA STRIPING */
/* UNREAD MAIL */
table.tlc tr.ur {
background-color: #000000 !important;
color: #00FF00 !important;
}

/* READ MAIL */
table.tlc tr.rr {
background-color: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

table.tlc tr.ur td,
table.tlc tr.rr td{
background-color: #000000 !important;
color: #FFFFFF !important;
}

/* SIDEBAR LINKS */
body div#nav table.cv,
body div#nav table.cv td,
body table.cv td.tl,
body table.cv td.bl {
height: 1px !important;
background: #000000 !important;
color: #00FF00 !important;
}

/* SELECTEDD TOP AND BOTTOM BORDER */
body b.rnd1l,
body b.rnd2l {
background: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

table.cv td.tl,
table.cv td.bl {
height: 0 !important;
background: #000000 !important;
color: #00FF00 !important;
border-color:#FFFFFF !important;
}

/* BOTH CURRENT AND OTHER */
body table.cv td span.lk,
body div.nl span.lk{
display: block !important;
background-color: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
-moz-border-radius: 6px !important;
padding: 2px !important;
margin-right: 5px !important;
}

/* JUST THE CURRENT SELECTION */
body table.cv td span.lk {
background: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

/* UNSELECTED FUNCTIONS */
body div.nl span.lk {
background-color: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

body div.nl span.lk:hover {
background-color: #000000 !important;
color: #00FF00 !important;
border-color: #FFFFFF !important;
}

/* HIDE PRINT/EXTERNAL WINDOW FROM OPEN MESSAGES */
div#ap,
div#ar,
div#prt,
div#ec,
div#ob,
div#rh{
display: none !important;
background-color: #000000 !important;
color: #00FF00 !important;
}

/* LABELS */
div#nb_0 {
background-color: #000000 !important;
color: #00FF00 !important;
padding: 0;
margin: 0;
border: 0;
}

div#nb_0 div {
background-color: #000000 !important;
color: #00FF00 !important;
padding: 0px;
margin: 0;
border: 0;
}

div#nb_0 div div {
border: solid 1px #FFFFFF;
-moz-border-radius: 6px !important;
padding: 0 1px 0 0 !important;
background-color: #000000 !important;
color: #00FF00 !important;
}

div#nb_0 div div div {
border: 0 !important;
padding: 0 !important;
background-color: #000000 !important;
color: #00FF00 !important;
-moz-border-radius: 0 !important;
}

div#nb_0 div.s,
div#nb_0 div.h{
padding: 1px 3px 0 3px !important;
background-color: #000000 !important;
}

/*rounded borders*/
.rnd, .rnd1, .rnd2, .thc  {
color: #FFFFFF !important;
background-color: #000000 !important;
}
.rnd1l, .rnd2l, .rnd1r, .rnd2r { 
color: #00FF00 !important;
border-bottom: solid 1px #FFFFFF !important;
-moz-opacity: 0.5;
}

div#nb_0 table,
div#nb_0 table td.tl,
div#nb_0 table td.tr,
div#nb_0 table td.bl,
div#nb_0 table td.br {
background-color: #000000 !important;
color: #00FF00 !important;
}

div#nb_0 table.nb {
background-color: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

div#nb_0 table.nb div.lk {
text-decoration: none !important;
margin: 3px 0 0 3px !important;
background-color: #000000 !important;
color: #00FF00 !important;
border: solid 1px #FFFFFF !important;
}

/* LABELS - EDIT LABELS */
div#nb_0 table.nb div#prf_l {
border: solid 1px #FFFFFF !important;
text-decoration: none !important;
margin-right: 50px !important;
-moz-opacity: 0.7 !important;
background-color: #000000 !important;
color: #FFFFFF !important;
}

/* HIDE *"NEW"* SUPERSCRIPT */
body div#nav sup {
display: none !important;
background-color: #000000 !important;
color: #00FF00 !important;
}

}
