css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.semanticscholar.org"), domain("citeomatic.semanticscholar.org"), domain("labs.semanticscholar.org"), domain("pdfs.semanticscholar.org") {
  
  /**** Palette ****/
  :root {
    --default-color: #6c90a9;
    --brighter-color: #8ca8bc;
    --gray-color: #aaaaaa;
    --default-bkgd-color: #333333;
    --heading-color: #7c9cb2;
    --darker-bkgd-color: #222222;
    --brighter-bkgd-color: #444444;
    --default-link-color: #2789bf;
    --border-color: #666;
  }
  
  /* Page Bkgd */
  .content {
    background-color: var(--default-bkgd-color);
  }
  
  /* Transitional Page lower Bkgd */
  div.s2.flex-column {
    background-color: var(--default-bkgd-color);
  }
  
  /* Default Text color */
  html {
    color: var(--default-color);
  }
  
  /* Header */
  .header {
    border-top-color: #1e303c; /* remove yellow line on top; set same color as bkgd-color */
  }
  
  /* Headings e.g. Pub Year, Author, Key Phrase, Abstract */
  h4 {
    color: var(--heading-color);
  }
  
  /* Overviews */
  .filter-type-overviews .checkbox:not(.is-disabled) .checkbox-label {
    color: var(--heading-color);
  }
  
  /* Heading icon
  .filter h4 svg {
    fill: var(--heading-color);
  } */
  
  /* 'Publication Year' slider YYYY bkgd */
  .slider div.bubble span {
    background-color: var(--default-bkgd-color);
  }
  
  /* Page N at top left */
  .results h1 {
    color: var(--heading-color);
  }
  
  /* Links Titles */
  a {
    color: var(--default-link-color);
  }
  
  a:hover {
    color: #41a2d8;
  }
  
  /* 'Citations', 'Highly Influential Citations', 'Citation Velocity', 'Citation Acceleration', data maybe incorrect pop-up */
  div.tooltip.mod-tooltip-bottom-left.tooltip--default-size, div.tooltip.mod-tooltip-bottom-left {
    color: var(--default-color);
    background-color: var(--default-bkgd-color);
  }
  
  div.histogram__chart > svg > g.buckets > g > rect.popover {
    background-color: var(--default-bkgd-color);
  }
  
  .popover {
    background-color: var(--default-bkgd-color);
  }
  
  /* extra authors */
  .more-authors-label {
    background-color: var(--darker-bkgd-color);
  }
  
  /* Google Scholar button */
  .icon-button, .button {
    background-color: var(--darker-bkgd-color);
    border-bottom: none;
  }
  
  /* Search */
  .search-bar input[type="search"] {
    color: #cccccc;
  }
  
  /* Search suggestions dropdown */
  li.flex-row-vcenter.suggestion {
    color: var(--default-color);
    background-color: var(--darker-bkgd-color);
  }
  
  /* Suggestions icon
  svg.type-icon {
    fill: red;
  } */
  
  /* 'Reading List' dropdown */
  .dropdown-menu-content {
    background-color: var(--default-bkgd-color);
  }
  
  .reading-list li {
    border-top-color: var(--border-color);
  }
  
/*   .reading-list-dropdown-menu .dropdown-menu-content {
    border-color: rgba(35,122,170,.3);
  }
  
  .dropdown-menu.dropdown-is-visible .header-button {
    background-color: rgba(35,122,170,.3);
    color: #5ea5d9;
  } */
  
  /* Search Results Embedded Tweet
  div.embedded-tweet > iframe#twitter-widget-0 > html.SandboxRoot {
    background-color: var(--default-bkgd-color);
  }
  
  .EmbeddedTweet {
    background-color: var(--default-bkgd-color);
  }
  
  div.EmbeddedTweet.js-clickToOpenTarget > blockquote {
    data-chrome: transparent;
  } */
  
  /* Left-side Pane Modifier checkboxes e.g. 'Publication Type', 'Author', 'Publication Venue', etc. */
  .checkbox:not(.is-disabled) .checkbox-input {
    border-color: #1a5c80; /* from #237aaa */
  }
  
  .checkbox.is-selected:not(.is-disabled) .checkbox-input {
    border-color: #237aaa;
  }
  
  .checkbox.is-disabled .checkbox-input {
    border-color: var(--border-color);
  }
  
/*   .checkbox.is-disabled .checkbox-input svg {
    fill: var(--border-color);
  }
  
  svg.check.flex-centered {
    fill: var(--border-color) !important;
  } */
  
  .checkbox.is-disabled .checkbox-label {
    color: var(--border-color);
  }
  
  /* Left-side Pane borders */
  .filters-wrapper section, .filters-wrapper .filter-header {
    border-bottom-color: var(--border-color);
  }
  
  .filter {
    border-right-color: var(--border-color);
  }
  
  /* Search result row */
  .search-result, .controls {
    border-bottom-color: var(--border-color);
  }
  
  .search-result-footer .paper-actions .paper-actions-toggle:hover, .search-result-footer .paper-actions .icon-button:hover {
    background-color: var(--brighter-bkgd-color);
    color: var(--brighter-color);
  }
  
  /* Page Numbers */
  .pagination li span {
    background-color: var(--default-bkgd-color);
  }
  
  .pagination-ellipsis {
    background-color: var(--default-bkgd-color);
  }
  
  /* Footer */
  .search {
    background-color: var(--default-bkgd-color);
  }
  
  .footer {
    background-color: var(--default-bkgd-color);
  }
  
  /**** Paper Page ****/
  
  /* current section on right side, changing on scrolling up/down */
  .sticky-nav__item {
    border-left-color: var(--brighter-bkgd-color);
  }
  
  .sticky-nav__item.is-active .sticky-nav__item__link {
    color: var(--default-link-color);
  }
  
  /* References, Citations Links */
  .citation .result-meta a .result-title {
    color: var(--default-link-color);
  }
  
  .citation .result-meta a .result-title:hover {
    color: #42a2d8;
  }
  
  .citation, .empty-citations {
    border-bottom-color: var(--border-color);
  }
  
  /* 'Extracted Key Phrases' */
  .key-phrases li {
    background-color: #444444;
    color: var(--heading-color);
  }
  
  /* 'Highly-influenced' pop-up pane */
  div.tooltip.mod-tooltip-bottom-right.citation-influence-tooltip {
    background-color: var(--default-bkgd-color);
  }
  
  /* 'Excerpts' pop-up pane */
  .citation .context {
    color: #cccccc;
    background-color: var(--default-bkgd-color);
  }
  
  div.tooltip.mod-tooltip-bottom-right.excerpts-tooltip {
    background-color: #262626;
  }
  
  /* Figures */
  .paper-detail-figures-list > li .paper-detail-figures-list-figure-image img {
    filter: invert(20%);
  }
  
  /* Footer */
  div.paper {
    background-color: var(--default-bkgd-color);
  }
  
  /**** Author Page ****/
  
  /* Statistics */
  .author-stat-label {
    color: var(--default-color);
  }
  
  .author-stat-number {
    color: var(--default-color);
  }
  
  /* Author links */
  a.influence-graph__node__link {
    color: var(--default-color);
  }
  
  a.influence-graph__node__link:hover{
    color: #66a0b6;
  }
  
  /* 'who most influenced' Pop-up pane */
  div.tooltip.mod-tooltip-right-top.influence-graph__node-details.tooltip--large.tooltip--blue-border {
    background-color: var(--default-bkgd-color);
  }
  
  /* 'most influenced by' Pop-up pane */
  div.tooltip.mod-tooltip-left-top.influence-graph__node-details.tooltip--large.tooltip--blue-border {
    background-color: var(--default-bkgd-color);
  }
  
  /* Pop-up pane - 'Influence Score' */
  div.influence-graph__node-details__meta__item > span > a {
    color: var(--default-color);
  }
  
  /* Pop-up pane - 'View Author Profile' */
  div.influence-graph__node-details__meta__item > a > span {
    color: var(--default-color);
  }
  
  div.tooltip.mod-tooltip-bottom-right {
    background-color: var(--default-bkgd-color);
  }
  
  /* Footer */
  div.author-page {
    background-color: var(--default-bkgd-color);
  }
  
  /**** Home Page ****/
  div.home-page {
    background-color: var(--default-bkgd-color);
  }
  
  /* Search field */
  .search-bar.v2-search-bar .input-container {
    background-color: #3C3B37; /* from its nbd */
  }
  
  .search-bar.v2-search-bar input[type="search"] {
    color: var(--gray-color);
  }
  
  /**** Semantic Scholar Insights ****/
  div.insights-page {
    background-color: var(--default-bkgd-color);
  }
  
  /* Table */
  .insights-table tr:nth-child(2n+1) td {
    background-color: #444444;
  }
  
  /** 'Why You'll Love Semantic Scholar' **/
  .why-s2 {
    background-color: var(--default-bkgd-color);
  }
  
  .why-s2 .tiles .tile .tile-image {
    filter: invert(80%);
  }
  
  h3 {
    color: inherit;
  }
  
  /* .insights-table tr:nth-child(even) {
    background-color: #f00;
  } Not needed */
  
  /**** FAQ ****/
  
  /* Footer */
  div.faq-page {
    background-color: var(--default-bkgd-color);
  }
  
  /**** Sign in, Submit Feedback ****/
  
  .modal {
    background-color: var(--default-bkgd-color);
  }
  
  .modal-container {
    background-color: rgba(68,68,68,.9)
  }
  
  .modal-header {
    color: var(--heading-color);
  }
  
  /**** Citeomatic ****/
  body {
    background-color: var(--default-bkgd-color);
  }
  
/*   .img-logo {
    background-image: url(../img/logos.60800cc0b2.png) no-repeat !important;
  } */
  
  .img-logo {
    filter: invert(80%);
  }
  
  /* Description */
  h3.intro, p.tagline {
    color: #5ea5d9;
  }
  
  /* Selected tab */
  .react-tabs [role="tab"][aria-selected="true"] {
    background-color: #2172a4;
    color: #fff;
  }
  
  /* 'Input URL' tab */
  .form-control {
    background-color: #3c3b37;
    color: var(--default-color);
  }
  
  /** Input URL -> Search Results **/
  
  /* Paper */
  div.your-paper {
    background-color: var(--default-bkgd-color);
    color: var(--gray-color);
  }
  
  div.downloads .btn-default {
    background-color: var(--brighter-bkgd-color);
    color: var(--gray-color);
    text-shadow: 0 1px 0 var(--gray-color);
    background-image: none;
    background-repeat: none;
  }
  
  /* Rows */
  .table > thead > tr > th {
    border-bottom-color: var(--border-color);
  }
  
  .table > tbody > tr > td {
    border-top-color: var(--border-color);
    color: var(--default-color);
  }
  
  .table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #3f3f3f;
  }
  
  /* PDF, Cite logos */
  img[src="/static/media/pdf.d6429f55.svg"], img[src="/static/media/cited.805a6abf.svg"] {
    filter: invert(60%);
  }
  
  /* Cite Pop-up */
  .ReactModal__Overlay {
    background-color: rgba(0,0,0,.7) !important;
  }
  
  .ReactModal__Content.modal-dialog pre {
    background-color: var(--default-bkgd-color);
    color: var(--gray-color);
  }
  
  /**** Blog ****/
  
  .metabar, .screenContent, .canvas-renderer {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  .u-backgroundWhite, .u-backgroundGrayLightest {
    background-color: var(--default-bkgd-color) !important;
  }
  
  .u-textColorDark {
    color: var(--default-color) !important;
  }
  
  /* Header */
  .graf--h2, .graf--h3, .graf--h4 {
    color: var(--default-color);
  }
  
  .tags--borderless .tag-button.is-active, .tags--borderless .tag-button:focus, .tags--borderless .tag-button:hover, .tags--borderless > .button.is-active, .tags--borderless > .button:focus, .tags--borderless > .button:hover, .tags--borderless > a:focus, .tags--borderless > a:hover {
    color: var(--default-color);
  }
  
  /*** Firefox PDF Viewer ***/
  .textLayer {
    background-color: black;
  }
  
}
