css:
@-moz-document domain("www.imzy.com") {
body, .header, .feed__new-post, .voting.is-default, .feed-item__main, .feed-post__content__body, .feed-item.is-post .feed-item__main__comment-create-wrapper, .post__main, .post__content__preview__body,
.comment__content, .post__content__body {
    background: #13182C;
    color: lightgrey;
}

.header, .typeahead__field {
    border-bottom: 1px solid #4C4D62;
}

.feed-item__main {
    box-shadow: 0 2px 4px 0 #4C4D62;
}

.typeahead__field, .feed-item__main__type__icon {
    color: #4C4D62;
}

.feed-item.is-post .feed-item__main__meta .feed-item__main__meta__link-community, .feed-post__content__heading__link, a.feed__new-post__item__link, .side-nav__list-item__link, .nav.is-tabs .nav__item__button, .nav.is-tabs .nav__item__link,
.post__main__heading__link, span.comment__meta__text a.href {
    color: #748EA5;
}

a.fi, .header__status__button-username__label, .side-nav__heading__link, .feed-item__main__meta__link-user, .side-nav__footer__link a.href, .post__main__meta .post__main__meta__link-user, .post__content__preview__heading,
.select-default-wrapper.is-simple .select-default, .btn-icon-text, .btn-text{
    color: #51B6A4;
}

.feed__new-post__item__link.is-plus {
    background: #748EA5;
}

.comment-create__cta-input {
    background: lightgrey;
}

li.is-community {
    display: none;
}

a:hover, .post-actions__item:hover {
    color: #91E4D0 !important;
}

.post__content__participation-label {
    background: #51B6A4;
    bottom: 0;
    box-shadow: -5px 0 5px 5px #51B6A4;
    color: #13182C;
}

.page-home-feed .page-home-feed__feed {
    max-width: 60%;
}

.feed-post__content__img {
    max-width: 100px !important;
    max-height: 100px !important;
    float:left;
}

.feed-post__content__ext-link {
    bottom: auto;
    display: inline-block;
    position: relative;
    left: auto;
    font-size: small;
    padding-left: 5px;
}

.feed-post__content__ext-link__text:first-child{
    display:none;
}

.feed-item.is-post .feed-item__main__comment-create-wrapper{
    clear: both;
}
}
